INSERT INTO `creature_text` VALUES (131720, 8, 0, 'Excellently done! We should be able to use this against those monstrous wooden creatures. Ah... but do watch it doesn\'t spill.', 12, 0, 100, 1, 0, 0, 144168, 0, 'Warren Ashton to Player');
INSERT INTO `creature_text` VALUES (131748, 0, 0, 'Okay! Lets go!', 12, 0, 100, 0, 0, 0, 144080, 0, 'Penny \"Precious\" Hardwick to Player');
