INSERT INTO `creature_text` VALUES (131748, 2, 0, 'Is this the right way?', 12, 0, 100, 0, 0, 0, 146028, 0, 'Penny \"Precious\" Hardwick to Player');
INSERT INTO `creature_text` VALUES (131748, 3, 0, 'Good thing I wore my boots!', 12, 0, 100, 0, 0, 0, 144356, 0, 'Penny \"Precious\" Hardwick to Player');
