INSERT INTO `creature_text` VALUES (132193, 6, 0, 'That\'s the one! Jus\' toss it atop the table there.', 12, 0, 100, 1, 0, 99382, 146384, 0, 'Angus Ballaster to Player');
INSERT INTO `creature_text` VALUES (132193, 7, 0, 'I\'ll need te modify this here hand cannon a mite first...', 12, 0, 100, 0, 0, 99384, 146386, 0, 'Angus Ballaster to Player');
INSERT INTO `creature_text` VALUES (132193, 8, 0, 'Ah, there we go!', 12, 0, 100, 1, 0, 99385, 146387, 0, 'Angus Ballaster to Player');
INSERT INTO `creature_text` VALUES (132193, 9, 0, 'All that\'s left is to give the girl a name. How bout we call her \"Witchrend?\"', 12, 0, 100, 1, 0, 99386, 146388, 0, 'Angus Ballaster to Player');
INSERT INTO `creature_text` VALUES (132292, 0, 0, '<sigh> I\'m never getting out of here.', 12, 0, 100, 18, 0, 0, 148771, 0, 'Raimond Mildenhall');
