INSERT INTO `creature_text` VALUES (132395, 4, 0, 'Interlopers! You will die!', 12, 0, 100, 0, 0, 104165, 145949, 0, 'Sister Noella Briarwood to Player');
INSERT INTO `creature_text` VALUES (132395, 5, 0, 'We will... have revenge...', 12, 0, 100, 0, 0, 104172, 145945, 0, 'Sister Noella Briarwood to Player');
