INSERT INTO `creature_text` VALUES (129311, 0, 0, 'This pleases me! Bring more to my lair... I must commune with the master!', 12, 0, 100, 0, 0, 98303, 140640, 0, 'Hir\'eek to Player');
INSERT INTO `creature_text` VALUES (129319, 0, 0, 'Hello? Is anyone dere?', 14, 0, 100, 22, 0, 0, 140197, 0, 'Bladeguard Tarkaj to Player');
INSERT INTO `creature_text` VALUES (129319, 1, 0, 'Please, I\'m injured... Have mercy on a fellow Zandalari!', 12, 0, 100, 1, 0, 0, 140200, 0, 'Bladeguard Tarkaj to Player');
INSERT INTO `creature_text` VALUES (129319, 2, 0, '$p...?', 12, 0, 100, 0, 0, 0, 140210, 0, 'Bladeguard Tarkaj to Player');
INSERT INTO `creature_text` VALUES (129330, 0, 0, 'What you doing out here, $p? I thought you were dead!', 12, 0, 100, 1, 0, 0, 140262, 0, 'Bladeguard Tarkaj to Player');
INSERT INTO `creature_text` VALUES (129330, 1, 0, 'My first drink in Vol\'dun. I\'ve been lost in da dunes since we were attacked!', 12, 0, 100, 1, 0, 0, 140264, 0, 'Bladeguard Tarkaj to Player');
INSERT INTO `creature_text` VALUES (129330, 2, 0, 'You helped dese exiles set up an inn way out here in da middle of nowhere?', 12, 0, 100, 1, 0, 0, 140291, 0, 'Bladeguard Tarkaj to Player');
INSERT INTO `creature_text` VALUES (129330, 3, 0, 'I\'d say you\'re crazy, but you just saved my life. So, thanks.', 12, 0, 100, 1, 0, 0, 140292, 0, 'Bladeguard Tarkaj to Player');
INSERT INTO `creature_text` VALUES (129330, 4, 0, 'Da Faithless chased me across da desert, through wild beasts and monsters. I barely survived.', 12, 0, 100, 1, 0, 0, 140290, 0, 'Bladeguard Tarkaj to Player');
INSERT INTO `creature_text` VALUES (129330, 5, 0, 'I can\'t believe I found you again, $p. But I\'m exhausted...', 12, 0, 100, 1, 0, 0, 140293, 0, 'Bladeguard Tarkaj to Player');
INSERT INTO `creature_text` VALUES (129330, 6, 0, 'Think I might stay here awhile. Help out where I can.', 12, 0, 100, 0, 0, 0, 140294, 0, 'Bladeguard Tarkaj to Player');
INSERT INTO `creature_text` VALUES (129330, 7, 0, 'Thanks for saving me...', 12, 0, 100, 0, 0, 0, 140295, 0, 'Bladeguard Tarkaj to Player');
INSERT INTO `creature_text` VALUES (129335, 0, 0, 'Dese things wanted to use my blood for dese bats!', 12, 0, 100, 5, 0, 0, 140288, 0, 'Zandalari Sacrifice to Player');
