INSERT INTO `creature_text` VALUES (129381, 1, 0, 'Anytin\' for ya, Ma\'da!', 12, 0, 100, 0, 0, 0, 140519, 0, 'Wardrummer Gix to Player');
INSERT INTO `creature_text` VALUES (129395, 0, 0, 'ENOUGH! I will feast upon your broken bones!', 14, 0, 100, 0, 0, 98300, 140614, 0, 'Hir\'eek to Player');
INSERT INTO `creature_text` VALUES (129395, 1, 0, 'You are strong... but none stand against the might of a loa!', 14, 0, 100, 0, 0, 98301, 140615, 0, 'Hir\'eek to Player');
INSERT INTO `creature_text` VALUES (129434, 0, 0, 'Get back in your golems, spirits! You belong to me!', 14, 0, 100, 0, 0, 0, 140718, 0, 'Zak\'rajan the Undying to Soulcatcher Totem');
