INSERT INTO `creature_text` VALUES (129451, 0, 0, 'I\'ll sneak ahead to scout the area. Clear out those specters and I\'ll grab the supplies.', 12, 0, 100, 1, 0, 0, 140854, 0, 'Omi to Player');
