INSERT INTO `creature_text` VALUES (129451, 3, 0, 'Oh, and can\'t forget the soul-filled golem eye! Tacha turns these into hilarious  little bombs.', 12, 0, 100, 1, 0, 0, 140903, 0, 'Omi to Player');
INSERT INTO `creature_text` VALUES (129451, 4, 0, 'And maybe a couple little trinkets for my trouble.', 12, 0, 100, 1, 0, 0, 140904, 0, 'Omi to Player');
INSERT INTO `creature_text` VALUES (129451, 5, 0, 'Nice fighting. Now let\'s see what all they\'ve got stockpiled in here.', 12, 0, 100, 1, 0, 0, 140898, 0, 'Omi to Player');
INSERT INTO `creature_text` VALUES (129451, 6, 0, 'Mostly standard Zandalari weapons here... Ooo, those look enchanted!', 12, 0, 100, 1, 0, 0, 140899, 0, 'Omi to Player');
INSERT INTO `creature_text` VALUES (129451, 7, 0, 'This is a good haul, $p. I\'ll take the best of these for the group, of course.', 12, 0, 100, 1, 0, 0, 140900, 0, 'Omi to Player');
INSERT INTO `creature_text` VALUES (129504, 0, 0, 'You bring dat failed king of ours? He will die, and you cannot stop it.', 12, 0, 100, 0, 0, 0, 140665, 0, 'Disciple of Zul to Player');
INSERT INTO `creature_text` VALUES (129504, 1, 0, 'Zandalar shall rule dis world!', 12, 0, 100, 0, 0, 0, 140662, 0, 'Disciple of Zul to Player');
INSERT INTO `creature_text` VALUES (129504, 2, 0, 'Dis world will be ours. Zul has foreseen it!', 12, 0, 100, 0, 0, 0, 140667, 0, 'Disciple of Zul to Wardruid Loti');
INSERT INTO `creature_text` VALUES (129506, 0, 0, 'Your soul will join the others in this golem!', 12, 0, 100, 0, 0, 0, 140815, 0, 'Specter of Mugabu to Player');
INSERT INTO `creature_text` VALUES (129506, 1, 0, 'Zak\'rajan wields our very souls as weapons. You are doomed.', 12, 0, 100, 0, 0, 0, 140818, 0, 'Specter of Mugabu to Player');
INSERT INTO `creature_text` VALUES (129507, 0, 0, 'Another body to break for Zak\'rajan!', 12, 0, 100, 0, 0, 0, 140816, 0, 'Specter of Jam\'jen to Player');
INSERT INTO `creature_text` VALUES (129507, 1, 0, 'Your weapons are worthless. Our spirits protect him.', 12, 0, 100, 0, 0, 0, 140817, 0, 'Specter of Jam\'jen to Player');
