INSERT INTO `creature_text` VALUES (129603, 2, 0, 'Spirits be powerful weapons... if you can control \'em.', 12, 0, 100, 1, 0, 0, 140790, 0, 'Released Spirit to Player');
INSERT INTO `creature_text` VALUES (129603, 3, 0, 'Why does Bwonsamdi refuse to let us into de Other Side?', 12, 0, 100, 1, 0, 0, 140786, 0, 'Released Spirit to Player');
INSERT INTO `creature_text` VALUES (129620, 0, 0, 'Aaahhh!!', 14, 0, 100, 0, 0, 102550, 140616, 0, 'Jo\'chunga to Hir\'eek');
INSERT INTO `creature_text` VALUES (129672, 0, 0, 'Let my spirit go free. Please...', 12, 0, 100, 0, 0, 107073, 153655, 0, 'Tortured Spirit to Player');
INSERT INTO `creature_text` VALUES (129672, 1, 0, 'We are coming for you, Zak\'rajan...', 12, 0, 100, 0, 0, 107070, 153654, 0, 'Tortured Spirit to Player');
