INSERT INTO `creature_text` VALUES (129672, 4, 0, 'You\'ll be next, Zak\'rajan...', 12, 0, 100, 0, 0, 107071, 153654, 0, 'Tortured Spirit to Player');
INSERT INTO `creature_text` VALUES (129708, 0, 0, 'Back in the day, I could have handled a swarm of these slithering worms. They came at me in the dark. I was overrun! Their orders seem to be, boiled down, \"Hurry! Azshara wants her prize!\"', 12, 0, 100, 0, 0, 0, 139353, 0, 'Rauloo the Hardened to Player');
INSERT INTO `creature_text` VALUES (129709, 0, 0, 'It was just little murlocs here, then there was a storm, then naga streaming up the beach. They took me first, and I am rather embarrassed.', 12, 0, 100, 0, 0, 0, 138799, 0, 'Crosh to Player');
INSERT INTO `creature_text` VALUES (129710, 0, 0, 'These naga are afraid. Something else is driving them. This is not merely loyalty to Queen Azshara.', 12, 0, 100, 0, 0, 0, 153372, 0, 'Akru to Player');
INSERT INTO `creature_text` VALUES (129712, 0, 0, 'The naga are looking for something here. It will be for Azshara. We must stop them, or obfuscate their goal.', 12, 0, 100, 0, 0, 0, 139350, 0, 'Kono to Player');
INSERT INTO `creature_text` VALUES (129717, 0, 0, 'Oh my! This is just awful. These are orders to kill those loyal to Gral!', 12, 0, 100, 1, 0, 0, 153657, 0, 'Akru to Player');
INSERT INTO `creature_text` VALUES (129717, 1, 0, 'Queen Azshara is planning something, and she is not alone.', 12, 0, 100, 0, 0, 0, 153658, 0, 'Akru to Player');
INSERT INTO `creature_text` VALUES (129717, 2, 0, 'Gral got too close.', 12, 0, 100, 0, 0, 0, 153714, 0, 'Akru to Player');
INSERT INTO `creature_text` VALUES (129717, 3, 0, 'The Zandalari do not know the horror before them. They must be warned.', 12, 0, 100, 1, 0, 0, 153659, 0, 'Akru to Player');
INSERT INTO `creature_text` VALUES (129717, 4, 0, 'Get to Atal\'gral. I truly hope the temple still stands.', 12, 0, 100, 0, 0, 0, 153713, 0, 'Akru to Player');
INSERT INTO `creature_text` VALUES (129730, 0, 0, 'You will not escape me, Ateena! No matter what dark voodoo you command!', 12, 0, 100, 0, 0, 101749, 140949, 0, 'Princess Talanji');
INSERT INTO `creature_text` VALUES (129730, 1, 0, 'She must be nearby. I will not give up, not when we are dis close to killing de leader of dese monsters.', 12, 0, 100, 0, 0, 101778, 140950, 0, 'Princess Talanji');
INSERT INTO `creature_text` VALUES (129752, 0, 0, 'I never be sniffin\' another brew long as I live!', 12, 0, 100, 0, 0, 0, 142708, 0, 'Mindslaved Gurubashi to Player');
INSERT INTO `creature_text` VALUES (129752, 1, 0, 'Curse da Jambani! Curse dem all!', 12, 0, 100, 0, 0, 0, 142709, 0, 'Mindslaved Gurubashi to Player');
