INSERT INTO `creature_text` VALUES (129907, 0, 0, 'I have words for my old student. You will find me when you are finished dealing with de fools dat answer to him.', 12, 0, 100, 25, 0, 106218, 141506, 0, 'Zul the Prophet to Player');
