INSERT INTO `creature_text` VALUES (129907, 2, 0, 'Let ol\' Rastakhan know I will return in my own time. Oh, and thanks for all your help.', 12, 0, 100, 0, 0, 106237, 143195, 0, 'Zul the Prophet to Player');
INSERT INTO `creature_text` VALUES (129961, 0, 0, 'Fear me powa!', 14, 0, 100, 0, 0, 0, 148157, 0, 'Atal\'zul Gotaka');
