INSERT INTO `creature_text` VALUES (130342, 3, 0, 'I\'ll put this sword to good use!', 12, 0, 100, 113, 0, 107307, 148551, 0, 'Vulpera Slave to Player');
INSERT INTO `creature_text` VALUES (130342, 4, 0, 'Let\'s burn this place to the ground!', 12, 0, 100, 113, 0, 107308, 148553, 0, 'Vulpera Slave to Player');
INSERT INTO `creature_text` VALUES (130342, 5, 0, 'They\'ll fear the vulpera!', 12, 0, 100, 113, 0, 107305, 148550, 0, 'Vulpera Slave to Player');
