INSERT INTO `creature_text` VALUES (130396, 0, 0, 'Hmm, you\'ll fetch a high price.', 12, 0, 100, 1, 0, 107771, 148725, 0, 'Appraiser Versik to Vulpera Slave');
INSERT INTO `creature_text` VALUES (130396, 1, 0, 'Freedom... never lasts...', 12, 0, 100, 1, 0, 107772, 148726, 0, 'Appraiser Versik to Player');
