INSERT INTO `creature_text` VALUES (115038, 0, 0, 'You\'ve got my attention, dragon. You\'ll find I\'m not as easily scared as the villagers below.', 14, 0, 100, 0, 0, 58641, 0, 0, 'IMAGE_MEDIVH_REPLY');
