INSERT INTO `creature_text` VALUES (98208, 6, 1, 'Think of this as a mercy killing.', 14, 0, 100, 0, 0, 58647, 0, 0, 'VANDROS - KILL');
INSERT INTO `creature_text` VALUES (98208, 6, 2, 'So brave, but so... shortsighted.', 14, 0, 100, 0, 0, 58648, 0, 0, 'VANDROS - KILL');
