INSERT INTO `creature_text` VALUES (43247, 0, 9, 'Get this damned line moving!', 12, 7, 100, 15, 0, 0, 43754, 0, 'Redridge Citizen');
INSERT INTO `creature_text` VALUES (43247, 0, 10, 'You\'re good for nothing, Solomon!', 12, 7, 100, 14, 0, 0, 43755, 0, 'Redridge Citizen');
INSERT INTO `creature_text` VALUES (43247, 0, 11, 'You can forget about re-election, magistrate!', 12, 7, 100, 274, 0, 0, 43756, 0, 'Redridge Citizen');
INSERT INTO `creature_text` VALUES (43247, 0, 12, 'No justice, no peace!', 12, 7, 100, 22, 0, 0, 43757, 0, 'Redridge Citizen');
INSERT INTO `creature_text` VALUES (43247, 0, 13, 'If you don\'t do something about these gnolls, we will!', 12, 7, 100, 25, 0, 0, 43758, 0, 'Redridge Citizen');
INSERT INTO `creature_text` VALUES (43247, 0, 14, 'Please help me! I\'m the last member of my family left. Everyone else has been kidnapped!', 12, 7, 100, 15, 0, 0, 43759, 0, 'Redridge Citizen');
INSERT INTO `creature_text` VALUES (43247, 1, 0, 'Forget you, Solomon. I\'ll find my family by myself.', 12, 7, 100, 14, 0, 0, 43760, 0, 'Redridge Citizen');
INSERT INTO `creature_text` VALUES (35836, 0, 2, 'There\'s one after me!', 12, 0, 100, 1, 0, 0, 36039, 0, 'Frightened Citizen to Player');
INSERT INTO `creature_text` VALUES (35836, 0, 1, 'Help me, please!', 12, 0, 100, 1, 0, 0, 36038, 0, 'Frightened Citizen to Player');
INSERT INTO `creature_text` VALUES (35836, 0, 0, 'It\'s coming right for me!!', 12, 0, 100, 1, 0, 0, 36037, 0, 'Frightened Citizen to Player');
INSERT INTO `creature_text` VALUES (34913, 0, 3, 'Push them back!', 14, 0, 100, 0, 0, 19620, 35169, 0, 'Prince Liam Greymane');
INSERT INTO `creature_text` VALUES (34913, 0, 2, 'Protect the civilians!', 14, 0, 100, 0, 0, 19619, 35168, 0, 'Prince Liam Greymane');
INSERT INTO `creature_text` VALUES (34913, 0, 1, 'Defeat these foul beasts!', 14, 0, 100, 0, 0, 19618, 35167, 0, 'Prince Liam Greymane');
INSERT INTO `creature_text` VALUES (34913, 0, 0, 'Stand your ground, men!', 14, 0, 100, 0, 0, 19617, 35166, 0, 'Prince Liam Greymane');
INSERT INTO `creature_text` VALUES (35112, 0, 0, 'Enough! I\'ve made up my mind. We need Crowley on our side.', 12, 0, 100, 0, 0, 0, 38602, 0, 'King Genn Greymane to Player');
INSERT INTO `creature_text` VALUES (35115, 0, 0, 'We ought to leave Crowley in prison with those beasts!', 12, 0, 100, 0, 0, 0, 38601, 0, 'Lord Godfrey to Player');
INSERT INTO `creature_text` VALUES (35906, 0, 0, 'Weve got Aranas!  Fire at will!', 14, 0, 100, 0, 0, 19634, 36099, 0, 'Lord Godfrey to Player');
INSERT INTO `creature_text` VALUES (35230, 1, 0, 'You\'ll never catch us, you blasted mongrels!', 14, 0, 100, 0, 0, 19696, 35497, 0, 'Lord Darius Crowley to Player');
INSERT INTO `creature_text` VALUES (49231, 0, 0, 'I see. Well then, let\'s get to work, $n! The Dark Lady needs us, right?', 12, 0, 100, 1, 0, 0, 49351, 0, 'Valdred');
INSERT INTO `creature_text` VALUES (49231, 1, 0, 'Valdred Moray, reporting for duty, sir!', 14, 0, 100, 1, 0, 0, 49408, 0, 'Valdred');
INSERT INTO `creature_text` VALUES (49230, 0, 0, 'Who are you calling a monster? You\'re the monster! I\'m just a man who died.', 12, 0, 100, 1, 0, 0, 49345, 0, 'Marshal');
INSERT INTO `creature_text` VALUES (38895, 0, 0, 'No. You\'re lying! My father will protect me!', 12, 0, 100, 0, 0, 0, 49337, 0, 'Lilian');
INSERT INTO `creature_text` VALUES (36440, 2, 0, 'The land gave way from under our feet...  I thought I was dead.  I owe you one.', 12, 0, 100, 0, 0, 0, 0, 0, 'Drowning Watchman to Player');
INSERT INTO `creature_text` VALUES (36606, 0, 0, 'We will overcome this.  Do not give into panic.', 12, 0, 100, 1, 0, 0, 0, 0, 'Queen Mia Greymane');
INSERT INTO `creature_text` VALUES (37694, 1, 0, 'The Forsaken will pay for what they have done!', 12, 0, 100, 0, 0, 0, 0, 0, 'Habitante esclavizado');
INSERT INTO `creature_text` VALUES (37694, 2, 0, 'Thank you!', 12, 0, 100, 0, 0, 0, 0, 0, 'Habitante esclavizado');
INSERT INTO `creature_text` VALUES (34851, 1, 0, 'Help...!', 12, 0, 100, 0, 0, 0, 39313, 0, 'Panicked Citizen to Player');
INSERT INTO `creature_text` VALUES (34851, 2, 0, 'I die of fear...', 12, 0, 100, 0, 0, 0, 39313, 0, 'Panicked Citizen to Player');
INSERT INTO `creature_text` VALUES (34864, 1, 3, 'In the woods is something strange going on, and to go beyond the city is unsafe.', 12, 0, 100, 0, 0, 0, 44374, 0, '');
INSERT INTO `creature_text` VALUES (34864, 2, 2, 'It isn\'t safe to leave, there\'s something lurking out there in the woods.', 12, 0, 100, 1, 0, 0, 44374, 0, 'Gilneas City Guard');
INSERT INTO `creature_text` VALUES (34913, 0, 4, 'Take heart, men!  We must protect our city!', 14, 0, 100, 0, 0, 19621, 35170, 0, 'Prince Liam Greymane');
INSERT INTO `creature_text` VALUES (35550, 0, 2, 'Keep them back!', 14, 0, 100, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (36283, 0, 0, 'You must return to the Duskmist Shore!', 42, 0, 100, 0, 0, 0, 50651, 0, 'Forsaken Catapult to Player');
INSERT INTO `creature_text` VALUES (19667, 0, 1, 'Heraazi exports are on the rise, sir.  We\'ve already bribed the appropriate taxation officers and contraband will begin to arrive within days.', 12, 0, 100, 0, 0, 0, 17186, 0, 'Consortium Nether Runner');
INSERT INTO `creature_text` VALUES (19667, 1, 0, 'By your leave, sir.', 12, 0, 100, 0, 0, 0, 17185, 0, 'Consortium Nether Runner');
INSERT INTO `creature_text` VALUES (18265, 0, 0, 'Nothing surprising there.  Come back with some real news, will you?', 12, 0, 100, 0, 0, 0, 17191, 0, 'Gezhe');
INSERT INTO `creature_text` VALUES (18265, 0, 1, 'As expected.  Very well, dismissed.', 12, 0, 100, 0, 0, 0, 17190, 0, 'Gezhe');
INSERT INTO `creature_text` VALUES (19776, 0, 0, 'All my bags are packed, I\'m ready to go.', 12, 0, 100, 0, 0, 0, 17413, 0, 'Experimental Pilot');
INSERT INTO `creature_text` VALUES (19776, 1, 0, 'You need to do what? All this science, I don\'t understand... Look, this is just my job, five days a week.', 12, 0, 100, 0, 0, 0, 17416, 0, 'Experimental Pilot');
INSERT INTO `creature_text` VALUES (19776, 2, 0, 'I can\'t. I\'m not the man they think I am at home.  Besides, I didn\'t bring them out here.', 12, 0, 100, 0, 0, 0, 17419, 0, 'Experimental Pilot');
INSERT INTO `creature_text` VALUES (19776, 3, 0, 'This isn\'t the kind of place to raise your kids. It\'s cold, and there\'d be no one to raise them.', 12, 0, 100, 0, 0, 0, 17421, 0, 'Experimental Pilot');
INSERT INTO `creature_text` VALUES (19776, 4, 0, 'Oh no, no, no... I\'m a rocket man.', 12, 0, 100, 0, 0, 0, 17423, 0, 'Experimental Pilot');
INSERT INTO `creature_text` VALUES (19737, 0, 0, 'The ship\'s not ready yet.  We still need to calibrate the fuse length to make sure that it doesn\'t burn out and leave you up there alone.', 12, 0, 100, 0, 0, 0, 17414, 0, 'Engineering Crewmember');
INSERT INTO `creature_text` VALUES (19737, 1, 0, 'Essentially, it\'s going to be a long, long time till we are ready to launch.  Maybe you should just head back home to your family.', 12, 0, 100, 0, 0, 0, 17417, 0, 'Engineering Crewmember');
INSERT INTO `creature_text` VALUES (19737, 2, 0, 'Why not? A family can give you strength.', 12, 0, 100, 0, 0, 0, 17420, 0, 'Engineering Crewmember');
INSERT INTO `creature_text` VALUES (19737, 3, 0, 'Couldn\'t you raise them?', 12, 0, 100, 0, 0, 0, 17422, 0, 'Engineering Crewmember');
INSERT INTO `creature_text` VALUES (19737, 4, 0, 'Well, it\'s like I said; it\'s going to be a while before we get this ship ready for flight.  We\'ll keep you informed.', 12, 0, 100, 0, 0, 0, 17424, 0, 'Engineering Crewmember');
INSERT INTO `creature_text` VALUES (25986, 0, 0, 'The Ore Cart squeaks against the rails as it is released.', 16, 0, 100, 0, 0, 0, 25416, 0, 'Ore Cart');
INSERT INTO `creature_text` VALUES (36608, 6, 0, 'There are more survivors to tend to. I\'ll see you on the shore.', 12, 0, 100, 397, 0, 0, 36549, 0, 'Doc Zapnozzle to Player');
INSERT INTO `creature_text` VALUES (16231, 0, 0, '%s nods her head yes at what her advisor has to say.', 16, 0, 100, 273, 0, 0, 13362, 0, 'Dame Auriferous');
INSERT INTO `creature_text` VALUES (16231, 0, 1, '%s disagrees with whatever it is that Valwyn has whispered to her.', 16, 0, 100, 274, 0, 0, 13363, 0, 'Dame Auriferous');
INSERT INTO `creature_text` VALUES (16231, 0, 2, '%s seems nonplussed by whatever it is that her advisor has whispered in her ear.', 16, 0, 100, 6, 0, 0, 13365, 0, 'Dame Auriferous');
INSERT INTO `creature_text` VALUES (16231, 1, 0, 'Executor, your direct assault approach will cost us more lives than I am comfortable with. A more nuanced strategy is called for; one that involves us reclaiming the surrounding territory, starting with the villages so as to cut off all points of reinforcement to Deatholme.', 12, 1, 100, 0, 0, 0, 12552, 0, 'Dame Auriferous');
INSERT INTO `creature_text` VALUES (16231, 2, 0, 'I hear what you are saying, Mavren. Nevertheless, the final decision is mine. I appreciate your assistance, but the majority of the lives on the line are blood elf. I will not have those lives carelessly thrown away!', 12, 1, 100, 0, 0, 0, 12571, 0, 'Dame Auriferous');
INSERT INTO `creature_text` VALUES (16231, 3, 0, 'How long until Forsaken reinforcements arrive? Our position here is tenuous. Your Lady promised us more soldiers. We must be able to concentrate solely on Deatholme and the Scourge!', 12, 1, 100, 0, 0, 0, 12557, 0, 'Dame Auriferous');
INSERT INTO `creature_text` VALUES (16231, 4, 0, 'Mavren, I don\'t want excuses, I want results! The Farstriders aren\'t available and we\'ve received all that we\'re going to get from Silvermoon for now. I cannot ignore the Shadowpine trolls and Zul\'Aman... they\'re arrayed across our eastern border!', 12, 1, 100, 0, 0, 0, 12572, 0, 'Dame Auriferous');
INSERT INTO `creature_text` VALUES (16231, 5, 0, 'No, I was right to begin with. Deatholme must come last. We must secure all of the Ghostlands first. I will not commit the forces here to a battle against Dar\'khan with enemies to our flank and rear!', 12, 1, 100, 0, 0, 0, 12562, 0, 'Dame Auriferous');
INSERT INTO `creature_text` VALUES (16231, 6, 0, 'Disagree with me all you like, High Executor. I will weigh your counsel, and then we will take the steps to free all of Quel\'Thalas as I deem necessary. We shall continue this discussion anon.', 12, 1, 100, 0, 0, 0, 12573, 0, 'Dame Auriferous');
INSERT INTO `creature_text` VALUES (16252, 0, 0, 'With all due respect, milady, you have not fought the Scourge as I have. The Lady appointed me to assist you in defeating Dar\'khan because of that, and you would do well to consider what I have to say.', 12, 1, 100, 0, 0, 0, 12553, 0, 'High Executor Mavren');
INSERT INTO `creature_text` VALUES (16252, 0, 1, 'I strongly disagree. Dar\'khan is not going to get reinforcements because he\'ll be bottlenecked. Deatholme has only one way in and out. Your traitor was a fool to corner himself there, and I intend to exploit that weakness!', 12, 1, 100, 0, 0, 0, 12554, 0, 'High Executor Mavren');
INSERT INTO `creature_text` VALUES (16252, 0, 2, 'This is war, Dame Auriferous, and in any war lives will be lost! The only areas that we need to secure are the two ziggurats. We will turtle in and take the Tower of the Damned and its master by brute force. Then you will have your Quel\'Thalas.', 12, 1, 100, 0, 0, 0, 12555, 0, 'High Executor Mavren');
INSERT INTO `creature_text` VALUES (16252, 0, 3, 'And if your Farstriders were here to reinforce us, I might agree. But, they are busying themselves instead with the Shadowpine trolls on your eastern border. The longer we wait for them, the stronger the Scourge will become.', 12, 1, 100, 0, 0, 0, 12556, 0, 'High Executor Mavren');
INSERT INTO `creature_text` VALUES (16252, 1, 0, 'I believe that you\'re making my argument for me, but yes it would be nice if we had more reinforcements. I would counter that it would be faster to pull the Farstriders from their senseless skirmishing with the trolls.', 12, 1, 100, 0, 0, 0, 12558, 0, 'High Executor Mavren');
INSERT INTO `creature_text` VALUES (16252, 1, 1, 'Dame Auriferous, we are the reinforcements. It may come to pass that The Lady will send more men in time. Better that you convince Silvermoon City to muster all of its forces and put them at our disposal here.', 12, 1, 100, 0, 0, 0, 12559, 0, 'High Executor Mavren');
INSERT INTO `creature_text` VALUES (16252, 1, 2, 'You are correct; we must concentrate on the Scourge. Ignore the trolls and the villages, and focus our attentions on Deatholme! With Dar\'khan defeated you will find that the rest of the Scourge \'body\' will fall quickly, lacking their \'head\'.', 12, 1, 100, 0, 0, 0, 12560, 0, 'High Executor Mavren');
INSERT INTO `creature_text` VALUES (16252, 1, 3, 'Forsaken reinforcements? Undercity is stretched thin with the Scourge on all sides. No, this will be a matter largely dealt with by the blood elves. We Forsaken are here as backup and advisors.', 12, 1, 100, 0, 0, 0, 12561, 0, 'High Executor Mavren');
INSERT INTO `creature_text` VALUES (16252, 2, 0, 'Again I strongly disagree with your view of the strategic situation. Your plan will spread our forces too thin. It will leave us with only a small force to assault Deatholme. I cannot in good conscience execute such a strategy.', 12, 1, 100, 0, 0, 0, 12567, 0, 'High Executor Mavren');
INSERT INTO `creature_text` VALUES (16252, 2, 1, 'Spread throughout the Ghostlands, our forces will be of little use in an assault on Deatholme. No, milady, I am here to kill Dar\'khan and that is what I intend to do!', 12, 1, 100, 0, 0, 0, 12568, 0, 'High Executor Mavren');
INSERT INTO `creature_text` VALUES (16252, 2, 2, 'We\'ve been over this before. Only a focused assault upon Deatholme will meet with success. These other targets are distractions that we can ill afford. I suggest that you inform your Captain Helios that he is to leave off his campaign against the Shadowpine trolls and focus solely on the Scourge.', 12, 1, 100, 0, 0, 0, 12569, 0, 'High Executor Mavren');
INSERT INTO `creature_text` VALUES (16252, 2, 3, 'Perhaps we should let the matter rest for the time being until your chef has prepared your meal? You look a bit piqued if you don\'t mind my saying. We can resume our discussion when you are feeling more yourself.', 12, 1, 100, 0, 0, 0, 12570, 0, 'High Executor Mavren');
INSERT INTO `creature_text` VALUES (16289, 0, 0, '%s whispers something in the Dame\'s ear.', 16, 0, 100, 0, 0, 0, 13361, 0, 'Advisor Valwyn');
INSERT INTO `creature_text` VALUES (16289, 1, 0, '%s looks appalled at the Executor\'s tone!', 16, 0, 100, 0, 0, 0, 12574, 0, 'Advisor Valwyn');
INSERT INTO `creature_text` VALUES (16289, 1, 1, '%s tries to busy herself with other matters.', 16, 0, 100, 0, 0, 0, 12575, 0, 'Advisor Valwyn');
INSERT INTO `creature_text` VALUES (16289, 1, 2, '%s glares daggers in the direction of High Executor Mavren and Deathstalker Maltendis.', 16, 0, 100, 0, 0, 0, 12576, 0, 'Advisor Valwyn');
INSERT INTO `creature_text` VALUES (16289, 1, 3, '%s lifts her nose and sniffs in response to the High Executor\'s reply.', 16, 0, 100, 0, 0, 0, 12577, 0, 'Advisor Valwyn');
INSERT INTO `creature_text` VALUES (16289, 1, 4, '%s shakes her head in disbelief at what she is hearing out of the Forsaken\'s mouth.', 16, 0, 100, 0, 0, 0, 12578, 0, 'Advisor Valwyn');
INSERT INTO `creature_text` VALUES (16289, 1, 5, '%s blanches as much of the blood drains from her face.', 16, 0, 100, 0, 0, 0, 12579, 0, 'Advisor Valwyn');
INSERT INTO `creature_text` VALUES (16289, 1, 6, '%s reddens furiously at what she is hearing in response to her lady\'s question.', 16, 0, 100, 0, 0, 0, 12580, 0, 'Advisor Valwyn');
INSERT INTO `creature_text` VALUES (16289, 1, 7, '%s concentrates on the wall opposite her, clearly ignoring the looks of the Executor and his assistant.', 16, 0, 100, 0, 0, 0, 12581, 0, 'Advisor Valwyn');
INSERT INTO `creature_text` VALUES (16289, 2, 0, '%s looks disgusted at the deathstalker\'s flirtations.', 16, 0, 100, 274, 0, 0, 13367, 0, 'Advisor Valwyn');
INSERT INTO `creature_text` VALUES (16251, 0, 0, '%s nods in agreement.', 16, 0, 100, 0, 0, 0, 12582, 0, 'Deathstalker Maltendis');
INSERT INTO `creature_text` VALUES (16251, 0, 1, '%s opens his mouth as if to add something, and then apparently thinks the better of it.', 16, 0, 100, 0, 0, 0, 12583, 0, 'Deathstalker Maltendis');
INSERT INTO `creature_text` VALUES (16251, 0, 2, '%s looks over at Advisor Valwyn with a smirk on his face.', 16, 0, 100, 0, 0, 0, 12584, 0, 'Deathstalker Maltendis');
INSERT INTO `creature_text` VALUES (16251, 0, 3, '%s grins at the High Executor\'s words.', 16, 0, 100, 0, 0, 0, 12585, 0, 'Deathstalker Maltendis');
INSERT INTO `creature_text` VALUES (16251, 0, 4, '%s appears bored with the whole discussion.', 16, 0, 100, 0, 0, 0, 12586, 0, 'Deathstalker Maltendis');
INSERT INTO `creature_text` VALUES (16251, 0, 5, '%s winks slyly at Advisor Valwyn.', 16, 0, 100, 0, 0, 0, 12587, 0, 'Deathstalker Maltendis');
INSERT INTO `creature_text` VALUES (16251, 0, 6, '%s fidgets with his armor, clearly uncomfortable at the tone of the discussion.', 16, 0, 100, 0, 0, 0, 12588, 0, 'Deathstalker Maltendis');
INSERT INTO `creature_text` VALUES (16251, 0, 7, '%s looks like he\'d rather be anywhere else but here.', 16, 0, 100, 0, 0, 0, 12589, 0, 'Deathstalker Maltendis');
INSERT INTO `creature_text` VALUES (16251, 1, 0, '%s turns to Advisor Valwyn and winks lasciviously in her direction.', 16, 0, 100, 2, 0, 0, 13366, 0, 'Deathstalker Maltendis');
INSERT INTO `creature_text` VALUES (1476, 0, 0, 'Stormy seas out there.  The sea spirits must be restless....hic!', 12, 6, 100, 0, 0, 0, 318, 0, 'Hargin Mundar');
INSERT INTO `creature_text` VALUES (1476, 0, 1, 'I saw a Sea Giant once.  It\'s not something I would care to see again.  Burp!', 12, 6, 100, 0, 0, 0, 319, 0, 'Hargin Mundar');
INSERT INTO `creature_text` VALUES (1476, 0, 2, 'I\'d go out and clear out some of those gnolls, but this leg has seen better days and I am afraid I\'d just wind up dead.', 12, 6, 100, 0, 0, 0, 320, 0, 'Hargin Mundar');
INSERT INTO `creature_text` VALUES (1476, 0, 3, 'Watch your step out there.  Many an adventure seeker has not returned from the marsh.  hic!', 12, 6, 100, 0, 0, 0, 321, 0, 'Hargin Mundar');
INSERT INTO `creature_text` VALUES (1476, 1, 0, 'Huuup...Huuup...Bleeeeehh!', 12, 0, 100, 0, 0, 0, 397, 0, 'Hargin Mundar');
INSERT INTO `creature_text` VALUES (48572, 0, 1, 'The sun has set on this mortal world, fools. Make peace with your end, for the hour of twilight falls!', 14, 0, 100, 0, 0, 23228, 0, 0, 'Deathwing talk');
INSERT INTO `creature_text` VALUES (34258, 0, 0, '¡Adelante! ¡En Marcha!', 12, 0, 100, 0, 0, 0, 0, 0, '');
