INSERT INTO `creature_text` VALUES (91797, 3, 1, 'The earth trembles before the rising tide!', 14, 0, 100, 0, 0, 54315, 0, 0, 'KING_DEEPBEARD - QUAKE_2');
INSERT INTO `creature_text` VALUES (91797, 4, 0, 'The ocean will sweep you away!', 14, 0, 100, 0, 0, 54311, 0, 0, 'KING_DEEPBEARD - KILL_2');
INSERT INTO `creature_text` VALUES (91797, 4, 1, 'Justice for the son you murdered!', 14, 0, 100, 0, 0, 54326, 0, 0, 'KING_DEEPBEARD - KILL_3');
INSERT INTO `creature_text` VALUES (91797, 4, 2, 'Blood repays blood!', 14, 0, 100, 0, 0, 54329, 0, 0, 'KING_DEEPBEARD - KILL_4');
INSERT INTO `creature_text` VALUES (91797, 4, 3, 'This was a inevitable as the tides, fools.', 14, 0, 100, 0, 0, 54332, 0, 0, 'KING_DEEPBEARD - KILL_4');
INSERT INTO `creature_text` VALUES (91797, 5, 0, 'You... haven\'t won... She... is almost here...', 14, 0, 100, 0, 0, 54318, 0, 0, 'KING_DEEPBEARD - DEATH');
INSERT INTO `creature_text` VALUES (91797, 5, 1, 'I... I\'ve failed... But my queen will not...', 14, 0, 100, 0, 0, 54319, 0, 0, 'KING_DEEPBEARD - DEATH_2');
INSERT INTO `creature_text` VALUES (91797, 5, 2, 'The oceans rift... but the wind... still rises against you...', 14, 0, 100, 0, 0, 54320, 0, 0, 'KING_DEEPBEARD - DEATH_3');
INSERT INTO `creature_text` VALUES (91797, 5, 3, 'The winds... will finish you... intruders...', 14, 0, 100, 0, 0, 54322, 0, 0, 'KING_DEEPBEARD - DEATH_4');
INSERT INTO `creature_text` VALUES (91789, 6, 1, 'Pitiful!', 14, 0, 100, 0, 0, 54198, 0, 0, 'LADY_HATECOIL - KILL_3');
INSERT INTO `creature_text` VALUES (91789, 6, 2, 'The Hatecoil proves superior!', 14, 0, 100, 0, 0, 54199, 0, 0, 'LADY_HATECOIL - KILL_4');
INSERT INTO `creature_text` VALUES (91789, 7, 0, 'Can you feel the winds? They come for you', 14, 0, 100, 0, 0, 54195, 0, 0, 'LADY_HATECOIL - KILL_4');
INSERT INTO `creature_text` VALUES (91789, 8, 0, 'You are too late! The Wrath of Azshara has arrived!', 14, 0, 100, 0, 0, 54196, 0, 0, 'LADY_HATECOIL - DEATH');
INSERT INTO `creature_text` VALUES (91789, 9, 0, 'It seems we have pets', 14, 0, 100, 0, 0, 0, 0, 0, 'LADY_HATECOIL - INTRO');
