INSERT INTO `creature_text` VALUES (74518, 0, 0, 'Fall back to the sanctum, Lord Bathiel\'s ritual must not be disturbed! Thruk, do not let them past!', 14, 0, 100, 0, 0, 43553, 78376, 1, 'Executioner Gore - On Kill Player');
INSERT INTO `creature_text` VALUES (74518, 1, 0, 'I was hoping you would make it to me. Now I get the pleasure of removing your heads!', 14, 0, 100, 0, 0, 43548, 78433, 1, 'Executioner Gore - On Kill Player');
INSERT INTO `creature_text` VALUES (74518, 2, 0, 'I delayed you long enough... Aku\'mai will rise...', 14, 0, 100, 0, 0, 43549, 78434, 1, 'Executioner Gore - On Kill Player');
INSERT INTO `creature_text` VALUES (33178, 3, 0, 'I\'ve shattered the shield protecting their leader! Quickly, kill him!', 14, 0, 100, 0, 0, 0, 34219, 0, '');
