INSERT INTO `creature_text` VALUES (124953, 3, 0, 'Insipid $c! This village is mine. The Mother has willed it!', 12, 0, 100, 0, 0, 97669, 133715, 0, 'Helena Gentle to Player');
INSERT INTO `creature_text` VALUES (124953, 4, 0, 'You should turn away now, lest you share their fate.', 12, 0, 100, 0, 0, 97670, 133716, 0, 'Helena Gentle to Player');
INSERT INTO `creature_text` VALUES (124953, 5, 0, 'Foolish $c! You dare challenge me here, in my domain!?', 14, 0, 100, 0, 0, 97671, 138020, 0, 'Helena Gentle to Player');
INSERT INTO `creature_text` VALUES (136963, 1, 0, 'Guíanoh al campamento vulpera, Nosotroh llevaremoh el tesoro.', 12, 0, 100, 1, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (136779, 0, 0, 'Haced sonar ese gong pa retar al rey pirata Zem´lan. Yo ehtaré preparado pa enfrentarme a él.', 12, 0, 100, 1, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (122723, 3, 0, '!Ja! Volni, !cómo ereh! Tal vez se haya vehtido a ohcurah... !Pero tieneh razón!', 12, 0, 100, 1, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (122723, 4, 0, 'Sí, ya he oído lo que ha dicho, Man´zul. Pero sigo pensando que tu parche eh máh feo que el trasero de un saurolihco', 12, 0, 100, 1, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (122723, 5, 0, 'Grenja, eh nuestrah bardo residente. Te tocará cualquier canción que le pidah... siempre que tenga percusión. Le encanta golpear cosah. Tu cara no, ehpero. !Ja, ja! Que no, que no... bueno, igual sí.', 12, 0, 100, 1, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (122723, 6, 0, 'El Paso del Hombre Muerto no tiene pérdida. Solo tieneh que dirigirte a lah rocah ohcurah y amenazanteh del noroehte. !Te vah a divertir!', 12, 0, 100, 1, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (122723, 7, 0, '!A por elloh! Zulsan y yo te ehperaremoh en la posada.', 12, 0, 100, 1, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (122723, 8, 0, 'It be safe to come out now?', 12, 0, 100, 1, 0, 0, 132696, 0, 'Rhan\'ka to Player');
INSERT INTO `creature_text` VALUES (122723, 9, 0, 'Grenja! Volni! Man\'zul! You be back! De gang\'s all here!', 12, 0, 100, 0, 0, 0, 147882, 0, 'Rhan\'ka to Player');
INSERT INTO `creature_text` VALUES (122723, 10, 0, 'You\'re right! It be time we brought $p into de gang officially. Prepare de ritual!', 12, 0, 100, 432, 0, 0, 134186, 0, 'Rhan\'ka to Player');
INSERT INTO `creature_text` VALUES (122723, 11, 0, 'He hervido la sangre y loh aguijoneroh y loh he mezclado en la copa ceremonial del Colmillo Dorado... Eh una piña. No te la comah. Solo tenemoh una.', 12, 0, 100, 1, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (122723, 12, 0, 'Ah la sangre eh dulce... bébela y veráh lo mihmo que yo.', 12, 0, 100, 1, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (122723, 13, 0, '!Te presento a tuh nuevo hermanoh y hermanah de la banda Colmillo Dorado!', 12, 0, 100, 1, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (122723, 14, 0, 'Zuldazar noh traicionnó. Exiliadoh como forahteroh. Pero nunca noh traicionamoh entre nosotroh. Loh Colmillo Dorado conservamoh nuehtra lealtad.', 12, 0, 100, 1, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (122723, 15, 0, 'Je, la primera vez siempre cuehta beber la sangre de ehcórpido del ritual. Échala. Ya te entrará mejor la próxima vez.', 12, 0, 100, 1, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (129354, 3, 0, 'Aqui siempre habrá un lugar pa ti. Seráh de la banda Colmillo Dorado de por vida.', 12, 0, 100, 1, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (129354, 2, 0, 'La Posada Colmillo Dorado, queda oficialmente inagurada!', 12, 0, 100, 1, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (129354, 1, 0, 'Preparaoh, !llegan huéhpedeh! !Qué emoción! Nada de matar, ¿eh?', 12, 0, 100, 1, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (129354, 0, 0, 'Te doy la bienvenida a la banda. Ahora tenemoh un objetivo común.', 12, 0, 100, 1, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (129365, 0, 0, 'Cuidado por si hay saurolihcoh al acecho arriba, en el rihco... Leh gusta abalanzarse sobre suh presah.', 12, 0, 100, 1, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (122725, 2, 0, 'Mientrah tú va a por fruta, !yo haré brochateah de ehcórpido pa desayunar!', 12, 0, 100, 1, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (123730, 1, 0, 'Bueno, no eh oro, pero lo puedo tallar en forma de colmillo.', 12, 0, 100, 1, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (123730, 2, 0, 'Vamoh, sé donde montar ehte... colmillo para que todo el mundo lo vea.', 12, 0, 100, 1, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (123730, 3, 0, 'Habrá que inventarse una buena hihtoria sobre el gigantehco trol terrible al que se lo arrancamoh.', 12, 0, 100, 1, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (123730, 4, 0, 'Now everyone\'ll know this is the Goldtusk Inn!', 12, 0, 100, 1, 0, 0, 147920, 0, 'Man\'zul to Player');
INSERT INTO `creature_text` VALUES (138749, 0, 0, '!A por elloh! Zulsan y yo te ehperaremoh en la posada.', 12, 0, 100, 1, 0, 0, 0, 0, '');
