INSERT INTO `creature_text` VALUES (90296, 0, 0, 'Даже смерть не может меня остановить!', 14, 0, 100, 0, 0, 50831, 95117, 0, '');
INSERT INTO `creature_text` VALUES (90296, 1, 0, 'Склонитесь предо мной!', 14, 0, 100, 0, 0, 50847, 95127, 0, '');
INSERT INTO `creature_text` VALUES (90316, 5, 0, '|cFFFF0000%s|r about to complete creation |cFFFF0000|Hspell:181912|h[\"Point explosion\"]|h|r!', 41, 0, 100, 0, 0, 0, 95546, 0, '');
INSERT INTO `creature_text` VALUES (90316, 8, 0, '|cFFFF0000%s|r begins to cast a spell |cFFFF0000|Hspell:182178|h[\"Шакрам Скверны\"]|h|r, нацеленное на вас!', 42, 0, 100, 0, 0, 0, 93991, 0, '');
INSERT INTO `creature_text` VALUES (90316, 3, 0, 'Ветра покарают вас.', 14, 0, 100, 15, 0, 50447, 96179, 0, '');
INSERT INTO `creature_text` VALUES (90316, 0, 0, 'Вы опоздали! Осквернение почти завершено!', 14, 0, 100, 15, 0, 50438, 96668, 0, '');
INSERT INTO `creature_text` VALUES (90316, 7, 0, 'Думаете, вы победили? Как бы не так!', 14, 0, 100, 15, 0, 50437, 96174, 0, '');
INSERT INTO `creature_text` VALUES (90316, 4, 0, 'За завесой тебя ждет лишь смерть!', 14, 0, 100, 15, 0, 50439, 96153, 0, '');
INSERT INTO `creature_text` VALUES (90316, 2, 0, 'Завеса накроет вас.', 14, 0, 100, 15, 0, 50449, 96159, 0, '');
INSERT INTO `creature_text` VALUES (90316, 9, 0, 'Истинна только твоя смерть!', 14, 0, 100, 15, 0, 50441, 96156, 0, '');
INSERT INTO `creature_text` VALUES (90316, 12, 1, 'Падать придется долго!', 14, 0, 100, 15, 0, 50448, 96180, 0, '');
INSERT INTO `creature_text` VALUES (90316, 1, 0, 'Тени поглотят вас.', 14, 0, 100, 15, 0, 50436, 96152, 0, '');
INSERT INTO `creature_text` VALUES (90316, 2, 1, 'Терокк не защитит вас!', 14, 0, 100, 15, 0, 50445, 96177, 0, '');
INSERT INTO `creature_text` VALUES (90316, 10, 0, 'Терокк обманул тебя!', 14, 0, 100, 15, 0, 50441, 96157, 0, '');
INSERT INTO `creature_text` VALUES (90316, 6, 0, 'Узрите истинную мощь!', 14, 0, 100, 15, 0, 50443, 96175, 0, '');
INSERT INTO `creature_text` VALUES (90316, 12, 0, 'Учитесь летать, щенки.', 14, 0, 100, 15, 0, 50446, 96178, 0, '');
INSERT INTO `creature_text` VALUES (90378, 6, 0, '%s выбирает вас целью для своей способности |cFFFF0404|Hspell:180372|h[\"Пронзатель сердец\"]|h|r!', 42, 0, 100, 0, 0, 50431, 94137, 0, '');
INSERT INTO `creature_text` VALUES (90378, 5, 0, 'Во избежание нанесения урона всем участникам рейда кто-то должен находиться в каждой руне \"Видений смерти\"!', 42, 0, 100, 0, 0, 50427, 96930, 0, '');
INSERT INTO `creature_text` VALUES (90378, 1, 0, 'Ко мне, братья! Ваша судьба ждет!', 14, 0, 100, 0, 0, 50421, 93343, 0, '');
INSERT INTO `creature_text` VALUES (90378, 7, 0, 'Наконец… такую смерть я видел!', 14, 0, 100, 0, 0, 50420, 94099, 0, '');
INSERT INTO `creature_text` VALUES (90378, 8, 0, 'Посмотрим, скольких из вас я заберу с собой!', 14, 0, 100, 0, 0, 50429, 94092, 0, '');
INSERT INTO `creature_text` VALUES (90378, 3, 0, 'Смотрите на меня! Так выглядит ваша смерть!', 14, 0, 100, 0, 0, 50430, 94093, 0, '');
INSERT INTO `creature_text` VALUES (90378, 4, 0, 'Ты ТАК хотел умереть?', 14, 0, 100, 0, 0, 50427, 94095, 0, '');
INSERT INTO `creature_text` VALUES (90378, 9, 0, 'Умри!', 14, 0, 100, 0, 0, 50426, 129619, 0, '');
INSERT INTO `creature_text` VALUES (90378, 0, 0, 'Ха-ха-ха! Я ждал этого дня!', 14, 0, 100, 0, 0, 50419, 94091, 0, '');
INSERT INTO `creature_text` VALUES (90378, 2, 0, 'Я вырежу ваши сердца! ', 14, 0, 100, 0, 0, 50431, 94094, 0, '');
INSERT INTO `creature_text` VALUES (90409, 0, 0, 'Ай!', 14, 0, 100, 0, 0, 50828, 95608, 0, '');
INSERT INTO `creature_text` VALUES (90409, 9, 0, 'В атаку!', 14, 0, 100, 0, 0, 50827, 139449, 0, '');
INSERT INTO `creature_text` VALUES (90409, 1, 0, 'Вперед!', 14, 0, 100, 0, 0, 50826, 131943, 0, '');
INSERT INTO `creature_text` VALUES (90409, 11, 0, 'Выдвигаемся!', 14, 0, 100, 0, 0, 50518, 95599, 0, '');
