INSERT INTO `creature_text` VALUES (90485, 0, 0, '|TInterface\\Icons\\inv_blacksmithdye_black.blp:20|t %s роняет на землю |cFFFF0000|Hspell:180147|h[боеприпасы огня Скверны]|h|r.', 41, 0, 100, 0, 0, 50733, 92905, 0, '');
INSERT INTO `creature_text` VALUES (90508, 0, 0, '\"Жажда жизни\" поглотила |3-3($n).', 16, 0, 100, 0, 0, 0, 103666, 0, '');
INSERT INTO `creature_text` VALUES (90508, 1, 0, '|TInterface\\Icons\\ability_fixated_state_purple:20|t%s |cFFFF0404|Hspell:180148|h[сосредотачивает внимание]|h|r на вас!', 42, 0, 100, 0, 0, 0, 92944, 0, '');
INSERT INTO `creature_text` VALUES (90521, 4, 0, 'Громадный ужас яростно завывает!', 41, 0, 100, 0, 0, 0, 94711, 0, '');
INSERT INTO `creature_text` VALUES (90980, 0, 0, 'I will die, but I will not serve the Legion!', 14, 0, 100, 0, 0, 50316, 94173, 0, '');
INSERT INTO `creature_text` VALUES (90980, 1, 0, 'Аааааааа!', 14, 0, 100, 0, 0, 50314, 94167, 0, '');
INSERT INTO `creature_text` VALUES (90980, 2, 0, 'Я ваш должник, герои. Распоряжайтесь мной.', 14, 0, 100, 0, 0, 50315, 94171, 0, '');
INSERT INTO `creature_text` VALUES (91103, 0, 0, '|TInterface\\Icons\\inv_blacksmithdye_black.blp:20|t %s роняет на землю |cFFFF0000|Hspell:180147|h[боеприпасы огня Скверны]|h|r.', 41, 0, 100, 0, 0, 0, 92905, 0, '');
INSERT INTO `creature_text` VALUES (91305, 0, 0, 'Они здесь! Быстрее! Гул\'дан требует завершить это…', 14, 0, 100, 0, 0, 50352, 96071, 0, '');
INSERT INTO `creature_text` VALUES (91326, 4, 0, '|cFFFF0000%s|r начинает усиливать шпиль Скверны!', 41, 0, 100, 0, 0, 0, 93978, 0, '');
INSERT INTO `creature_text` VALUES (91326, 2, 0, 'Восстань, Маннорот… Да, да! Твои повелители вновь призывают тебя!', 14, 0, 100, 0, 0, 50948, 96012, 0, '');
INSERT INTO `creature_text` VALUES (91326, 1, 0, 'Глупцы. Воля Легиона будет исполнена.', 14, 0, 100, 0, 0, 50947, 96022, 0, '');
INSERT INTO `creature_text` VALUES (91326, 7, 0, 'Задержи их. Я почти закончил. Наш владыка скоро будет здесь!', 14, 0, 100, 0, 0, 50951, 93972, 0, '');
INSERT INTO `creature_text` VALUES (91326, 3, 0, 'Не бойся, Маннорот. Дар скверны вернул твою силу. Заставь их страдать!', 14, 0, 100, 0, 0, 50949, 96015, 0, '');
INSERT INTO `creature_text` VALUES (91326, 6, 0, 'Ощути поток силы. Ты - разрушитель, истребитель плоти!', 14, 0, 100, 0, 0, 50950, 96019, 0, '');
INSERT INTO `creature_text` VALUES (91326, 0, 0, 'Появляется |cFFFF0000%s|r и начинает усиливать шпиль Скверны!', 41, 0, 100, 0, 0, 50947, 93975, 0, '');
INSERT INTO `creature_text` VALUES (91331, 19, 0, 'I will break your miserable will.', 14, 0, 100, 0, 0, 50693, 94758, 0, '');
INSERT INTO `creature_text` VALUES (91331, 11, 0, 'I will destroy this miserable world! His remains will be scattered around the Twisting Nether!', 14, 0, 100, 0, 0, 50659, 94782, 0, '');
INSERT INTO `creature_text` VALUES (91331, 32, 0, '|TINTERFACE\\ICONS\\ability_warlock_moltencoregreen.blp:20|t $n $gотмечен:отмечена, для |cFFF00000|Hspell:186961|h[\"Изгнания в Пустоту\"]|h|r!', 41, 0, 100, 0, 0, 0, 96969, 0, '');
INSERT INTO `creature_text` VALUES (91331, 4, 0, '|TINTERFACE\\ICONS\\spell_fire_felflamestrike.blp:20|t Архимонд begins to cast a spell |cFFF00000|Hspell:183254|h[\"Притяжение пламени\"]|h|r!', 41, 0, 100, 0, 0, 50687, 96693, 0, '');
INSERT INTO `creature_text` VALUES (91331, 14, 0, 'Аким-курай!', 14, 0, 100, 0, 0, 50688, 94753, 0, '');
INSERT INTO `creature_text` VALUES (91331, 23, 0, 'Анакх кри!', 14, 0, 100, 0, 0, 50682, 94747, 0, '');
INSERT INTO `creature_text` VALUES (91331, 8, 0, 'Вам не спасти этот мир!', 14, 0, 100, 0, 0, 50707, 94788, 0, '');
INSERT INTO `creature_text` VALUES (91331, 10, 0, 'Вас так легко осквернить.', 14, 0, 100, 0, 0, 50690, 94755, 0, '');
INSERT INTO `creature_text` VALUES (91331, 21, 0, 'Ваша мышиная возня завершится здесь, смертные. Легион покончит с этим миром.', 14, 0, 100, 0, 0, 50649, 94745, 0, '');
INSERT INTO `creature_text` VALUES (91331, 25, 0, 'Ваши души будут моими!', 14, 0, 100, 0, 0, 50696, 94761, 0, '');
INSERT INTO `creature_text` VALUES (91331, 29, 0, 'Ваши души познают вечные муки!', 14, 0, 100, 0, 0, 50697, 94762, 0, '');
INSERT INTO `creature_text` VALUES (91331, 22, 0, 'Все сгорят в огне Легиона.', 14, 0, 100, 0, 0, 50686, 94751, 0, '');
INSERT INTO `creature_text` VALUES (91331, 1, 0, 'Гул\'дан, готовь Черные Врата к вторжению. Этим червям не остановить меня. Свет померкнет в моих руках.', 14, 0, 100, 396, 0, 50663, 94742, 0, '');
INSERT INTO `creature_text` VALUES (91331, 6, 0, 'Жалкие отродья!', 14, 0, 100, 0, 0, 50681, 94746, 0, '');
INSERT INTO `creature_text` VALUES (91331, 24, 0, 'Жалкий червь.', 14, 0, 100, 0, 0, 50680, 94787, 0, '');
INSERT INTO `creature_text` VALUES (91331, 12, 0, 'Забвение уже близко, маг! Сдавайся!', 14, 0, 100, 0, 0, 50660, 94783, 0, '');
INSERT INTO `creature_text` VALUES (91331, 3, 0, 'Кайрел-нарак!', 14, 0, 100, 0, 0, 50689, 94754, 0, '');
INSERT INTO `creature_text` VALUES (91331, 30, 0, 'Легкая победа.', 14, 0, 100, 0, 0, 50689, 94786, 0, '');
INSERT INTO `creature_text` VALUES (91331, 27, 0, 'Мне надоела эта бесцельная забава. Пред вами — бессмертный Легион, бич тысячи миров.', 14, 0, 100, 0, 0, 50658, 94781, 0, '');
INSERT INTO `creature_text` VALUES (91331, 2, 0, 'Мой приход предвещает конец вашего убогого мира. Ваше скудное войско станет первой жертвой Пылающего Легиона!', 14, 0, 100, 0, 0, 50664, 94743, 0, '');
INSERT INTO `creature_text` VALUES (91331, 5, 0, 'Прими свою плачевную судьбу.', 14, 0, 100, 0, 0, 50687, 94752, 0, '');
INSERT INTO `creature_text` VALUES (91331, 18, 0, 'Слизняк.', 14, 0, 100, 0, 0, 50678, 94785, 0, '');
INSERT INTO `creature_text` VALUES (91331, 20, 0, 'Твой жалкий свет бессилен против меня, дитя. Довольно этой глупости!', 14, 0, 100, 0, 0, 50651, 94774, 0, '');
INSERT INTO `creature_text` VALUES (91331, 31, 0, 'Тебе не выбраться из бездны!', 14, 0, 100, 0, 0, 50698, 94763, 0, '');
INSERT INTO `creature_text` VALUES (91331, 13, 0, 'Тебя поглотит вечная тьма!', 14, 0, 100, 0, 0, 50699, 94764, 0, '');
INSERT INTO `creature_text` VALUES (91331, 0, 0, 'Трепещите, смертные! Этот мир обречен.', 14, 0, 100, 396, 0, 50662, 94741, 0, '');
INSERT INTO `creature_text` VALUES (91331, 26, 0, 'Узрите несметные войска Пылающего Легиона и осознайте всю тщетность борьбы!', 14, 0, 100, 0, 0, 50655, 94778, 0, '');
INSERT INTO `creature_text` VALUES (91331, 16, 0, 'Узрите пламя, поглотившее сотни миров!', 14, 0, 100, 0, 0, 50685, 94750, 0, '');
INSERT INTO `creature_text` VALUES (91331, 28, 0, 'Хватит! Вашим тщетным потугам — КОНЕЦ!', 14, 0, 100, 0, 0, 50654, 94777, 0, '');
INSERT INTO `creature_text` VALUES (91331, 15, 0, 'Червь!', 14, 0, 100, 0, 0, 50683, 94748, 0, '');
INSERT INTO `creature_text` VALUES (91331, 7, 0, 'Этот мир сгорит!', 14, 0, 100, 0, 0, 50684, 94749, 0, '');
INSERT INTO `creature_text` VALUES (91331, 17, 0, 'Этот презренный мирок уже пал жертвой моей скверны. Сама земля отмечена клеймом Легиона!', 14, 0, 100, 0, 0, 50650, 94773, 0, '');
INSERT INTO `creature_text` VALUES (91331, 9, 0, 'Эхо вашей гибели разнесется по этому никчемному миру, повергая в отчаяние живущих!', 14, 0, 100, 0, 0, 50648, 94744, 0, '');
INSERT INTO `creature_text` VALUES (91349, 15, 0, '|TInterface\\Icons\\ability_ironmaidens_convulsiveshadows:20|t%s begins to cast a spell |cFFFF0000|Hspell:182084|h[\"Усиленная темная мощь\"]|h|r!', 41, 0, 100, 0, 0, 0, 95482, 0, '');
INSERT INTO `creature_text` VALUES (91349, 3, 0, 'Бойся силы Легиона!', 14, 0, 100, 0, 0, 50482, 96014, 0, '');
INSERT INTO `creature_text` VALUES (91349, 1, 0, 'Ваш конец настал.', 14, 0, 100, 0, 0, 50494, 96034, 0, '');
INSERT INTO `creature_text` VALUES (91349, 7, 0, 'Ваша плоть сгорит.', 14, 0, 100, 0, 0, 50492, 96033, 0, '');
INSERT INTO `creature_text` VALUES (91349, 14, 0, 'Достойная попытка... но тщетная.', 14, 0, 100, 0, 0, 50475, 96036, 0, '');
INSERT INTO `creature_text` VALUES (91349, 13, 0, 'Кричите! Корчитесь в муках!', 14, 0, 100, 0, 0, 50478, 96038, 0, '');
INSERT INTO `creature_text` VALUES (91349, 11, 0, 'Легион испепелит ваш мир, как и все миры до этого!', 14, 0, 100, 0, 0, 50491, 96032, 0, '');
INSERT INTO `creature_text` VALUES (91349, 9, 0, 'МЕНЯ… НЕ… ОСТАНОВИТЬ…', 14, 0, 100, 0, 0, 50462, 96020, 0, '');
INSERT INTO `creature_text` VALUES (91349, 5, 0, 'Мои силы вернулись! Я неудержим!', 14, 0, 100, 0, 0, 50460, 96016, 0, '');
INSERT INTO `creature_text` VALUES (91349, 10, 0, 'На колени!', 14, 0, 100, 0, 0, 50489, 96030, 0, '');
INSERT INTO `creature_text` VALUES (91349, 6, 0, 'Прочь!', 14, 0, 100, 0, 0, 50483, 135117, 0, '');
INSERT INTO `creature_text` VALUES (91349, 8, 0, 'Смертные не могут быть так сильны. Гул\'дан, сделай что-нибудь!', 14, 0, 100, 0, 0, 50461, 96018, 0, '');
INSERT INTO `creature_text` VALUES (91349, 12, 0, 'Трепещи перед мощью Легиона!', 14, 0, 100, 0, 0, 50484, 96023, 0, '');
INSERT INTO `creature_text` VALUES (91349, 2, 0, 'Умрите, шавки.', 14, 0, 100, 0, 0, 50486, 96028, 0, '');
INSERT INTO `creature_text` VALUES (91349, 0, 0, 'Что… Что это? Гул\'дан, что ты наделал? Я… так слаб…', 14, 0, 100, 0, 0, 50459, 96013, 0, '');
INSERT INTO `creature_text` VALUES (91349, 4, 0, 'Я - Разрушитель!', 14, 0, 100, 0, 0, 50480, 96042, 0, '');
INSERT INTO `creature_text` VALUES (91521, 0, 0, 'I am the will of the Punisher ... and your doom.', 12, 0, 100, 0, 0, 0, 93879, 0, '');
INSERT INTO `creature_text` VALUES (91522, 0, 0, 'Дальше ты не пройдешь!', 14, 0, 100, 0, 0, 0, 103319, 0, '');
INSERT INTO `creature_text` VALUES (91604, 0, 0, '|TInterface\\Icons\\spell_fel_elementaldevastation.blp:20|t|cFFFF0000|Hspell:181498|h[\"Волна разрушения\"]|h|r ударяет в |cFFFF0000|Hspell:179620|h[кристалл Скверны]|h|r!', 16, 0, 100, 0, 0, 0, 94031, 0, '');
INSERT INTO `creature_text` VALUES (91710, 0, 0, 'ЗА АЗЕРОТ!', 14, 0, 100, 0, 0, 50632, 135754, 0, '');
INSERT INTO `creature_text` VALUES (91710, 0, 1, 'Мы так легко не сдадимся!', 14, 0, 100, 0, 0, 50404, 93605, 0, '');
INSERT INTO `creature_text` VALUES (91710, 0, 2, 'С последним вздохом Кадгар высвобождает мощнейший взрыв маны, убивая вокруг все живое.', 42, 0, 100, 0, 0, 0, 96187, 0, '');
INSERT INTO `creature_text` VALUES (91830, 1, 0, '|TInterface\\Icons\\warlock_summon_doomguard.blp:20|t|cFFFF0000$n|r поражается |cFFFF0000|Hspell:181275|h[\"Проклятием Легиона\"]|h|r!', 41, 0, 100, 0, 0, 0, 93974, 0, '');
INSERT INTO `creature_text` VALUES (92038, 0, 0, 'Вам не удержать нас!', 14, 0, 100, 565, 0, 50360, 94300, 0, '');
INSERT INTO `creature_text` VALUES (92041, 0, 0, 'Теперь умрите!', 14, 0, 100, 0, 0, 0, 98255, 0, '');
INSERT INTO `creature_text` VALUES (92142, 2, 0, '%s выходит из тени!', 41, 0, 100, 0, 0, 0, 96134, 0, '');
INSERT INTO `creature_text` VALUES (92142, 1, 0, '|TInterface\\Icons\\Spell_Frost_WindWalkOn:20|t %s активирует способность |cFFFF0404|Hspell:183480|h[\"Ветроступ\"]|h|r, оставляя на своем месте зеркальные изображения!', 41, 0, 100, 0, 0, 50405, 94867, 0, '');
INSERT INTO `creature_text` VALUES (92142, 5, 0, 'Ваша тьма пылает во мне.', 14, 0, 100, 0, 0, 50414, 96621, 0, '');
INSERT INTO `creature_text` VALUES (92142, 3, 0, 'Гори! Гори!', 14, 0, 100, 0, 0, 50412, 96278, 0, '');
INSERT INTO `creature_text` VALUES (92142, 0, 0, 'Мой меч жаждет отмщения!', 14, 0, 100, 0, 0, 50405, 96274, 0, '');
INSERT INTO `creature_text` VALUES (92142, 4, 0, 'Я - вечное пламя!', 14, 0, 100, 0, 0, 50406, 96625, 0, '');
INSERT INTO `creature_text` VALUES (92144, 1, 0, '%s |cFFFF0404|Hspell:184476|h[пожинает]|h|r всех отмеченных противников!', 41, 0, 100, 0, 0, 50772, 95369, 0, '');
INSERT INTO `creature_text` VALUES (92144, 6, 0, '%s отмечает половину своих противников |cFFFF0404|Hspell:184449|h[\"Знаком некроманта\"]|h|r!', 41, 0, 100, 0, 0, 50771, 95261, 0, '');
INSERT INTO `creature_text` VALUES (92144, 3, 0, '%s призывает ужасы из |cFFFF0404|Hspell:184681|h[Тьмы]|h|r!', 41, 0, 100, 0, 0, 50774, 95262, 0, '');
INSERT INTO `creature_text` VALUES (92144, 4, 0, 'Бездна жаждет.', 14, 0, 100, 0, 0, 50773, 96252, 0, '');
INSERT INTO `creature_text` VALUES (92144, 2, 0, 'И мертвые вновь восстанут из могил.', 14, 0, 100, 0, 0, 50774, 96253, 0, '');
INSERT INTO `creature_text` VALUES (92144, 5, 0, 'Их души будут запятнаны тьмой.', 14, 0, 100, 0, 0, 50771, 96250, 0, '');
INSERT INTO `creature_text` VALUES (92144, 7, 0, 'Милосердие смерти.', 14, 0, 100, 0, 0, 28232, 96246, 0, '');
INSERT INTO `creature_text` VALUES (92144, 0, 0, 'Она терзает. Бескрайняя и вечная.', 14, 0, 100, 0, 0, 50772, 96251, 0, '');
INSERT INTO `creature_text` VALUES (92146, 2, 0, '|TInterface\\Icons\\inv_misc_boilingblood.blp:20|t %s начинает использовать |cFFFF0404|Hspell:184357|h[\"Оскверненную кровь\"]|h|r!', 41, 0, 100, 0, 0, 50402, 95260, 0, '');
INSERT INTO `creature_text` VALUES (92146, 4, 0, 'АрРРрргх...', 14, 0, 100, 0, 0, 50389, 96257, 0, '');
INSERT INTO `creature_text` VALUES (92146, 1, 0, 'Больше! Хочу больше!', 14, 0, 100, 0, 0, 50402, 96273, 0, '');
INSERT INTO `creature_text` VALUES (92146, 3, 0, 'Выпью кровь! Пожру плоть!', 14, 0, 100, 0, 0, 50400, 96271, 0, '');
INSERT INTO `creature_text` VALUES (92146, 5, 0, 'Железная Орда сокрушит вас!', 14, 0, 100, 0, 0, 50388, 96256, 0, '');
INSERT INTO `creature_text` VALUES (92146, 0, 0, 'Я выпущу вам кишки!', 14, 0, 100, 0, 0, 50401, 96272, 0, '');
INSERT INTO `creature_text` VALUES (92208, 0, 0, '|TINTERFACE\\ICONS\\ability_fixated_state_red.blp:20|t Дух Рокового Огня |cFFF00000|Hspell:182879|h[сосредотачивает внимание]|h|r на вас!', 42, 0, 100, 0, 0, 0, 96743, 0, '');
INSERT INTO `creature_text` VALUES (92554, 0, 0, '|TInterface\\Icons\\spell_fire_ragnaros_molteninferno.blp:20|t$n |cFFFF0000|Hspell:185828|h[разжигает]|h|r выжигателя Скверны!', 16, 0, 100, 0, 0, 0, 96139, 0, '');
INSERT INTO `creature_text` VALUES (92554, 1, 0, '|TInterface\\Icons\\spell_fire_ragnaros_molteninferno.blp:20|t$n |cFFFF0000|Hspell:185835|h[воспламеняет]|h|r всех находящихся поблизости выжигателей Скверны!', 16, 0, 100, 0, 0, 0, 96140, 0, '');
INSERT INTO `creature_text` VALUES (92769, 1, 0, 'Нет! Я никогда не поддамся тьме. Легион падет!', 14, 0, 100, 0, 0, 50563, 94800, 0, '');
INSERT INTO `creature_text` VALUES (92769, 0, 0, 'Свет, защити нас от воплощения зла! Дай силы изгнать скверну!', 14, 0, 100, 0, 0, 50562, 94798, 0, '');
INSERT INTO `creature_text` VALUES (92769, 8, 0, 'Свет… сильнее.', 14, 0, 100, 0, 0, 50564, 94801, 0, '');
INSERT INTO `creature_text` VALUES (92911, 0, 0, 'В атаку!', 14, 0, 100, 0, 0, 50497, 139449, 0, '');
INSERT INTO `creature_text` VALUES (92911, 1, 0, 'Вперед!', 14, 0, 100, 0, 0, 50496, 131943, 0, '');
INSERT INTO `creature_text` VALUES (92911, 9, 0, 'Выдвигаемся!', 14, 0, 100, 0, 0, 50495, 95599, 0, '');
INSERT INTO `creature_text` VALUES (92911, 3, 0, 'Ой!', 14, 0, 100, 0, 0, 50498, 144842, 0, '');
INSERT INTO `creature_text` VALUES (93023, 8, 0, 'Батарея, огонь!', 14, 0, 100, 0, 0, 50732, 95215, 0, '');
INSERT INTO `creature_text` VALUES (93023, 13, 0, 'Будет жарко!', 14, 0, 100, 0, 0, 50733, 95216, 0, '');
INSERT INTO `creature_text` VALUES (93023, 3, 0, 'Вижу, у нас гости. К оружию!', 14, 0, 100, 0, 0, 50538, 95206, 0, '');
INSERT INTO `creature_text` VALUES (93023, 4, 0, 'Вы решили потягаться с Ордой Скверны? Ко мне, воины! Вышвырнем этих непрошеных гостей!', 14, 0, 100, 0, 0, 50517, 95207, 0, '');
INSERT INTO `creature_text` VALUES (93023, 11, 0, 'Давите их!', 14, 0, 100, 0, 0, 50735, 95218, 0, '');
INSERT INTO `creature_text` VALUES (93023, 16, 0, 'Если хочешь что-то сделать хорошо, сделай это сама…', 14, 0, 100, 0, 0, 50518, 95213, 0, '');
INSERT INTO `creature_text` VALUES (93023, 10, 0, 'За Гул\'дана!', 14, 0, 100, 0, 0, 50540, 93781, 0, '');
INSERT INTO `creature_text` VALUES (93023, 17, 0, 'Мы победили!', 14, 0, 100, 0, 0, 50541, 95210, 0, '');
INSERT INTO `creature_text` VALUES (93023, 5, 0, 'Отведайте топора!', 14, 0, 100, 0, 0, 50542, 95211, 0, '');
INSERT INTO `creature_text` VALUES (93023, 9, 0, 'Пришлите буравчик!', 14, 0, 100, 0, 0, 50738, 95221, 0, '');
INSERT INTO `creature_text` VALUES (93023, 18, 0, 'Пушки скоро прибудут.', 14, 0, 100, 0, 0, 50736, 95219, 0, '');
INSERT INTO `creature_text` VALUES (93023, 7, 0, 'Разрушение!', 14, 0, 100, 0, 0, 50529, 95224, 0, '');
INSERT INTO `creature_text` VALUES (93023, 0, 0, 'Слушайте меня, воины. Наши враги уже у стен великой цитадели.  Будьте наготове.', 14, 0, 100, 0, 0, 50532, 95201, 0, '');
INSERT INTO `creature_text` VALUES (93023, 12, 0, 'Смерть приходит ниоткуда… ', 14, 0, 100, 0, 0, 50737, 95220, 0, '');
INSERT INTO `creature_text` VALUES (93023, 15, 0, 'Спалите их дотла!', 14, 0, 100, 0, 0, 50828, 95223, 0, '');
INSERT INTO `creature_text` VALUES (93023, 14, 0, 'Ха! Потопчитесь на их трупах!', 14, 0, 100, 0, 0, 50539, 95208, 0, '');
INSERT INTO `creature_text` VALUES (93023, 2, 0, 'Что там с машинами?', 14, 0, 100, 25, 0, 50537, 95205, 0, '');
INSERT INTO `creature_text` VALUES (93023, 1, 0, 'Эй, ты, проверь укрепления цитадели.', 14, 0, 100, 25, 0, 50536, 95204, 0, '');
INSERT INTO `creature_text` VALUES (93023, 6, 0, 'Я вернусь...', 14, 0, 100, 0, 0, 50519, 95214, 0, '');
INSERT INTO `creature_text` VALUES (93068, 6, 0, 'I DECOMPOSE! And WITH ME ... YOU DIE!', 14, 0, 100, 0, 0, 50623, 96880, 0, '');
INSERT INTO `creature_text` VALUES (93068, 3, 0, 'Все будет поглощено! Жизнь исчезнет!', 14, 0, 100, 0, 0, 50614, 96882, 0, '');
INSERT INTO `creature_text` VALUES (93068, 10, 0, 'ГОРИТЕ!', 14, 0, 100, 0, 0, 50620, 133419, 0, '');
INSERT INTO `creature_text` VALUES (93068, 9, 0, 'ДА! ДА! Я ВИЖУ! Я вижу… конец…', 14, 0, 100, 0, 0, 50612, 96888, 0, '');
INSERT INTO `creature_text` VALUES (93068, 7, 0, 'Жизнь должна сгинуть.', 14, 0, 100, 0, 0, 50620, 96877, 0, '');
INSERT INTO `creature_text` VALUES (93068, 2, 0, 'ЛЕГИОН! Убейте всех на своем пути!', 14, 0, 100, 53, 0, 50622, 96879, 0, '');
INSERT INTO `creature_text` VALUES (93068, 5, 0, 'Не могу остаться… Я становлюсь… ВСЕМ! НИЧЕМ!', 14, 0, 100, 0, 0, 50615, 96883, 0, '');
INSERT INTO `creature_text` VALUES (93068, 1, 0, 'Разрывы реальности... Я должен расширить их!', 14, 0, 100, 0, 0, 50613, 96881, 0, '');
INSERT INTO `creature_text` VALUES (93068, 4, 0, 'Слуги Бездны! Поглотите этот мир!', 14, 0, 100, 53, 0, 50619, 96876, 0, '');
INSERT INTO `creature_text` VALUES (93068, 8, 0, 'СТРАДАЙТЕ!', 14, 0, 100, 0, 0, 50620, 94017, 0, '');
INSERT INTO `creature_text` VALUES (93068, 11, 0, 'Чем бы все ни закончилось, вы умрете здесь.', 14, 0, 100, 0, 0, 50610, 96874, 0, '');
INSERT INTO `creature_text` VALUES (93068, 0, 0, 'Я могу столько вам показать…', 14, 0, 100, 0, 0, 50611, 96875, 0, '');
INSERT INTO `creature_text` VALUES (93154, 0, 0, 'Вы слышали повелителя. Этим чужакам пора умереть!', 14, 0, 100, 0, 0, 50364, 94969, 0, '');
INSERT INTO `creature_text` VALUES (93154, 1, 0, 'Наконец… едина… с повелителем…', 14, 0, 100, 0, 0, 50362, 94970, 0, '');
INSERT INTO `creature_text` VALUES (93369, 0, 0, 'Громадный ужас яростно завывает!', 41, 0, 100, 0, 0, 0, 94711, 0, '');
INSERT INTO `creature_text` VALUES (93618, 2, 0, 'Лок\'тар огар! Мы тесним их! К порталу! С Гул\'даном я разберусь сам!', 14, 0, 100, 0, 0, 50804, 96890, 0, '');
INSERT INTO `creature_text` VALUES (93618, 0, 0, 'Просто так тебе не захватить этот мир, демон. Его обитатели не сдадутся без боя. Наша воля несокрушима! За Дренор!', 14, 0, 100, 0, 0, 50803, 96889, 0, '');
INSERT INTO `creature_text` VALUES (93858, 1, 0, 'В атаку!', 14, 0, 100, 0, 0, 50497, 139449, 0, '');
INSERT INTO `creature_text` VALUES (93858, 0, 0, 'Жду приказаний!', 14, 0, 100, 0, 0, 50499, 95603, 0, '');
INSERT INTO `creature_text` VALUES (93913, 0, 0, '|TInterface\\Icons\\spell_fire_ragnaros_molteninferno.blp:20|t$n |cFFFF0000|Hspell:185824|h[взрывается]|h|r в момент гибели!', 16, 0, 100, 0, 0, 0, 96142, 0, '');
INSERT INTO `creature_text` VALUES (93926, 0, 0, 'Глупцы! Здесь вас ждет лишь смерть! Черные Врата готовы!', 14, 0, 100, 0, 0, 0, 95882, 0, '');
INSERT INTO `creature_text` VALUES (93926, 2, 0, 'Да, повелитель.', 14, 0, 100, 1, 0, 50946, 95884, 0, '');
INSERT INTO `creature_text` VALUES (93926, 1, 0, 'Мощь легиона сокрушит вас!', 14, 0, 100, 1, 0, 50945, 95883, 0, '');
INSERT INTO `creature_text` VALUES (93931, 1, 0, 'Выдвигаемся!', 14, 0, 100, 0, 0, 50825, 95599, 0, '');
INSERT INTO `creature_text` VALUES (93931, 0, 0, 'Я готова!', 14, 0, 100, 0, 0, 50830, 95610, 0, '');
INSERT INTO `creature_text` VALUES (93955, 0, 0, '|cFFFF0000%s|r begins to cast a spell |cFFFF0000|Hspell:186045|h[\"Солнечный шакрам\"]|h|r, нацеленное на вас!', 42, 0, 100, 0, 0, 0, 95630, 0, '');
INSERT INTO `creature_text` VALUES (94142, 1, 0, 'Никогда! Этот мир не падет перед Легионом!', 14, 0, 100, 0, 0, 50666, 95709, 0, '');
INSERT INTO `creature_text` VALUES (94142, 0, 0, 'Помогите мне, герои! Это еще не конец! Мы должны выжить!', 14, 0, 100, 0, 0, 50665, 95708, 0, '');
INSERT INTO `creature_text` VALUES (94916, 0, 0, 'Прикосновение Скверны уже совсем близко!', 12, 0, 100, 0, 0, 0, 96615, 0, '');
INSERT INTO `creature_text` VALUES (95659, 2, 0, 'I broke the first seal, soon the portal will lead us to Gul\'dan.', 14, 0, 100, 0, 0, 50671, 95715, 0, '');
INSERT INTO `creature_text` VALUES (95659, 5, 0, 'Гул\'дан сбежал к Темному Порталу! Быстрее! Ему там некуда деться, покончим с ним!', 14, 0, 100, 0, 0, 50676, 95721, 0, '');
INSERT INTO `creature_text` VALUES (95659, 0, 0, 'Мы прорвались в цитадель! Гул\'дан внутри. Я обустрою плацдарм. Развивайте наступление!', 14, 0, 100, 0, 0, 50667, 95710, 0, '');
INSERT INTO `creature_text` VALUES (95659, 3, 0, 'Никогда бы не поверил, что Гул\'дан зайдет так далеко… Продолжаем наступление. В этих руинах есть портал вглубь цитадели.', 14, 0, 100, 0, 0, 50668, 95712, 0, '');
INSERT INTO `creature_text` VALUES (95659, 4, 0, 'Портал готов, герои. Крепитесь. Мне неведомо, каких чудовищ Гул\'дан призвал по ту его сторону.', 14, 0, 100, 0, 0, 50669, 95713, 0, '');
INSERT INTO `creature_text` VALUES (95659, 6, 0, 'Портал открыт! Он приведет нас прямо к Гул\'дану. Он заплатит за свои преступления.', 14, 0, 100, 0, 0, 50675, 95720, 0, '');
INSERT INTO `creature_text` VALUES (95659, 1, 0, 'Порча Гул\'дана сочится из стен этой цитадели! Второй портал рядом. Сдерживайте демонов, пока я ломаю печати и привязываю портал к его логову.', 14, 0, 100, 0, 0, 50670, 95714, 0, '');
INSERT INTO `creature_text` VALUES (95795, 1, 0, 'Вы не смогли остановить меня в Шаттрате, не справитесь и здесь, глупцы!', 14, 0, 100, 0, 0, 50840, 95111, 0, '');
INSERT INTO `creature_text` VALUES (95795, 5, 0, 'Голем полностью готов… а ритуал единения может подождать… Ну что, время платить по счетам, мерзкие букашки?', 14, 0, 100, 0, 0, 50844, 95115, 0, '');
INSERT INTO `creature_text` VALUES (95795, 2, 0, 'Еще немного, и ритуал будет завершен…', 14, 0, 100, 0, 0, 50841, 95112, 0, '');
INSERT INTO `creature_text` VALUES (95795, 0, 0, 'Они здесь! Остановите их, саргераи!', 14, 0, 100, 0, 0, 50839, 95110, 0, '');
INSERT INTO `creature_text` VALUES (95795, 3, 0, 'Сражайтесь! Убейте их!', 14, 0, 100, 0, 0, 50842, 95113, 0, '');
INSERT INTO `creature_text` VALUES (95795, 4, 0, 'Этого следовало ожидать. Впрочем, неважно.', 14, 0, 100, 0, 0, 50843, 95114, 0, '');
INSERT INTO `creature_text` VALUES (140853, 0, 0, 'Alert. Viral containment breached. Contaminated specimen approaching. Risk of infection imminent.', 14, 0, 100, 0, 0, 104967, 0, 0, 'MOTHER');
