INSERT INTO `creature_text` VALUES (10296, 0, 0, 'At last - one among you is worthy of my knowledge. Worry not - I have probed your thoughts, no harm will come to you.', 12, 0, 100, 1, 0, 0, 5555, 0, 'Vaelan');
INSERT INTO `creature_text` VALUES (10296, 1, 0, 'Listen carefully, for the lord of Blackrock will surely destroy you, should you not be prepared.', 12, 0, 100, 1, 0, 0, 5556, 0, 'Vaelan');
INSERT INTO `creature_text` VALUES (10296, 2, 0, '%s is surrounded by a barrier of anti-magic.', 16, 0, 100, 0, 0, 0, 5590, 0, 'Vaelan');
INSERT INTO `creature_text` VALUES (10296, 3, 0, 'I have discerned the path that you must take. Are you prepared?', 12, 0, 100, 1, 0, 0, 5591, 0, 'Vaelan');
INSERT INTO `creature_text` VALUES (10299, 0, 0, 'Begone, mortal. You are not worthy of my knowledge.', 15, 0, 100, 0, 0, 0, 5592, 0, 'Scarshield Infiltrator');
INSERT INTO `creature_text` VALUES (20201, 0, 0, 'Stop! Do not go any further, mortal. You are ill-prepared to face the forces of the Infinite Dragonflight. Come, let me help you.', 12, 0, 100, 1, 0, 0, 17814, 0, 'Saat');
INSERT INTO `creature_text` VALUES (4489, 0, 0, 'Ah, Ysera the Dreamer.', 12, 0, 100, 1, 3000, 0, 8382, 0, 'Braug Dimspirit - Test of Lore');
INSERT INTO `creature_text` VALUES (4489, 1, 0, 'Neltharion, hmm? As good an answer as any, yes?', 12, 0, 100, 1, 3000, 0, 8386, 0, 'Braug Dimspirit - Test of Lore');
INSERT INTO `creature_text` VALUES (4489, 2, 0, 'Alexstrazsa, the Life-Binder. A magnificent creature.', 12, 0, 100, 1, 3000, 0, 8387, 0, 'Braug Dimspirit - Test of Lore');
INSERT INTO `creature_text` VALUES (4489, 3, 0, 'Malygos, is it?', 12, 0, 100, 1, 3000, 0, 8388, 0, 'Braug Dimspirit - Test of Lore');
INSERT INTO `creature_text` VALUES (4489, 4, 0, 'That is the wrong answer, $n. Be off with you.', 12, 0, 100, 1, 3000, 0, 8393, 0, 'Braug Dimspirit - Test of Lore');
INSERT INTO `creature_text` VALUES (10817, 2, 0, 'Where th\' scent of death is on th\' wind and everythin\' is mostly brown.', 12, 0, 100, 6, 0, 0, 50908, 0, 'Duggan Wildhammer - Line 2');
INSERT INTO `creature_text` VALUES (10817, 3, 0, 'An\' when he did arrive there, what\'d his dwarf eyes see?', 12, 0, 100, 25, 0, 0, 50909, 0, 'Duggan Wildhammer - Line 3');
INSERT INTO `creature_text` VALUES (10817, 4, 0, 'A hundred crates of barley there ta\' be makin\' inta\' mead!', 12, 0, 100, 5, 0, 0, 50910, 0, 'Duggan Wildhammer - Line 4');
INSERT INTO `creature_text` VALUES (10817, 5, 0, 'But tha\' mead was cursed with th\' plague o\' death, and now old Duggan, too.', 12, 0, 100, 18, 0, 0, 50911, 0, 'Duggan Wildhammer - Line 5');
INSERT INTO `creature_text` VALUES (10817, 6, 0, 'An\' surrounded by the Lich King\'s beasts, what could old Duggan do?', 12, 0, 100, 430, 0, 0, 50912, 0, 'Duggan Wildhammer - Line 6');
INSERT INTO `creature_text` VALUES (10817, 7, 0, 'But though I feel the plague within, my hopes \'ave not yet sunk.', 12, 0, 100, 274, 0, 0, 50913, 0, 'Duggan Wildhammer - Line 7');
INSERT INTO `creature_text` VALUES (10817, 8, 0, 'If\'n I\'m gonna be Scourge anyway, I might as well be drunk!', 12, 0, 100, 11, 0, 0, 50914, 0, 'Duggan Wildhammer - Line 8');
INSERT INTO `creature_text` VALUES (10817, 9, 0, 'Ah ken see very well through this haze, but I\'d know tha\' smell anywhere!  Die ye foul ogre!', 12, 0, 100, 0, 0, 0, 50905, 0, 'Duggan Wildhammer - Line 9');
INSERT INTO `creature_text` VALUES (951, 0, 1, 'AND I LAY MY HANDS UPON YOU!', 12, 0, 100, 0, 0, 0, 49890, 0, 'Brother Paxton');
INSERT INTO `creature_text` VALUES (951, 0, 2, 'Let the Holy Light embrace you!', 12, 0, 100, 0, 0, 0, 49891, 0, 'Brother Paxton');
INSERT INTO `creature_text` VALUES (951, 0, 3, 'BY THE LIGHT BE RENEWED!', 12, 0, 100, 0, 0, 0, 49892, 0, 'Brother Paxton');
INSERT INTO `creature_text` VALUES (951, 0, 4, 'FIGHT ON, $G BROTHER:SISTER;!', 12, 0, 100, 0, 0, 0, 49893, 0, 'Brother Paxton');
