INSERT INTO `creature_text` VALUES (6746, 0, 0, 'I may have ninety-nine problems, but dancin\' ain\'t one of them!  Happy Hallow\'s End, $n!', 12, 0, 100, 94, 0, 0, 10787, 0, 'Innkeeper Pala');
INSERT INTO `creature_text` VALUES (6741, 0, 0, 'Bawk, bawk, bawk!  Happy Hallow\'s End, $n!', 12, 0, 100, 19, 0, 0, 10786, 0, 'Innkeeper Norman');
INSERT INTO `creature_text` VALUES (6740, 0, 0, 'Ooo, so strong!  Happy Hallow\'s End, $n!', 12, 0, 100, 23, 0, 0, 10797, 0, 'Innkeeper Allison');
