INSERT INTO `creature_text` VALUES (18810, 0, 0, 'How can you have any pudding if you don\'t eat your fish?', 12, 7, 100, 5, 0, 0, 15941, 0, 'Otonambusi');
INSERT INTO `creature_text` VALUES (18810, 0, 1, 'If you don\'t eat your fish, you can\'t have any pudding!', 12, 7, 100, 5, 0, 0, 15942, 0, 'Otonambusi');
INSERT INTO `creature_text` VALUES (27056, 0, 0, '%s grins at Sentinel Amberline across the camp, waggling her long eyebrows suggestively.', 16, 0, 100, 0, 0, 0, 29555, 0, 'Sentinel Sweetspring');
INSERT INTO `creature_text` VALUES (27056, 1, 0, '%s peers at Modoru\'s Draenic robes.', 16, 0, 100, 0, 0, 0, 27085, 0, 'Sentinel Sweetspring');
INSERT INTO `creature_text` VALUES (27056, 2, 0, 'Pssst. Naohain. Why does Modoru still wear those ridiculous robes? He looks like some interdimensional bumpkin who stumbled out of Ammen Vale not six hours past.', 12, 2, 100, 0, 0, 0, 27086, 0, 'Sentinel Sweetspring');
INSERT INTO `creature_text` VALUES (27056, 3, 0, 'Say, Modoru. Where did you get those... ah... \"wonderful\" robes of yours, hmmm?', 12, 7, 100, 0, 0, 0, 29546, 0, 'Sentinel Sweetspring');
INSERT INTO `creature_text` VALUES (27056, 4, 0, 'Oh. Oh... I see. How nice!', 12, 7, 100, 0, 0, 0, 29547, 0, 'Sentinel Sweetspring');
INSERT INTO `creature_text` VALUES (27056, 5, 0, '%s bites back a laugh.', 16, 0, 100, 0, 0, 0, 29548, 0, 'Sentinel Sweetspring');
INSERT INTO `creature_text` VALUES (27055, 0, 0, '%s huffs with annoyance, glaring briefly at Sentinel Sweetspring before turning away with her nose in the air.', 16, 0, 100, 0, 0, 0, 29557, 0, 'Sentinel Amberline');
INSERT INTO `creature_text` VALUES (27052, 0, 0, 'I... I do not know.', 12, 2, 100, 0, 0, 0, 29544, 0, 'Naohain');
INSERT INTO `creature_text` VALUES (27054, 0, 0, '%s beams, unaware that she is teasing him, answering in his thickly-accented Common.', 16, 0, 100, 0, 0, 0, 29550, 0, 'Modoru');
INSERT INTO `creature_text` VALUES (27054, 1, 0, 'Well! You see, was gift from Grandmother Oluba. She hand-weave from finest silk on Draenor! Am quite proud of, yes.', 12, 7, 100, 0, 0, 0, 29552, 0, 'Modoru');
INSERT INTO `creature_text` VALUES (27119, 0, 0, '%s snickers, nuzzling into Sentinel Sweetspring\'s ankles.', 16, 0, 100, 0, 0, 0, 29553, 0, 'Snowpaw');
INSERT INTO `creature_text` VALUES (26568, 0, 0, 'Don\'t be shy, elfy. Don\'t you feel dis burnin\' love formin\' between us?', 12, 1, 100, 0, 0, 0, 25790, 0, 'Zebu\'tan');
INSERT INTO `creature_text` VALUES (26568, 1, 0, 'Lookie what Zebu\'tan got for you! Roses for da rose!', 12, 1, 100, 0, 0, 0, 25782, 0, 'Zebu\'tan');
INSERT INTO `creature_text` VALUES (26568, 2, 0, '%s grumbles.', 16, 0, 100, 0, 0, 0, 25783, 0, 'Zebu\'tan');
INSERT INTO `creature_text` VALUES (26568, 3, 0, 'Hey pretty lady, Zebu\'tan got two rugs over here! Why don\'t you come on over and keep he warm, eh?', 12, 1, 100, 0, 0, 0, 25781, 0, 'Zebu\'tan');
INSERT INTO `creature_text` VALUES (26568, 4, 0, 'Dis troll tink you got a crush on he! Come on over!', 12, 1, 100, 0, 0, 0, 25789, 0, 'Zebu\'tan');
INSERT INTO `creature_text` VALUES (26569, 0, 0, 'Get lost, creep!', 12, 1, 100, 0, 0, 0, 25787, 0, 'Alys Vol\'tyr');
INSERT INTO `creature_text` VALUES (26569, 1, 0, 'Ewwww!', 12, 1, 100, 0, 0, 0, 25785, 0, 'Alys Vol\'tyr');
INSERT INTO `creature_text` VALUES (26569, 2, 0, 'Not if you were the last troll on Azeroth!', 12, 1, 100, 0, 0, 0, 25784, 0, 'Alys Vol\'tyr');
INSERT INTO `creature_text` VALUES (26569, 2, 1, 'I\'ve got a man! Now take a hike!', 12, 1, 100, 0, 0, 0, 25788, 0, 'Alys Vol\'tyr');
INSERT INTO `creature_text` VALUES (26569, 3, 0, 'Never!', 12, 1, 100, 0, 0, 0, 25786, 0, 'Alys Vol\'tyr');
INSERT INTO `creature_text` VALUES (26564, 0, 0, 'Faster! Faster! These armaments must be delivered to the Kor\'kron Vanguard TODAY!', 12, 1, 100, 0, 0, 0, 25775, 0, 'Borus Ironbender');
INSERT INTO `creature_text` VALUES (26564, 0, 1, 'I will happily replace you all with goblins! Do not fail me!', 12, 1, 100, 0, 0, 0, 25776, 0, 'Borus Ironbender');
INSERT INTO `creature_text` VALUES (26564, 0, 2, 'Overlord Agmar demands more of us! We must not disappoint!', 12, 1, 100, 0, 0, 0, 25777, 0, 'Borus Ironbender');
INSERT INTO `creature_text` VALUES (26564, 0, 3, 'Don\'t make me jump off of this crate! I will beat your head in, orc.', 12, 1, 100, 0, 0, 0, 25778, 0, 'Borus Ironbender');
INSERT INTO `creature_text` VALUES (26649, 0, 0, 'Pssst... Over here, by the mailbox. Come quickly and make sure you aren\'t followed.', 15, 0, 100, 0, 0, 0, 25823, 0, 'Borus Ironbender');
INSERT INTO `creature_text` VALUES (4275, 1, 0, 'You, too, shall serve!', 14, 0, 100, 0, 0, 0, 6115, 0, 'Archmage Arugal - Aggro');
INSERT INTO `creature_text` VALUES (4275, 2, 0, 'Release your rage!', 14, 0, 100, 0, 0, 0, 6535, 0, 'Archmage Arugal - Transforms player into a Worgen');
INSERT INTO `creature_text` VALUES (4275, 3, 0, 'Another falls!', 14, 0, 100, 0, 0, 0, 6116, 0, 'Archmage Arugal - Killing a player');
INSERT INTO `creature_text` VALUES (18966, 0, 0, 'Soldiers of Azeroth, you now fight against the vile Burning Crusade. And the hopes and prayers of all Light-loving people march with you. In the company of our brave allies and brothers -in-arms you will hold your ground and break the demonic charge. You will stem the destructive tyranny that rages across so many worlds.', 14, 0, 100, 0, 0, 0, 16625, 0, 'Justinius the Harbinger Aggro');
INSERT INTO `creature_text` VALUES (18966, 0, 1, 'With each passing moment, I see the people of Azeroth prove again the reasons we have come. It is with solemn pride that I fight with you, and if I die this day, then I die among friends.', 14, 0, 100, 0, 0, 0, 16633, 0, 'Justinius the Harbinger Aggro');
INSERT INTO `creature_text` VALUES (18966, 0, 2, 'The demons should be in no doubt that we are their nemesis and, though they wish our destruction, it is we who bring about their ruin. Kazzak and his forces will be diminished and defeated by this coalition. And to do this, we must throw at them our bodies and hearts, for they seek no less than the obliteration of this world.', 14, 0, 100, 0, 0, 0, 16627, 0, 'Justinius the Harbinger Aggro');
INSERT INTO `creature_text` VALUES (18966, 0, 3, 'Fight on, good people of Azeroth. And take heart! Your brethren rush to this hellish portal and will soon throw themselves at these demons as you have so bravely done. May the Light keep you!', 14, 0, 100, 0, 0, 0, 16933, 0, 'Justinius the Harbinger Aggro');
INSERT INTO `creature_text` VALUES (18966, 0, 4, 'Behold the power of the Light! Grace and glory to the Alliance!', 14, 0, 100, 0, 0, 0, 16286, 0, 'Justinius the Harbinger Aggro');
INSERT INTO `creature_text` VALUES (18969, 0, 0, 'Hunters, to me! Let us show these demons what it is to fight and die in Azeroth!', 14, 0, 100, 0, 0, 0, 16616, 0, 'MELGROMM_HIGHMOUNTAIN');
INSERT INTO `creature_text` VALUES (18969, 0, 1, 'The spirits sing to us! They sing the song of battle!', 14, 0, 100, 0, 0, 0, 16617, 0, 'MELGROMM_HIGHMOUNTAIN');
INSERT INTO `creature_text` VALUES (18969, 0, 2, 'Strike true, brothers and sisters! Today our quarry is demons!', 14, 0, 100, 0, 0, 0, 16618, 0, 'MELGROMM_HIGHMOUNTAIN');
INSERT INTO `creature_text` VALUES (18969, 0, 3, 'Once again, we will show this Burning Legion that our world is not for the taking!', 14, 0, 100, 0, 0, 0, 16620, 0, 'MELGROMM_HIGHMOUNTAIN');
INSERT INTO `creature_text` VALUES (18969, 0, 4, 'These fiends will fail! For Azeroth!', 14, 0, 100, 0, 0, 0, 16621, 0, 'MELGROMM_HIGHMOUNTAIN');
INSERT INTO `creature_text` VALUES (18969, 0, 5, 'If it is our fate to die this day, then we will not go easy! Mark me, these demons will fall like wheat!', 14, 0, 100, 0, 0, 0, 16622, 0, 'MELGROMM_HIGHMOUNTAIN');
INSERT INTO `creature_text` VALUES (18969, 0, 6, 'Though your sinews tire and your blood flows from cuts and wounds to this sodden ground, you fight for the spirits of your world. No evil force can stand against us!', 14, 0, 100, 0, 0, 0, 16624, 0, 'MELGROMM_HIGHMOUNTAIN');
INSERT INTO `creature_text` VALUES (18969, 0, 7, 'My brethern, my commands-in-arms! I must go to seek the forces of the Horde and direct them here! Keep your spirits strong, brave hunters! Do not fall to the portal-spawned fiends!', 14, 0, 100, 0, 0, 0, 16935, 0, 'MELGROMM_HIGHMOUNTAIN');
INSERT INTO `creature_text` VALUES (24288, 0, 0, 'Stone troggs are attacking!', 42, 0, 100, 0, 0, 0, 0, 0, 'ELM General Purpose Bunny Hide Body to Player');
INSERT INTO `creature_text` VALUES (24288, 1, 0, 'Here come more stone troggs. Defend the catapult!', 42, 0, 100, 0, 0, 0, 0, 0, 'ELM General Purpose Bunny Hide Body to Player');
INSERT INTO `creature_text` VALUES (24288, 2, 0, ' Looks like a last ditch effort by the stone troggs. Your destination is just around the corner!', 42, 0, 100, 0, 0, 0, 0, 0, 'ELM General Purpose Bunny Hide Body to Player');
INSERT INTO `creature_text` VALUES (40789, 0, 0, 'The Stone Dragon Broodmother approaches.', 16, 0, 100, 0, 0, 3525, 0, 0, 'Generic Controller Bunny (CSA) to Player');
INSERT INTO `creature_text` VALUES (42465, 0, 0, 'They seek only power, as the Twilight do.  Yet you are confident that they will not turn on us.  Did I raise you to be fools?', 12, 0, 100, 1, 0, 21105, 0, 0, 'Therazane');
INSERT INTO `creature_text` VALUES (42465, 1, 0, 'They slaughtered stone troggs, Gorik?  This is supposed to sway me?  It is not much of a feat.', 12, 0, 100, 1, 0, 21106, 0, 0, 'Therazane to Therazane');
INSERT INTO `creature_text` VALUES (42465, 2, 0, 'What of you Boden?  What do you see in this \"champion?\"', 12, 0, 100, 1, 0, 21107, 0, 0, 'Therazane to Therazane');
INSERT INTO `creature_text` VALUES (42465, 3, 0, 'That very well may be.  Even fools have uses though.  I keep you around after all.', 12, 0, 100, 1, 0, 21108, 0, 0, 'Therazane to Therazane');
INSERT INTO `creature_text` VALUES (42465, 4, 0, 'And so the \"champion\" arrives.', 12, 0, 100, 1, 0, 21093, 0, 0, 'Therazane to Player');
INSERT INTO `creature_text` VALUES (42465, 5, 0, 'So small.  So soft.  It\'s a wonder you\'ve even lasted this long down here, fleshling.', 12, 0, 100, 1, 0, 21094, 0, 0, 'Therazane to Player');
INSERT INTO `creature_text` VALUES (42465, 6, 0, 'Or perhaps a testament to your abilities?', 12, 0, 100, 1, 0, 21095, 0, 0, 'Therazane to Player');
INSERT INTO `creature_text` VALUES (42465, 7, 0, 'All of this for a piece of the World Pillar?  What exactly, shaman, do you intend to do with it?', 12, 0, 100, 1, 0, 21096, 0, 0, 'Therazane to Player');
INSERT INTO `creature_text` VALUES (42465, 8, 0, 'And what then? Will you leave Deepholm to its denizens?', 12, 0, 100, 1, 0, 21097, 0, 0, 'Therazane to Player');
INSERT INTO `creature_text` VALUES (42465, 9, 0, 'I\'m glad you\'re at least that perceptive.', 12, 0, 100, 1, 0, 21098, 0, 0, 'Therazane to Player');
INSERT INTO `creature_text` VALUES (42465, 10, 0, 'I propose this: We will march on the Twilight cult and extinguish any utterings of Deathwing\'s name from this realm.  Your Earthen Ring will march with us.', 12, 0, 100, 1, 0, 21099, 0, 0, 'Therazane to Player');
INSERT INTO `creature_text` VALUES (42465, 11, 0, 'You do this, and I will send Gorsik to the temple with our piece of the World Pillar.', 12, 0, 100, 1, 0, 21100, 0, 0, 'Therazane to Player');
INSERT INTO `creature_text` VALUES (42465, 12, 0, 'Seal the rift and leave this place.', 12, 0, 100, 1, 0, 21101, 0, 0, 'Therazane to Player');
INSERT INTO `creature_text` VALUES (42465, 13, 0, 'Save your thanks.  We march upon the Twilight Precipice as soon as our forces are gathered. I expect to see you on the battlefield.', 12, 0, 100, 1, 0, 21102, 0, 0, 'Therazane to Player');
INSERT INTO `creature_text` VALUES (42466, 0, 0, 'Speak with Terrath the Steady when you are ready to begin.', 42, 0, 100, 0, 0, 0, 0, 0, 'Terrath the Steady to Player');
INSERT INTO `creature_text` VALUES (42466, 1, 0, 'Be swift, $c. The stone dragons will not give up easily.', 12, 0, 100, 1, 0, 21647, 0, 0, 'Terrath the Steady to Player');
INSERT INTO `creature_text` VALUES (42466, 2, 0, 'You\'ve strayed too far from the Stonemother\'s gaze, dragon.  It is time that you be put in your place.', 14, 0, 100, 1, 0, 21649, 0, 0, 'Terrath the Steady to Player');
INSERT INTO `creature_text` VALUES (42466, 3, 0, 'Jump from rock to rock to attract the attention of the Broodmother!', 42, 0, 100, 0, 0, 0, 0, 0, 'Terrath the Steady to Player');
INSERT INTO `creature_text` VALUES (42466, 4, 0, 'She submits.  Well done, fleshling.', 14, 0, 100, 1, 0, 21650, 0, 0, 'Terrath the Steady to Aeosera');
INSERT INTO `creature_text` VALUES (42466, 5, 0, 'This champion also faced the Stone Dragons and their Broodmother with no glimpse of fear or hesitation.  I\'ve seen the most hardy of giants falter where the fleshling did not.', 12, 0, 100, 0, 0, 21653, 0, 0, 'Terrath the Steady to Therazane');
INSERT INTO `creature_text` VALUES (42467, 0, 0, 'Not wasted, Stonemother.  It has been very useful.', 12, 0, 100, 1, 0, 21757, 0, 0, 'Diamant the Patient to Player');
INSERT INTO `creature_text` VALUES (42467, 1, 0, 'It stands to reason that it may be here for exactly the reasons it says it is.', 12, 0, 100, 1, 0, 21758, 0, 0, 'Diamant the Patient to Player');
INSERT INTO `creature_text` VALUES (42467, 2, 0, 'You still have much to prove.  But you brought the stone.  That is an excellent start.', 12, 0, 100, 1, 0, 21759, 0, 0, 'Diamant the Patient to Player');
INSERT INTO `creature_text` VALUES (42467, 3, 0, 'The other Stone Lords have already heard my words.', 12, 0, 100, 1, 0, 21760, 0, 0, 'Diamant the Patient to Player');
INSERT INTO `creature_text` VALUES (42467, 4, 0, 'They should show you a degree of trust.', 12, 0, 100, 1, 0, 21761, 0, 0, 'Diamant the Patient to Player');
INSERT INTO `creature_text` VALUES (42467, 5, 0, 'At least enough so that you can prove yourself.', 12, 0, 100, 1, 0, 21762, 0, 0, 'Diamant the Patient to Player');
INSERT INTO `creature_text` VALUES (42468, 0, 0, 'You\'ve come to prove yourself, fleshling?  The Crumbling Depths lie northeast.  The stone you seek is within.', 12, 0, 100, 1, 0, 21767, 0, 0, 'Felsen the Enduring to Player');
INSERT INTO `creature_text` VALUES (42468, 1, 0, 'I\'ve not spoken with the strangers, but I bore witness to their champion treading deep into the Crumbling Depths and returning unscathed.  It is clearly capable.', 12, 0, 100, 1, 0, 21768, 0, 0, 'Felsen the Enduring to Therazane');
INSERT INTO `creature_text` VALUES (42471, 0, 0, 'Hah! Did you mistake me for Diamant, $r? Or perhaps some other whimpering, compliant stone trogg who cares?', 14, 0, 100, 1, 0, 21743, 0, 0, 'Boden the Imposing to Player');
INSERT INTO `creature_text` VALUES (42471, 1, 0, 'If you seek peace, relinquish the World Pillar and leave Deepholm. This is our realm. Your only welcome here shall be found underneath my stone foot.', 14, 0, 100, 1, 0, 21744, 0, 0, 'Boden the Imposing to Player');
INSERT INTO `creature_text` VALUES (42471, 2, 0, 'It\'s not bravery that drives it.  It strode before me like it thought this place its home. It is a fool.  It will surely get itself killed soon enough.', 12, 0, 100, 0, 0, 21749, 0, 0, 'Boden the Imposing to Therazane');
INSERT INTO `creature_text` VALUES (42472, 0, 0, 'I have no love for the intruders, Stonemother, but these fleshlings went well out of their way to cleanse the filth from the Crimson Expanse. ', 12, 0, 100, 1, 0, 21879, 0, 0, 'Gorsik the Tumultuous to Therazane');
INSERT INTO `creature_text` VALUES (42472, 1, 0, 'They are not the Twilight.  I see no reason to oppose their goals.', 12, 0, 100, 1, 0, 21880, 0, 0, 'Gorsik the Tumultuous to Therazane');
INSERT INTO `creature_text` VALUES (42479, 0, 0, 'The line is secured to his ankle.  Pull away to trip him!', 42, 0, 100, 0, 0, 0, 0, 0, 'Servant of Therazane to Player');
INSERT INTO `creature_text` VALUES (42573, 0, 0, 'What have we here, $n?', 12, 0, 100, 6, 0, 0, 0, 0, 'Earthcaller Yevaa to Player');
INSERT INTO `creature_text` VALUES (42573, 1, 0, '$n, you\'ve returned! Is that the middle fragment?', 12, 0, 100, 3, 0, 0, 0, 0, 'Earthcaller Yevaa to Player');
INSERT INTO `creature_text` VALUES (42614, 0, 0, 'The Stone Dragons are here, fleshling.  Take wing!', 14, 0, 100, 1, 0, 21652, 0, 0, 'Terrath the Steady to Zoltrik Drakebane');
INSERT INTO `creature_text` VALUES (42731, 0, 0, 'So it is certain den. Dese elementals were bound from dormant stone only recently.', 12, 0, 100, 1, 0, 21027, 0, 0, 'Earthmender Norsala to Player');
INSERT INTO `creature_text` VALUES (42731, 1, 0, 'Only an elemental lord could bind so many elementals so quickly. It seems we\'re at war with da Stonemother herself...', 12, 0, 100, 1, 0, 21028, 0, 0, 'Earthmender Norsala to Player');
INSERT INTO `creature_text` VALUES (42747, 0, 0, 'Everyone started dropping like flies... everyone who ate the rations.', 12, 0, 100, 0, 0, 0, 0, 0, 'Slain Crew Member\'s Spirit to Player');
INSERT INTO `creature_text` VALUES (42747, 1, 0, 'They poisoned our supplies... we had cultists onboard all along...', 12, 0, 100, 0, 0, 0, 0, 0, 'Slain Crew Member\'s Spirit to Player');
INSERT INTO `creature_text` VALUES (42747, 2, 0, 'I heard them praying to their dark gods as everything went black... the Twilight\'s Hammer did this!', 12, 0, 100, 0, 0, 0, 0, 0, 'Slain Crew Member\'s Spirit to Player');
INSERT INTO `creature_text` VALUES (42757, 0, 0, 'Twilight scum!  You did this to us!', 12, 0, 100, 0, 0, 0, 0, 0, 'Slain Crew Member\'s Spirit to Player');
INSERT INTO `creature_text` VALUES (42788, 0, 0, 'I\'d started to think we\'d been left out here. Thanks for the hand!', 12, 0, 100, 396, 0, 0, 0, 0, 'Hargoth Dimblaze');
INSERT INTO `creature_text` VALUES (42792, 0, 0, 'Nice job! Let\'s finish him off.', 12, 0, 100, 396, 0, 0, 0, 0, 'Earthguard Sentinel');
INSERT INTO `creature_text` VALUES (42801, 0, 0, 'Ok, ok, I give up!  I\'ll tell you everything!', 12, 0, 100, 0, 0, 23551, 0, 0, 'Mor\'norokk the Hateful');
INSERT INTO `creature_text` VALUES (42801, 1, 0, 'Gotcha!', 12, 0, 100, 0, 0, 20989, 0, 0, 'Mor\'norokk the Hateful to Player');
INSERT INTO `creature_text` VALUES (42801, 2, 0, 'I hope you can fly, pipsqueak!', 12, 0, 100, 0, 0, 20990, 0, 0, 'Mor\'norokk the Hateful to Player');
INSERT INTO `creature_text` VALUES (42801, 3, 0, 'You!  Let me go now!  Wait... no!  Don\'t let go!', 14, 0, 100, 0, 0, 20991, 0, 0, 'Mor\'norokk the Hateful to Stormcaller Mylra');
INSERT INTO `creature_text` VALUES (42823, 0, 0, 'You face the glorious wrath of the Twilight!', 12, 0, 100, 0, 0, 0, 0, 0, 'Twilight Priestess to Player');
INSERT INTO `creature_text` VALUES (42892, 0, 0, 'Over here, you ugly whale!', 12, 0, 100, 0, 0, 21001, 0, 0, 'Stormcaller Mylra to Stormbeak');
INSERT INTO `creature_text` VALUES (42892, 1, 0, 'Meet me back up top.  This two-headed freak has a few questions to answer!', 12, 0, 100, 0, 0, 21002, 0, 0, 'Stormcaller Mylra to Stormbeak');
INSERT INTO `creature_text` VALUES (42900, 0, 0, 'You hear a loud cracking noise as the rockling tries to smile.', 16, 0, 100, 0, 0, 0, 0, 0, 'Quartz Rockling to Rockling Chain Bunny');
INSERT INTO `creature_text` VALUES (42900, 1, 0, 'The rockling looks confused for a moment before running off.', 16, 0, 100, 0, 0, 0, 0, 0, 'Quartz Rockling to Rockling Chain Bunny');
INSERT INTO `creature_text` VALUES (42900, 2, 0, 'The rockling looks up at you questioningly.', 16, 0, 100, 0, 0, 0, 0, 0, 'Quartz Rockling to Rockling Chain Bunny');
INSERT INTO `creature_text` VALUES (42900, 3, 0, 'The rockling thuds happily upon the ground.', 16, 0, 100, 0, 0, 0, 0, 0, 'Quartz Rockling to Rockling Chain Bunny');
INSERT INTO `creature_text` VALUES (42914, 0, 0, 'Therazane\'s finally gathered the nerve to challenge us, has she?', 12, 0, 100, 0, 0, 20933, 0, 0, 'High Priestess Lorthuna to Player');
INSERT INTO `creature_text` VALUES (42914, 1, 0, 'You do realize there\'s no hope, don\'t you?  Deathwing has already risen.  Your world is doomed.', 12, 0, 100, 0, 0, 20935, 0, 0, 'High Priestess Lorthuna');
INSERT INTO `creature_text` VALUES (42914, 2, 0, 'I\'m not that easy, hon.  Perhaps another time.', 12, 0, 100, 0, 0, 20937, 0, 0, 'High Priestess Lorthuna');
INSERT INTO `creature_text` VALUES (42916, 0, 0, 'Therazane\'s doom is assured!', 12, 0, 100, 0, 0, 0, 0, 0, 'Twilight Soulreaper to Gorsik the Tumultuous');
INSERT INTO `creature_text` VALUES (42917, 0, 0, 'There is no hope for enemies of the Twilight!', 12, 0, 100, 0, 0, 0, 0, 0, 'Twilight Duskwarden to Player');
INSERT INTO `creature_text` VALUES (42918, 0, 0, 'Ya handle yaself on da ground.  How about in da skies?', 12, 0, 100, 0, 0, 21615, 0, 0, 'Zoltrik Drakebane');
INSERT INTO `creature_text` VALUES (42923, 0, 0, 'Deathwing will have his way with your Stonemother.', 12, 0, 100, 0, 0, 21511, 0, 0, 'Boldrich Stonerender to Player');
INSERT INTO `creature_text` VALUES (42923, 1, 0, 'Agh...', 12, 0, 100, 0, 0, 21517, 0, 0, 'Boldrich Stonerender to Player');
INSERT INTO `creature_text` VALUES (42964, 0, 0, 'Wait!  You can\'t do this!  You people must have a code of honor or something!', 14, 0, 100, 0, 0, 20992, 0, 0, 'Mor\'norokk the Hateful to Stormbeak');
INSERT INTO `creature_text` VALUES (42964, 1, 0, 'Stop it, you tiny, angry woman!  I\'ll tell you everything!  What do you want to know?', 14, 0, 100, 0, 0, 20993, 0, 0, 'Mor\'norokk the Hateful to Stormbeak');
INSERT INTO `creature_text` VALUES (42964, 2, 0, 'It was delivered to the forgemaster at Deathwing\'s Fall.  I swear... that\'s all I know!', 14, 0, 100, 0, 0, 20994, 0, 0, 'Mor\'norokk the Hateful to Stormbeak');
INSERT INTO `creature_text` VALUES (42964, 3, 0, 'Happy now?  Pull me up, you vicious little dwarf!', 14, 0, 100, 0, 0, 20995, 0, 0, 'Mor\'norokk the Hateful to Stormbeak');
INSERT INTO `creature_text` VALUES (42964, 4, 0, 'What? What have you done?  No!  Do you have any idea what the Twilight Council will do to me when they find out I\'ve talked!?  I would sooner die!', 14, 0, 100, 0, 0, 20996, 0, 0, 'Mor\'norokk the Hateful to Stormbeak');
INSERT INTO `creature_text` VALUES (42965, 0, 0, 'All right, sunshine.  Time to spill your guts before I spill \'em for ya!', 12, 0, 100, 0, 0, 21003, 0, 0, 'Stormcaller Mylra to Stormbeak');
INSERT INTO `creature_text` VALUES (42965, 1, 0, 'I\'m a daughter of the Wildhammer Clan and a shaman of the Earthen Ring.  My code compels me to honor my ancestors and the elements.', 12, 0, 100, 0, 0, 21004, 0, 0, 'Stormcaller Mylra to Stormbeak');
INSERT INTO `creature_text` VALUES (42965, 2, 0, 'Doesn\'t say a damn thing about dog-ugly ogres.', 12, 0, 100, 0, 0, 21005, 0, 0, 'Stormcaller Mylra to Stormbeak');
INSERT INTO `creature_text` VALUES (42965, 3, 0, 'You can start by telling us where the World Pillar fragment is.  My gryphon\'s starting to look a bit tired from hauling you around.', 12, 0, 100, 0, 0, 21006, 0, 0, 'Stormcaller Mylra to Stormbeak');
INSERT INTO `creature_text` VALUES (42965, 4, 0, 'You were never in any danger, sweetheart.  My friends down below would\'ve caught you.  You\'re worth too much to us alive!', 12, 0, 100, 0, 0, 21007, 0, 0, 'Stormcaller Mylra to Stormbeak');
INSERT INTO `creature_text` VALUES (42965, 5, 0, 'Interesting.  I\'d never heard of the Twilight Council until now.', 12, 0, 100, 0, 0, 21008, 0, 0, 'Stormcaller Mylra to Stormbeak');
INSERT INTO `creature_text` VALUES (43026, 0, 0, 'Those who are not of the earth must be destroyed!', 12, 0, 100, 0, 0, 0, 0, 0, 'Deepstone Elemental to Player');
INSERT INTO `creature_text` VALUES (43026, 1, 0, 'You do not belong in the realm of earth!', 12, 0, 100, 0, 0, 0, 0, 0, 'Deepstone Elemental to Player');
INSERT INTO `creature_text` VALUES (43032, 0, 0, 'The cannoneer\'s hands are black with gunpowder.', 42, 0, 100, 0, 0, 0, 0, 0, 'Slain Cannoneer to Player');
INSERT INTO `creature_text` VALUES (43039, 0, 0, 'By the elements, this one is even bigger than the last!', 14, 0, 100, 0, 0, 0, 0, 0, 'Twilight Duskwarden to Agitated Tunneler');
INSERT INTO `creature_text` VALUES (43039, 1, 0, 'We are under attack! To arms!', 14, 0, 100, 0, 0, 0, 0, 0, 'Twilight Duskwarden to Agitated Tunneler');
INSERT INTO `creature_text` VALUES (43044, 0, 0, 'The shell\'s markings read: \"STORMWIND ROYAL INDUSTRIES\"', 42, 0, 100, 0, 0, 0, 0, 0, 'Unexploded Artillery Shell to Player');
INSERT INTO `creature_text` VALUES (43047, 0, 0, 'I am ready to meet with your so-called Earthcaller.', 12, 0, 100, 1, 0, 0, 0, 0, 'Flint Oremantle to Player');
INSERT INTO `creature_text` VALUES (43047, 1, 0, 'The war between the Earthen and the stone troggs has raged for time beyond measure. But now in the chaos, the Stonefather, prisoner. They are threatening to kill him if we do not surrender.', 12, 0, 100, 1, 0, 0, 0, 0, 'Flint Oremantle to Flint Oremantle');
INSERT INTO `creature_text` VALUES (43047, 2, 0, 'Earthcaller, my father, Stonefather Oremantle, has been taken prisoner by the stone troggs of Needlerock Spire.', 12, 0, 100, 2, 0, 0, 0, 0, 'Flint Oremantle to Player');
INSERT INTO `creature_text` VALUES (43047, 3, 0, 'I demand that you send aid to Stonehearth. Help us with our war and to free Stonefather Oremantle.', 12, 0, 100, 25, 0, 0, 0, 0, 'Flint Oremantle to Player');
INSERT INTO `creature_text` VALUES (43047, 4, 0, 'This one that assisted me should be adequate. Have them report to our force commander, Crag Rockcrusher at Stonehearth.', 12, 0, 100, 1, 0, 0, 0, 0, 'Flint Oremantle to Player');
INSERT INTO `creature_text` VALUES (43048, 0, 0, 'The Captain\'s Log lies nearby.', 42, 0, 100, 0, 0, 0, 0, 0, 'Captain Skullshatter to Player');
INSERT INTO `creature_text` VALUES (43071, 0, 0, 'Indeed!', 12, 0, 100, 66, 0, 0, 0, 0, 'Crag Rockcrusher to Player');
INSERT INTO `creature_text` VALUES (43082, 0, 0, 'Thanks... this\'ll give us a couple more hours of not running into the rocks.  No guarantees after that.', 12, 0, 100, 0, 0, 0, 0, 0, 'First Mate Moody to Player');
INSERT INTO `creature_text` VALUES (43116, 0, 0, 'You hear the sound of shuffling stones behind you.', 16, 0, 100, 0, 0, 0, 0, 0, 'Pebble to Player');
INSERT INTO `creature_text` VALUES (43116, 1, 0, 'Pebble sees the crystals ahead and excitedly rushes towards them!', 16, 0, 100, 0, 0, 0, 0, 0, 'Pebble to Generic Controller Bunny (CSA)');
INSERT INTO `creature_text` VALUES (43116, 2, 0, 'Pebble bobbles around appreciatively.', 16, 0, 100, 0, 0, 0, 0, 0, 'Pebble to Generic Controller Bunny (CSA)');
INSERT INTO `creature_text` VALUES (43116, 3, 0, 'Pebble races in circles, clearly happy.', 16, 0, 100, 0, 0, 0, 0, 0, 'Pebble to Player');
INSERT INTO `creature_text` VALUES (43138, 0, 0, 'For Stonehearth!', 12, 0, 100, 0, 0, 0, 0, 0, 'Stonehearth Defender to Stonefather\'s Banner');
INSERT INTO `creature_text` VALUES (43138, 1, 0, 'For the Stonefather!', 12, 0, 100, 0, 0, 0, 0, 0, 'Stonehearth Defender to Stonefather\'s Banner');
INSERT INTO `creature_text` VALUES (43160, 0, 0, 'Do you want to live forever?', 14, 0, 100, 0, 0, 1285, 0, 0, 'Earthbreaker Dolomite');
INSERT INTO `creature_text` VALUES (43160, 1, 0, 'We have reinforcements! For the Stonefather!', 14, 0, 100, 0, 0, 0, 0, 0, 'Earthbreaker Dolomite');
INSERT INTO `creature_text` VALUES (43168, 0, 0, 'One of Clay\'s catapult teams is broken down on the front. $n, I need you to get some parts from him and get that catapult back in the fight!', 12, 0, 100, 1, 0, 0, 0, 0, 'Gravel Longslab to Player');
INSERT INTO `creature_text` VALUES (43170, 0, 0, 'Give me the reagents again when these stone troggs are dead.', 12, 0, 100, 0, 0, 0, 0, 0, 'Earthen Geomancer to Player');
INSERT INTO `creature_text` VALUES (43229, 0, 0, 'Thank you, $r.', 12, 0, 100, 2, 0, 0, 0, 0, 'Injured Earthen to Player');
INSERT INTO `creature_text` VALUES (43229, 1, 0, 'Kill a few for me, would you?', 12, 0, 100, 1, 0, 0, 0, 0, 'Injured Earthen to Player');
INSERT INTO `creature_text` VALUES (43229, 2, 0, 'You\'re a strange-looking creature. Thank you.', 12, 0, 100, 25, 0, 0, 0, 0, 'Injured Earthen to Player');
INSERT INTO `creature_text` VALUES (43229, 3, 0, 'Don\'t worry about me. We have to rescue the king!', 12, 0, 100, 5, 0, 0, 0, 0, 'Injured Earthen to Player');
INSERT INTO `creature_text` VALUES (43229, 4, 0, 'What happened?', 12, 0, 100, 6, 0, 0, 0, 0, 'Injured Earthen to Player');
INSERT INTO `creature_text` VALUES (43229, 5, 0, 'I have to report back. Thank you, $c!', 12, 0, 100, 66, 0, 0, 0, 0, 'Injured Earthen to Player');
INSERT INTO `creature_text` VALUES (43229, 6, 0, 'I think I can make it back on my own.', 12, 0, 100, 1, 0, 0, 0, 0, 'Injured Earthen to Player');
INSERT INTO `creature_text` VALUES (43339, 0, 0, 'A Son of Kor has arrived to assist you, but watch out for his boulders!', 41, 0, 100, 0, 0, 0, 0, 0, 'Gorgonite to Player');
INSERT INTO `creature_text` VALUES (43346, 0, 0, 'I thought we\'d killed everybody... Mor\'norokk should be pleased with this!', 12, 0, 100, 0, 0, 0, 0, 0, 'Twilight Saboteur to Player');
INSERT INTO `creature_text` VALUES (43372, 0, 0, 'No no no! Leave Glop alone! My mushrooms! Mine!', 14, 0, 100, 0, 0, 21769, 0, 0, 'Fungalmancer Glop to Earthmender Norsala');
INSERT INTO `creature_text` VALUES (43372, 1, 0, 'Mushrooms grow big and strong. Too big to stomp on! So strong they stomp back!', 14, 0, 100, 0, 0, 21770, 0, 0, 'Fungalmancer Glop to Earthmender Norsala');
INSERT INTO `creature_text` VALUES (43372, 2, 0, 'Mean... rock fiends...', 14, 0, 100, 0, 0, 21773, 0, 0, 'Fungalmancer Glop to Player');
INSERT INTO `creature_text` VALUES (43456, 0, 0, 'POUND!', 14, 0, 100, 0, 0, 0, 0, 0, 'Troggzor the Earthinator');
INSERT INTO `creature_text` VALUES (43456, 1, 0, 'DESTROY!', 14, 0, 100, 51, 0, 0, 0, 0, 'Troggzor the Earthinator');
INSERT INTO `creature_text` VALUES (43503, 0, 0, 'Let\'s finish with this quickly.', 12, 0, 100, 0, 0, 21030, 0, 0, 'Earthmender Norsala to Player');
INSERT INTO `creature_text` VALUES (43503, 1, 0, 'You\'ve caused enough damage, Glop. The earth welcomes you no longer.', 14, 0, 100, 0, 0, 21031, 0, 0, 'Earthmender Norsala to Player');
INSERT INTO `creature_text` VALUES (43503, 2, 0, 'After him, quickly!', 14, 0, 100, 0, 0, 21032, 0, 0, 'Earthmender Norsala to Fungalmancer Glop');
INSERT INTO `creature_text` VALUES (43635, 0, 0, 'Alright, here we go!', 14, 0, 100, 25, 0, 0, 0, 0, 'Catapult Driver to Earthen Catapult');
INSERT INTO `creature_text` VALUES (43635, 1, 0, 'Stone troggs incoming!', 14, 0, 100, 5, 0, 0, 0, 0, 'Catapult Driver to Player');
INSERT INTO `creature_text` VALUES (43635, 2, 0, 'You stone troggs have no idea of what\'s coming!', 14, 0, 100, 1, 0, 0, 0, 0, 'Catapult Driver to Earthen Catapult');
INSERT INTO `creature_text` VALUES (43635, 3, 0, 'Damn, stone troggs! Over there!', 14, 0, 100, 25, 0, 0, 0, 0, 'Catapult Driver to Player');
INSERT INTO `creature_text` VALUES (43635, 4, 0, 'How\'d that feel, you stone trogg scum?!', 14, 0, 100, 14, 0, 0, 0, 0, 'Catapult Driver to Earthen Catapult');
INSERT INTO `creature_text` VALUES (43635, 5, 0, 'Oh yeah! Tremble at the might of our catapults!', 14, 0, 100, 15, 0, 0, 0, 0, 'Catapult Driver to Earthen Catapult');
INSERT INTO `creature_text` VALUES (43641, 0, 0, 'The Stone Dragon Broodmother bows her head in surrender.', 41, 0, 100, 0, 0, 0, 0, 0, 'Aeosera');
INSERT INTO `creature_text` VALUES (43792, 0, 0, 'You\'ve proven true to your word, Earthmender.  For that, we will spare you our wrath.  ', 12, 0, 100, 1, 0, 21078, 0, 0, 'Therazane to Player');
INSERT INTO `creature_text` VALUES (43792, 1, 0, 'Once your wounded are gathered, you can thank me by leaving.', 12, 0, 100, 1, 0, 21079, 0, 0, 'Therazane to Player');
INSERT INTO `creature_text` VALUES (43802, 0, 0, 'Is this the one you are wasting your time with, Diamant?', 12, 0, 100, 1, 0, 21087, 0, 0, 'Therazane to Player');
INSERT INTO `creature_text` VALUES (43802, 1, 0, 'Then you take responsibility for its actions, Stone Lord.  You will be the one answering to me when it shows its true intent.', 12, 0, 100, 1, 0, 21088, 0, 0, 'Therazane to Player');
INSERT INTO `creature_text` VALUES (43802, 2, 0, 'And you fleshling, consider for a moment that you are entirely surrounded by rock in this place.  The very earth you walk on bends to my will.', 12, 0, 100, 1, 0, 21089, 0, 0, 'Therazane to Player');
INSERT INTO `creature_text` VALUES (43802, 3, 0, 'If you betray us, there will be no escape for you.  I will crush your body so slowly that you shall hear every single one of your bones break.', 12, 0, 100, 1, 0, 21090, 0, 0, 'Therazane to Player');
INSERT INTO `creature_text` VALUES (43802, 4, 0, 'I\'m glad we had this talk.', 12, 0, 100, 1, 0, 21091, 0, 0, 'Therazane to Player');
INSERT INTO `creature_text` VALUES (43807, 0, 0, 'You know where I stand, Stonemother.', 12, 0, 100, 0, 0, 21764, 0, 0, 'Diamant the Patient to Therazane');
INSERT INTO `creature_text` VALUES (43807, 1, 0, 'This champion has proved intent and capable.', 12, 0, 100, 0, 0, 21765, 0, 0, 'Diamant the Patient to Therazane');
INSERT INTO `creature_text` VALUES (43807, 2, 0, 'I see no reason to deny our trust.', 12, 0, 100, 0, 0, 21766, 0, 0, 'Diamant the Patient to Therazane');
INSERT INTO `creature_text` VALUES (43835, 0, 0, 'Stop her!  We can still complete da binding!', 14, 0, 100, 0, 0, 21115, 0, 0, 'Earthcaller Torunscar to Player');
INSERT INTO `creature_text` VALUES (43835, 1, 0, 'Thank da elements...', 14, 0, 100, 0, 0, 21116, 0, 0, 'Earthcaller Torunscar to Player');
INSERT INTO `creature_text` VALUES (43835, 2, 0, 'It is done!  We are finished.', 14, 0, 100, 0, 0, 21113, 0, 0, 'Earthcaller Torunscar to Player');
INSERT INTO `creature_text` VALUES (43835, 3, 0, 'With the pillar restored, dis realm is safe.  We must now attend to the wound on the surface.', 14, 0, 100, 0, 0, 21114, 0, 0, 'Earthcaller Torunscar to Player');
INSERT INTO `creature_text` VALUES (43836, 0, 0, 'Got it. I\'ll head there right away.', 12, 0, 100, 0, 0, 0, 0, 0, 'Windspeaker Lorvarius to Player');
INSERT INTO `creature_text` VALUES (43837, 0, 0, 'Your tale ends here, shaman. ', 12, 0, 100, 0, 0, 20938, 0, 0, 'High Priestess Lorthuna to Player');
INSERT INTO `creature_text` VALUES (43837, 1, 0, 'Lorthuna is preparing a powerful spell - get behind cover!', 41, 0, 100, 0, 0, 20941, 0, 0, 'High Priestess Lorthuna');
INSERT INTO `creature_text` VALUES (43837, 2, 0, 'Hah...', 12, 0, 100, 0, 0, 20940, 0, 0, 'High Priestess Lorthuna to Player');
INSERT INTO `creature_text` VALUES (43897, 0, 0, '$n, you\'re going to want to fly over these giants and stone troggs. Don\'t try to ride through them.', 12, 0, 100, 1, 0, 0, 0, 0, 'Pyrium Lodestone to Player');
INSERT INTO `creature_text` VALUES (43898, 0, 0, '$n? It\'s about time you got here. What kept you? My father\'s life is in jeopardy!', 12, 0, 100, 396, 0, 0, 0, 0, 'Flint Oremantle to Player');
INSERT INTO `creature_text` VALUES (43901, 0, 0, 'We came here to seal da rift Deathwing tore into Azeroth. We need the World Pillar to repair da hole on dis side.', 12, 0, 100, 1, 0, 21117, 0, 0, 'Earthcaller Torunscar to Player');
INSERT INTO `creature_text` VALUES (43901, 1, 0, 'We\'ll leave. We\'ve no desire to stay where we\'re not welcome.', 12, 0, 100, 1, 0, 21118, 0, 0, 'Earthcaller Torunscar to Player');
INSERT INTO `creature_text` VALUES (43901, 2, 0, 'We\'ll be doin\' exactly that then. Ya have our tanks, Stonemother.', 12, 0, 100, 1, 0, 21119, 0, 0, 'Earthcaller Torunscar');
INSERT INTO `creature_text` VALUES (43984, 0, 0, 'Reactivated.', 12, 0, 100, 0, 0, 0, 0, 0, 'Reactivated War Construct to Player');
INSERT INTO `creature_text` VALUES (43984, 1, 0, 'Destroy.', 12, 0, 100, 0, 0, 0, 0, 0, 'Reactivated War Construct to Player');
INSERT INTO `creature_text` VALUES (43984, 2, 0, 'Kill nearest mystic.', 12, 0, 100, 0, 0, 0, 0, 0, 'Reactivated War Construct to Player');
INSERT INTO `creature_text` VALUES (44135, 0, 0, 'Ya need ta be more swift den that!', 14, 0, 100, 0, 0, 21616, 0, 0, 'Zoltrik Drakebane to Zoltrik\'s Pyremaw');
INSERT INTO `creature_text` VALUES (44135, 1, 0, 'Me tink I\'ve had my fill of ya, mon.', 14, 0, 100, 0, 0, 21618, 0, 0, 'Zoltrik Drakebane to Zoltrik\'s Pyremaw');
INSERT INTO `creature_text` VALUES (44137, 0, 0, 'If you can\'t fight Zoltrik at range, use your Jump Drakes! ability:\n\n|TInterface\\Icons\\ability_vehicle_launchplayer.blp:64|t', 42, 0, 100, 0, 0, 21616, 0, 0, 'Crystalspire Stone Drake to Player');
INSERT INTO `creature_text` VALUES (44143, 0, 0, 'Don\'t forget to grab a war guardian, $n.', 12, 0, 100, 1, 0, 0, 0, 0, 'Slate Quicksand to Player');
INSERT INTO `creature_text` VALUES (44148, 0, 0, 'You hear rustling of heavy wings in the distance.  The matriarch approaches!', 42, 0, 100, 0, 0, 0, 0, 0, 'Stonescale Matriarch to Player');
INSERT INTO `creature_text` VALUES (44148, 1, 0, 'The Stonescale Matriarch is snared!  Avoid her attacks by staying on the move!', 41, 0, 100, 0, 0, 0, 0, 0, 'Stonescale Matriarch to Player');
INSERT INTO `creature_text` VALUES (44152, 0, 0, '$n! You\'re a sight for sore eyes. Get over here and give me a hand, would you?', 14, 0, 100, 0, 0, 0, 0, 0, 'Flint Oremantle to Player');
INSERT INTO `creature_text` VALUES (44152, 1, 0, 'Father! Let me get you free of those chains.', 14, 0, 100, 0, 0, 0, 0, 0, 'Flint Oremantle');
INSERT INTO `creature_text` VALUES (44152, 2, 0, 'This hero represents the Earthen Ring. They need the middle fragment.', 14, 0, 100, 37, 0, 13092, 0, 0, 'Flint Oremantle');
INSERT INTO `creature_text` VALUES (44152, 3, 0, 'Sorry about my earlier rudeness. I was worried about my father. Friends?', 14, 0, 100, 2, 0, 0, 0, 0, 'Flint Oremantle to Flint Oremantle');
INSERT INTO `creature_text` VALUES (44153, 0, 0, 'You\'ve saved both myself and my son.', 14, 0, 100, 1, 0, 0, 0, 0, 'Stonefather Oremantle to Flint Oremantle');
INSERT INTO `creature_text` VALUES (44153, 1, 0, 'You have my gratitude and that of my people.', 14, 0, 100, 2, 0, 0, 0, 0, 'Stonefather Oremantle to Flint Oremantle');
INSERT INTO `creature_text` VALUES (44153, 2, 0, 'Return to Pyrium Lodestone with the good news. We\'ll see you back at Stonehearth shortly.', 14, 0, 100, 66, 0, 0, 0, 0, 'Stonefather Oremantle to Flint Oremantle');
INSERT INTO `creature_text` VALUES (44153, 3, 0, 'I\'ll see to it that you get the middle fragment of the World Pillar.', 14, 0, 100, 1, 0, 0, 0, 0, 'Stonefather Oremantle to Flint Oremantle');
INSERT INTO `creature_text` VALUES (44204, 0, 0, 'All hail, $n! Huzzah!', 14, 0, 100, 2, 0, 0, 0, 0, 'Stonefather Oremantle to Player');
INSERT INTO `creature_text` VALUES (44289, 0, 0, 'Abyssion is approaching.  Get ready!', 42, 0, 100, 0, 0, 0, 0, 0, 'Abyssion to Player');
INSERT INTO `creature_text` VALUES (44289, 1, 0, 'Abyssion is snared!  Stay on the move to avoid his attacks.', 41, 0, 100, 0, 0, 0, 0, 0, 'Abyssion to Player');
INSERT INTO `creature_text` VALUES (44289, 2, 0, 'Abyssion is casting a powerful Shadow Nova, seek cover!', 41, 0, 100, 0, 0, 0, 0, 0, 'Abyssion');
INSERT INTO `creature_text` VALUES (44289, 3, 0, 'The First World Pillar Fragment drops from Abyssion\'s lifeless corpse!', 41, 0, 100, 0, 0, 0, 0, 0, 'Abyssion to Player');
INSERT INTO `creature_text` VALUES (44352, 0, 0, 'Thanks for the help. That one was far tougher for some reason.', 12, 0, 100, 396, 0, 0, 0, 0, 'Tawn Winterbluff');
INSERT INTO `creature_text` VALUES (44353, 0, 0, 'By the elements... that one... just wouldn\'t die...', 12, 0, 100, 396, 0, 0, 0, 0, 'Stormcaller Mylra');
INSERT INTO `creature_text` VALUES (44372, 0, 0, 'Do me know you? No matter. Me crush your bones!', 14, 0, 100, 0, 0, 0, 0, 0, 'Avalanchion to Player');
INSERT INTO `creature_text` VALUES (44372, 1, 0, 'NOOOOOOOOOoooooo....... not again!', 14, 0, 100, 0, 0, 0, 0, 0, 'Avalanchion to Player');
INSERT INTO `creature_text` VALUES (44631, 0, 0, 'That encampment has been begging for our attention. Let\'s get this done.', 12, 0, 100, 0, 0, 0, 0, 0, 'Tharm Wildfire to Player');
INSERT INTO `creature_text` VALUES (44633, 0, 0, 'I\'m glad to hear we\'re fighting along side the Stonemother herself this time.', 12, 0, 100, 0, 0, 0, 0, 0, 'Stormcaller Jalara to Player');
INSERT INTO `creature_text` VALUES (44647, 0, 0, 'Elements be with us.', 12, 0, 100, 0, 0, 0, 0, 0, 'Mariahn the Soulcleanser to Player');
INSERT INTO `creature_text` VALUES (44666, 0, 0, 'Thank you, Stonemother.  It was an honor to have...', 12, 0, 100, 0, 0, 21038, 0, 0, 'Earthmender Norsala to Player');
INSERT INTO `creature_text` VALUES (44673, 0, 0, 'The cult is upon us! We need help in the Respite Chambers!', 14, 0, 100, 0, 0, 0, 0, 0, 'Stormcaller Mylra to Player');
INSERT INTO `creature_text` VALUES (44768, 0, 0, 'Thank you... I wish I could help you find the forgemaster.', 12, 0, 100, 0, 0, 0, 0, 0, 'Enslaved Miner to Player');
INSERT INTO `creature_text` VALUES (44768, 1, 0, 'The forgemaster\'s a terrible murderer.  Are you sure you want to go looking for him?', 12, 0, 100, 0, 0, 0, 0, 0, 'Enslaved Miner to Player');
INSERT INTO `creature_text` VALUES (44768, 2, 0, 'Ah, yes... I know about the forgemaster!  I just overheard that he\'s not in the camp right now.', 12, 0, 100, 0, 0, 0, 0, 0, 'Enslaved Miner to Player');
INSERT INTO `creature_text` VALUES (44768, 3, 0, 'The forgemaster is meeting with the Twilight leadership.  Trust me, I traded some gossip for a loaf of stale bread!', 12, 0, 100, 0, 0, 0, 0, 0, 'Enslaved Miner to Player');
INSERT INTO `creature_text` VALUES (44768, 4, 0, 'I don\'t know nothin\' bout no forgemaster.  Now tell me where this safe place you came from is!', 12, 0, 100, 0, 0, 0, 0, 0, 'Enslaved Miner to Player');
INSERT INTO `creature_text` VALUES (44768, 5, 0, 'I heard the forgemaster keeps notes about his meetings  in a book at the tower overlooking the camp.', 12, 0, 100, 0, 0, 0, 0, 0, 'Enslaved Miner to Player');
INSERT INTO `creature_text` VALUES (44835, 0, 0, 'You will be destroyed!', 14, 0, 100, 0, 0, 0, 0, 0, 'Haethen Kaul to Player');
INSERT INTO `creature_text` VALUES (44835, 1, 0, 'What?! You\'re immune to my air magic?', 14, 0, 100, 5, 0, 0, 0, 0, 'Haethen Kaul to Player');
INSERT INTO `creature_text` VALUES (44835, 2, 0, 'My firebolt is useless! No matter you smug little insect. I will still destroy you!', 14, 0, 100, 14, 0, 0, 0, 0, 'Haethen Kaul to Player');
INSERT INTO `creature_text` VALUES (44835, 3, 0, 'You\'re immune to my water spell! How can this be?', 14, 0, 100, 15, 0, 0, 0, 0, 'Haethen Kaul to Player');
INSERT INTO `creature_text` VALUES (44839, 0, 0, 'The Twilight\'s Hammer has unearthed a titan Waygate and it appears that they\'re attempting to activate it.', 42, 0, 100, 0, 0, 1285, 0, 0, 'Fly Over Kill Credit to Player');
INSERT INTO `creature_text` VALUES (44879, 0, 0, 'Here she comes, boss!', 14, 0, 100, 0, 0, 0, 0, 0, 'Ogre Bodyguard to Player');
INSERT INTO `creature_text` VALUES (44881, 0, 0, 'Forgemaster!  I\'ve been informed the enemy has disrupted your operations.  Where is the fragment?', 14, 0, 100, 0, 0, 21623, 0, 0, 'High Priestess Azil to Player');
INSERT INTO `creature_text` VALUES (44881, 1, 0, 'Well done.  Meet me inside the Stonecore for further instructions.', 12, 0, 100, 0, 0, 21624, 0, 0, 'High Priestess Azil to Player');
INSERT INTO `creature_text` VALUES (44881, 2, 0, 'Let\'s just say we have friends in high places, forgemaster.', 12, 0, 100, 397, 0, 21625, 0, 0, 'High Priestess Azil to Player');
INSERT INTO `creature_text` VALUES (44936, 0, 0, 'You no take crate!', 14, 0, 100, 0, 0, 0, 0, 0, 'Murkstone Trogg to Player');
INSERT INTO `creature_text` VALUES (44956, 0, 0, 'He\'s coming.  Get ready, lads!', 14, 0, 100, 0, 0, 0, 0, 0, 'Stormcaller Mylra to Player');
INSERT INTO `creature_text` VALUES (45027, 0, 0, 'You made it!  Good!', 12, 0, 100, 4, 0, 22610, 0, 0, 'Aggra to Player');
INSERT INTO `creature_text` VALUES (45027, 1, 0, 'This is the Temple of Earth.  It\'s the most important structure in Deepholm.', 12, 0, 100, 0, 0, 22611, 0, 0, 'Aggra to Player');
INSERT INTO `creature_text` VALUES (45027, 2, 0, 'There was once a Titan-made stone inside.  It was known as the World Pillar.  The name was literal.  It bore the weight of all the elemental and magnetic forces in Deepholm.', 12, 0, 100, 0, 0, 22612, 0, 0, 'Aggra to Player');
INSERT INTO `creature_text` VALUES (45027, 3, 0, 'It was shattered when Deathwing broke through the dome above and created the rift between the two worlds.  All of Deepholm is going to collapse into Azeroth if we do not put the World Pillar back together.', 12, 0, 100, 0, 0, 22613, 0, 0, 'Aggra to Player');
INSERT INTO `creature_text` VALUES (45027, 4, 0, 'The first fragment was going to be delivered to us by a Horde gunship known as the Storm\'s Fury.  It never made it here.', 12, 0, 100, 0, 0, 22614, 0, 0, 'Aggra to Player');
INSERT INTO `creature_text` VALUES (45027, 5, 0, 'We\'ve taken control of the temple and we\'re containing as much of the damage as we can.  Go inside and speak to Maruut; he\'s in charge.', 12, 0, 100, 0, 0, 22615, 0, 0, 'Aggra to Player');
INSERT INTO `creature_text` VALUES (45027, 6, 0, 'We are all counting on you.', 12, 0, 100, 0, 0, 22616, 0, 0, 'Aggra to Player');
INSERT INTO `creature_text` VALUES (45028, 0, 0, 'I hope you have a strong stomach.  Travelling through the breach is not for the weak.', 12, 0, 100, 0, 0, 22606, 0, 0, 'Aggra to Player');
INSERT INTO `creature_text` VALUES (45028, 1, 0, 'We\'re at the center of the Maelstrom, the rift between Deepholm and Azeroth.  Watch as it threatens to tear your world apart!', 12, 0, 100, 0, 0, 22607, 0, 0, 'Aggra to Player');
INSERT INTO `creature_text` VALUES (45028, 2, 0, 'Let us hope Thrall and the others can contain it while you\'re inside.', 12, 0, 100, 0, 0, 22608, 0, 0, 'Aggra to Player');
INSERT INTO `creature_text` VALUES (45028, 3, 0, 'This is it!  Brace yourself!', 14, 0, 100, 0, 0, 22609, 0, 0, 'Aggra to Player');
INSERT INTO `creature_text` VALUES (45034, 0, 0, 'I\'m on my way.', 12, 0, 100, 0, 0, 0, 0, 0, 'Yuldris Smolderfury to Player');
INSERT INTO `creature_text` VALUES (45043, 0, 0, 'Let me know when you\'re ready, $n.', 12, 0, 100, 25, 0, 4614, 0, 0, 'Peak Grindstone to Player');
INSERT INTO `creature_text` VALUES (45099, 0, 0, 'Deepholm? I hate this place. Wait... you\'re not Initiate Goldmine!', 14, 0, 100, 51, 0, 0, 0, 0, 'Magmatooth to Player');
INSERT INTO `creature_text` VALUES (45099, 1, 0, 'I know what you\'re after. I can sense it!', 14, 0, 100, 34, 0, 0, 0, 0, 'Magmatooth to Player');
INSERT INTO `creature_text` VALUES (45099, 2, 0, 'This is not in my contract! Die, $r!', 14, 0, 100, 0, 0, 0, 0, 0, 'Magmatooth to Player');
INSERT INTO `creature_text` VALUES (45099, 3, 0, 'NOOOOOO! You tell Goldmine he\'s a dead goblin! Our contract is over!', 14, 0, 100, 0, 0, 0, 0, 0, 'Magmatooth to Player');
INSERT INTO `creature_text` VALUES (47195, 0, 0, '$C, over here.', 12, 0, 100, 70, 0, 0, 0, 0, 'Slate Quicksand to Player');
INSERT INTO `creature_text` VALUES (47195, 1, 0, 'Just let me know when you\'re ready for the ritual, $n.', 12, 0, 100, 1, 0, 23543, 0, 0, 'Slate Quicksand to Player');
INSERT INTO `creature_text` VALUES (47197, 0, 0, 'Huzzah!', 14, 0, 100, 4, 0, 0, 0, 0, 'Flint Oremantle to Player');
INSERT INTO `creature_text` VALUES (51434, 0, 0, 'The fragment is now in the dragon\'s custody, ma\'am.', 12, 0, 100, 2, 0, 20886, 0, 0, 'Millhouse Manastorm to Player');
INSERT INTO `creature_text` VALUES (51434, 1, 0, 'Of course, ma\'am.  At once!', 12, 0, 100, 66, 0, 20888, 0, 0, 'Millhouse Manastorm to Player');
INSERT INTO `creature_text` VALUES (51434, 2, 0, 'If you don\'t mind me asking.  How did we manage to recover the fragment?  How did we infiltrate our enemies\' forces without their knowledge?', 12, 0, 100, 6, 0, 20887, 0, 0, 'Millhouse Manastorm to Player');
INSERT INTO `creature_text` VALUES (23789, 0, 0, 'Pitiful mortal, Onyxia answers to no one!', 14, 0, 100, 0, 0, 0, 22285, 0, 'Smolderwing');
INSERT INTO `creature_text` VALUES (23789, 1, 0, 'Your pathetic challenge has not gone unnoticed. I shall enjoy toying with you before you die.', 14, 0, 100, 0, 0, 0, 22286, 0, 'Smolderwing');
INSERT INTO `creature_text` VALUES (9116, 0, 0, 'May this evil aura be banished from this artifact!', 12, 0, 100, 0, 0, 0, 8366, 0, 'Eridan Bluewind');
INSERT INTO `creature_text` VALUES (9116, 1, 0, 'I can feel the presence of evil leaving...', 12, 0, 100, 0, 0, 0, 8367, 0, 'Eridan Bluewind');
INSERT INTO `creature_text` VALUES (39959, 0, 0, 'Armies of the depths, wash over our enemies as a tide of death!', 14, 0, 100, 0, 0, 18890, 50510, 0, 'Lady Naz\'jar to Player');
INSERT INTO `creature_text` VALUES (39959, 1, 0, 'Meddlesome gnats! You think us defeated so easily?', 14, 0, 100, 0, 0, 18891, 43121, 0, 'Lady Naz\'jar to Player');
INSERT INTO `creature_text` VALUES (40586, 0, 0, 'You have interfered with our plans for the last time, mortals!', 14, 0, 100, 0, 0, 18886, 50505, 0, 'Lady Naz\'jar to Player');
INSERT INTO `creature_text` VALUES (40586, 1, 0, 'Take arms, minions! Rise from the icy depths!', 14, 0, 100, 0, 0, 18892, 50513, 0, 'Lady Naz\'jar');
INSERT INTO `creature_text` VALUES (40586, 2, 0, 'Destroy these intruders! Leave them for the great dark beyond!', 14, 0, 100, 0, 0, 18893, 50514, 0, 'Lady Naz\'jar');
INSERT INTO `creature_text` VALUES (40586, 3, 0, 'Ulthok, stop them...', 14, 0, 100, 0, 0, 18889, 50509, 0, 'Lady Naz\'jar to Player');
INSERT INTO `creature_text` VALUES (40765, 0, 0, 'Iilth vwah, uhn\'agth fhssh za.', 14, 0, 100, 0, 0, 18543, 50522, 0, 'Commander Ulthok to Player');
INSERT INTO `creature_text` VALUES (40765, 1, 0, 'Where one falls, many shall take its place...', 15, 0, 100, 0, 0, 18543, 50523, 0, 'Commander Ulthok to Player');
INSERT INTO `creature_text` VALUES (40765, 2, 0, 'Ywaq maq oou.', 14, 0, 100, 0, 0, 18542, 50529, 0, 'Commander Ulthok to Player');
INSERT INTO `creature_text` VALUES (40788, 0, 0, 'A new host must be found.', 14, 0, 100, 0, 0, 18860, 50534, 0, 'Mindbender Ghur\'sha to Erunak Stonespeaker');
INSERT INTO `creature_text` VALUES (40788, 1, 0, 'Is. This. Reality?', 14, 0, 100, 0, 0, 18867, 50543, 0, 'Mindbender Ghur\'sha');
INSERT INTO `creature_text` VALUES (40788, 2, 0, 'They are outside the cycle...', 14, 0, 100, 0, 0, 18863, 50538, 0, 'Mindbender Ghur\'sha to Player');
INSERT INTO `creature_text` VALUES (40792, 0, 0, 'Ahh - my fearless assistants, I assume.', 14, 0, 100, 0, 0, 19469, 44263, 0, 'Neptulon to Player');
INSERT INTO `creature_text` VALUES (40792, 1, 0, 'You may yet regret your presence here.', 14, 0, 100, 0, 0, 19470, 44264, 0, 'Neptulon to Player');
INSERT INTO `creature_text` VALUES (40792, 2, 0, 'As I purify these waters, the servants of filth will surely be stirred. Beware!', 14, 0, 100, 0, 0, 19471, 44265, 0, 'Neptulon to Player');
INSERT INTO `creature_text` VALUES (40792, 3, 0, 'Patience, guests. The waters are nearly cleansed.', 14, 0, 100, 0, 0, 19475, 44269, 0, 'Neptulon to Player');
INSERT INTO `creature_text` VALUES (40792, 4, 0, 'The beast has returned! It must not pollute my waters!', 14, 0, 100, 0, 0, 19476, 44270, 0, 'Neptulon to Player');
INSERT INTO `creature_text` VALUES (40792, 5, 0, 'Augh... Their filthiness stains me!', 14, 0, 100, 0, 0, 19472, 44266, 0, 'Neptulon to Player');
INSERT INTO `creature_text` VALUES (40792, 6, 0, 'My waters are cleansed! Drink in their power!', 14, 0, 100, 0, 0, 19478, 44272, 0, 'Neptulon to Player');
INSERT INTO `creature_text` VALUES (40792, 7, 0, 'Behold the power of pure water!', 14, 0, 100, 0, 0, 19477, 44271, 0, 'Neptulon to Player');
INSERT INTO `creature_text` VALUES (40792, 8, 0, 'Ozumat is vulnerable, strike now!', 41, 0, 100, 0, 0, 19477, 50863, 0, 'Neptulon to Player');
INSERT INTO `creature_text` VALUES (40825, 0, 0, 'I thank you, my friends. Forgive me. I... I have grown too weak to be of any assistance.', 14, 0, 100, 0, 0, 18662, 50533, 0, 'Erunak Stonespeaker');
INSERT INTO `creature_text` VALUES (40825, 1, 0, 'I am... weary. You must carry on without me. I shall take my leave once my strength has returned.', 14, 0, 100, 0, 0, 18664, 50648, 0, 'Erunak Stonespeaker to Player');
INSERT INTO `creature_text` VALUES (35170, 0, 0, '%s goes into a frenzy!', 16, 0, 100, 0, 0, 0, 0, 0, 'Worgen Alpha');
INSERT INTO `creature_text` VALUES (35550, 1, 0, 'Hold the barricades!  Do not give them an inch!', 14, 0, 100, 0, 0, 19709, 0, 0, 'King Genn Greymane');
INSERT INTO `creature_text` VALUES (35907, 0, 0, 'Thank you!  I owe you my life.', 12, 0, 100, 0, 0, 20922, 0, 0, 'Krennan Aranas to King Greymane\'s Horse');
INSERT INTO `creature_text` VALUES (37195, 2, 0, 'Does this toad speak for you, Genn?  Do you come to our dwelling as a friend?  Or do you come as a tyrant?', 14, 0, 100, 0, 0, 19510, 0, 0, 'Lord Darius Crowley to Player');
INSERT INTO `creature_text` VALUES (37195, 3, 0, 'Aye, Genn.  It is not law that binds us.  It is something far stronger.  My men are ready to give their lives under your command.', 12, 0, 100, 273, 0, 19512, 0, 0, 'Lord Darius Crowley to Player');
INSERT INTO `creature_text` VALUES (37701, 0, 0, 'Consider yourselves lucky to be alive, Gilnean dogs!  Now get to work before we change our minds!', 14, 0, 100, 25, 0, 0, 0, 0, 'Forsaken Slavedriver');
INSERT INTO `creature_text` VALUES (37875, 0, 0, 'No... I\'d sooner die than have one of your kind for a king!', 12, 0, 100, 0, 0, 19631, 0, 0, 'Lord Godfrey to Player');
INSERT INTO `creature_text` VALUES (38218, 0, 0, 'The Forsaken think we\'re weak.  A broken people.  They think we\'ll roll over like a scared dog.', 14, 0, 100, 0, 0, 19623, 0, 0, 'Prince Liam Greymane to Krennan Aranas');
INSERT INTO `creature_text` VALUES (38218, 1, 0, 'How wrong they are.  We will fight them in the fields until the last trench collapses and the last cannon is silenced.', 14, 0, 100, 0, 0, 19624, 0, 0, 'Prince Liam Greymane to Krennan Aranas');
INSERT INTO `creature_text` VALUES (38218, 2, 0, 'We will fight them on the streets until the last shot is fired.  And when there\'s no more ammunition, we\'ll crush their skulls with the stones that pave our city.', 14, 0, 100, 0, 0, 19625, 0, 0, 'Prince Liam Greymane to Krennan Aranas');
INSERT INTO `creature_text` VALUES (38218, 3, 0, 'We will fight them in the alleys, until our knuckles are skinned and bloody and our rapiers lay on the ground shattered.', 14, 0, 100, 0, 0, 19626, 0, 0, 'Prince Liam Greymane to Krennan Aranas');
INSERT INTO `creature_text` VALUES (38218, 4, 0, 'And if we find ourselves surrounded and disarmed... wounded and without hope... we will lift our heads in defiance and spit in their faces.', 14, 0, 100, 25, 0, 19627, 0, 0, 'Prince Liam Greymane to Krennan Aranas');
INSERT INTO `creature_text` VALUES (38218, 5, 0, 'But we will... NEVER SURRENDER!!!!!', 14, 0, 100, 0, 0, 19628, 0, 0, 'Prince Liam Greymane to Krennan Aranas');
INSERT INTO `creature_text` VALUES (38218, 6, 0, 'FOR GILNEAS!!!', 14, 0, 100, 0, 0, 19651, 0, 0, 'Prince Liam Greymane to Krennan Aranas');
INSERT INTO `creature_text` VALUES (38218, 7, 0, 'Attack!', 12, 0, 100, 0, 0, 0, 0, 0, 'Prince Liam Greymane to Forsaken Infantry');
INSERT INTO `creature_text` VALUES (38218, 8, 0, 'Push them back!', 12, 0, 100, 0, 0, 0, 0, 0, 'Prince Liam Greymane to Forsaken Crossbowman');
INSERT INTO `creature_text` VALUES (38218, 9, 0, 'Abominations are blocking the way towards the military district!  This won\'t be easy.', 12, 0, 100, 0, 0, 19609, 0, 0, 'Prince Liam Greymane to Krennan Aranas');
INSERT INTO `creature_text` VALUES (38218, 10, 0, 'You\'re a sight for sore eyes, Lorna.  Let\'s get those cannons manned!  The rest of you, lure the abominations into the open!', 14, 0, 100, 0, 0, 19610, 0, 0, 'Prince Liam Greymane to Krennan Aranas');
INSERT INTO `creature_text` VALUES (38218, 11, 0, 'Crowley\'s troops are up ahead!  Press on!', 14, 0, 100, 0, 0, 19611, 0, 0, 'Prince Liam Greymane to Krennan Aranas');
INSERT INTO `creature_text` VALUES (38331, 0, 0, 'Gorerot crush puny worgen!!', 14, 0, 100, 0, 0, 20914, 0, 0, 'Gorerot to Krennan Aranas');
INSERT INTO `creature_text` VALUES (38415, 0, 0, 'He\'s too strong!  Use the catapults to bring him down!', 14, 0, 100, 0, 0, 19499, 0, 0, 'Lord Darius Crowley');
INSERT INTO `creature_text` VALUES (38415, 1, 0, 'Let us join your father\'s force\'s, Liam.  They\'ll need our help against Sylvanas.', 14, 0, 100, 0, 0, 19500, 0, 0, 'Lord Darius Crowley to Krennan Aranas');
INSERT INTO `creature_text` VALUES (38426, 0, 0, 'The villagers were thankful to have Emberstone back.  They brought us a little something to help against the Forsaken!', 12, 0, 100, 0, 0, 19684, 0, 0, 'Lorna Crowley to Krennan Aranas');
INSERT INTO `creature_text` VALUES (38766, 1, 0, 'Impossible!', 14, 0, 100, 0, 0, 19637, 0, 0, 'Lord Godfrey to Player');
INSERT INTO `creature_text` VALUES (38767, 1, 0, 'It is decided, then.  We will unite all Gilneans and drive the Forsaken from our lands.', 12, 0, 100, 0, 0, 19724, 0, 0, 'King Genn Greymane to Player');
INSERT INTO `creature_text` VALUES (43749, 2, 0, 'Time to go!', 12, 0, 100, 0, 0, 0, 0, 0, 'Tobias Mistmantle');
INSERT INTO `creature_text` VALUES (50881, 0, 0, 'You were a true man of the people, Liam.  Unlike any Royal I ever met.  We\'ll make them pay for this.', 12, 0, 100, 0, 0, 19686, 0, 0, 'Lorna Crowley to Player');
INSERT INTO `creature_text` VALUES (50893, 1, 0, 'It is here, surrounded by the heroes and patriots of Gilneas where he belongs.', 12, 0, 100, 1, 0, 19714, 0, 0, 'King Genn Greymane to Player');
INSERT INTO `creature_text` VALUES (50893, 2, 0, 'We\'ll return, Liam.  I swear this to you.', 12, 0, 100, 66, 0, 19715, 0, 0, 'King Genn Greymane to Player');
INSERT INTO `creature_text` VALUES (50902, 0, 0, 'Gilneas will remember your courage forever, Liam.', 12, 0, 100, 66, 0, 19497, 0, 0, 'Lord Darius Crowley to Player');
INSERT INTO `creature_text` VALUES (35845, 1, 0, 'The Alliance now controls the Twilight Shores beach head.', 41, 0, 100, 0, 0, 0, 0, 0, 'Dave\'s Industrial Light and Magic Bunny (Small)(Sessile) to Player');
INSERT INTO `creature_text` VALUES (42195, 0, 0, 'Take us out, Mister Gearchum.', 12, 0, 100, 1, 0, 19660, 0, 0, 'Captain Krazz');
INSERT INTO `creature_text` VALUES (42195, 1, 0, 'Chart a course east with all possible speed. We\'ll rendezvous with the main fleet.', 12, 0, 100, 5, 0, 19661, 0, 0, 'Captain Krazz');
INSERT INTO `creature_text` VALUES (42195, 2, 0, 'Signal the fleet to fall into formation.', 12, 0, 100, 1, 0, 19663, 0, 0, 'Captain Krazz');
INSERT INTO `creature_text` VALUES (42195, 3, 0, 'Order the fleet into close formation. Keep it tight, everyone!', 12, 0, 100, 25, 0, 19664, 0, 0, 'Captain Krazz');
INSERT INTO `creature_text` VALUES (42195, 4, 0, 'Heart of Orgrimmar, you\'re coming in too low. Too low!', 12, 0, 100, 1, 0, 19665, 0, 0, 'Captain Krazz');
INSERT INTO `creature_text` VALUES (42195, 5, 0, 'Pull up! Pull down! PULL SOMETHING!', 14, 0, 100, 5, 0, 0, 0, 0, 'Captain Krazz');
INSERT INTO `creature_text` VALUES (42195, 6, 0, 'The valley should mask our movements. Stay low. Stay close.', 12, 0, 100, 1, 0, 19666, 0, 0, 'Captain Krazz');
INSERT INTO `creature_text` VALUES (42195, 7, 0, 'I don\'t want to see any more mistakes!', 12, 0, 100, 5, 0, 0, 0, 0, 'Captain Krazz');
INSERT INTO `creature_text` VALUES (42195, 8, 0, 'What - WHAT!? What does he think he\'s DOING? The carriers are unprotected.', 12, 0, 100, 5, 0, 19667, 0, 0, 'Captain Krazz');
INSERT INTO `creature_text` VALUES (42195, 9, 0, 'What kind of madman orders away his close air support?', 12, 0, 100, 6, 0, 19668, 0, 0, 'Captain Krazz');
INSERT INTO `creature_text` VALUES (42195, 10, 0, 'Mister Gearchum, signal the fleet. Defensive formations!', 12, 0, 100, 1, 0, 19669, 0, 0, 'Captain Krazz');
INSERT INTO `creature_text` VALUES (42195, 11, 0, 'We\'re without our escort. I want to see interlocking fields of fire. Now, now!', 12, 0, 100, 5, 0, 19670, 0, 0, 'Captain Krazz');
INSERT INTO `creature_text` VALUES (42195, 12, 0, 'What the WHAT!?', 12, 0, 100, 0, 0, 19671, 0, 0, 'Captain Krazz');
INSERT INTO `creature_text` VALUES (42195, 13, 0, 'The guns, man the guns! Fire everything we\'ve got!', 14, 0, 100, 5, 0, 19672, 0, 0, 'Captain Krazz');
INSERT INTO `creature_text` VALUES (42195, 14, 0, 'Egah - all hands aft. One\'s aboard. Shoot it! Kill it!', 12, 0, 100, 5, 0, 19673, 0, 0, 'Captain Krazz');
INSERT INTO `creature_text` VALUES (42195, 15, 0, 'Somebody, DO SOMETHING! That\'s an order!', 12, 0, 100, 15, 0, 19674, 0, 0, 'Captain Krazz');
INSERT INTO `creature_text` VALUES (42195, 16, 0, 'Tamp down the inflammants. Batten up the gear carriage. Rudder for the shore.', 12, 0, 100, 1, 0, 19675, 0, 0, 'Captain Krazz');
INSERT INTO `creature_text` VALUES (42195, 17, 0, 'Everyone, to the parachutes!', 14, 0, 100, 15, 0, 19677, 0, 0, 'Captain Krazz');
INSERT INTO `creature_text` VALUES (42195, 18, 0, 'Mayday! Mayday! Mayday! Bail out everyone. BAIL!', 14, 0, 100, 5, 0, 19676, 0, 0, 'Captain Krazz');
INSERT INTO `creature_text` VALUES (42243, 0, 0, 'Aye aye, sir.', 12, 0, 100, 66, 0, 19597, 0, 0, 'Fergus Gearchum');
INSERT INTO `creature_text` VALUES (42243, 1, 0, 'Approaching the Thandol Span, sir.', 12, 0, 100, 25, 0, 19599, 0, 0, 'Fergus Gearchum');
INSERT INTO `creature_text` VALUES (42243, 2, 0, 'Oh, the Goblinity!', 14, 0, 100, 15, 0, 19600, 0, 0, 'Fergus Gearchum');
INSERT INTO `creature_text` VALUES (42243, 3, 0, 'Coming up on the Twilight Highlands, Captain. ', 12, 0, 100, 25, 0, 19601, 0, 0, 'Fergus Gearchum');
INSERT INTO `creature_text` VALUES (42243, 4, 0, 'Captain, sir! Alliance naval fleet sighted off the port bow.', 12, 0, 100, 25, 0, 19602, 0, 0, 'Fergus Gearchum');
INSERT INTO `creature_text` VALUES (42243, 5, 0, 'It\'s tangled up in the rigging.', 12, 0, 100, 1, 0, 19603, 0, 0, 'Fergus Gearchum');
INSERT INTO `creature_text` VALUES (42243, 6, 0, 'She\'s breaking up, sir!', 12, 0, 100, 5, 0, 19604, 0, 0, 'Fergus Gearchum');
INSERT INTO `creature_text` VALUES (42277, 0, 0, 'Fellow warriors of the Horde. Hear me now!', 14, 0, 100, 15, 0, 20542, 0, 0, 'Garrosh Hellscream to Garrosh Formation Bunny');
INSERT INTO `creature_text` VALUES (42277, 1, 0, 'Ahead hides our foe. Worshippers of chaos who seek to remake the world.', 14, 0, 100, 1, 0, 20564, 0, 0, 'Garrosh Hellscream to Garrosh Formation Bunny');
INSERT INTO `creature_text` VALUES (42277, 2, 0, 'A new world is coming, friends. But it is not the world of their design.', 14, 0, 100, 273, 0, 0, 0, 0, 'Garrosh Hellscream to Garrosh Formation Bunny');
INSERT INTO `creature_text` VALUES (42277, 3, 0, 'Together, we will crush the Twilight\'s Hammer, raze their stronghold,', 14, 0, 100, 25, 0, 0, 0, 0, 'Garrosh Hellscream to Garrosh Formation Bunny');
INSERT INTO `creature_text` VALUES (42277, 4, 0, 'salt the earth, and burn the bodies. All will tremble at our might. ', 14, 0, 100, 5, 0, 0, 0, 0, 'Garrosh Hellscream to Garrosh Formation Bunny');
INSERT INTO `creature_text` VALUES (42277, 5, 0, 'Then we will stand astride this world as its masters -', 14, 0, 100, 1, 0, 0, 0, 0, 'Garrosh Hellscream to Garrosh Formation Bunny');
INSERT INTO `creature_text` VALUES (42277, 6, 0, 'united in our conviction, unrivaled in strength, beholden to no one. ', 14, 0, 100, 5, 0, 0, 0, 0, 'Garrosh Hellscream to Garrosh Formation Bunny');
INSERT INTO `creature_text` VALUES (42277, 7, 0, 'Today, WE will remake the world. OUR world. FOR THE HORDE!', 14, 0, 100, 15, 0, 0, 0, 0, 'Garrosh Hellscream to Garrosh Formation Bunny');
INSERT INTO `creature_text` VALUES (42277, 8, 0, 'The Alliance steams in close formation without escort.', 14, 0, 100, 1, 0, 20543, 0, 0, 'Garrosh Hellscream to Garrosh Formation Bunny');
INSERT INTO `creature_text` VALUES (42277, 9, 0, 'Air guard, attack!! Strafe them now, while they cannot maneuver!', 14, 0, 100, 5, 0, 0, 0, 0, 'Garrosh Hellscream to Garrosh Formation Bunny');
INSERT INTO `creature_text` VALUES (42277, 10, 0, 'GET!', 14, 0, 100, 5, 0, 20544, 0, 0, 'Garrosh Hellscream to Garrosh\'s Airship');
INSERT INTO `creature_text` VALUES (42277, 11, 0, 'OFF!', 14, 0, 100, 5, 0, 0, 0, 0, 'Garrosh Hellscream to Garrosh\'s Airship');
INSERT INTO `creature_text` VALUES (42277, 12, 0, 'MY!', 14, 0, 100, 5, 0, 0, 0, 0, 'Garrosh Hellscream to Garrosh\'s Airship');
INSERT INTO `creature_text` VALUES (42277, 13, 0, 'SHIP!', 14, 0, 100, 5, 0, 21505, 0, 0, 'Garrosh Hellscream to Garrosh\'s Airship');
INSERT INTO `creature_text` VALUES (42300, 0, 0, 'Look - dwarves. Hah! They sulk like prairie dogs in holes in the ground.', 12, 0, 100, 25, 0, 21501, 0, 0, 'Kor\'kron Air Marine');
INSERT INTO `creature_text` VALUES (42300, 1, 0, 'A winner.', 12, 0, 100, 1, 0, 21502, 0, 0, 'Kor\'kron Air Marine');
INSERT INTO `creature_text` VALUES (42300, 2, 0, 'It\'s an attack!', 14, 0, 100, 5, 0, 21503, 0, 0, 'Kor\'kron Air Marine');
INSERT INTO `creature_text` VALUES (42300, 3, 0, 'They\'re after the Warchief -', 12, 0, 100, 5, 0, 21504, 0, 0, 'Kor\'kron Air Marine');
INSERT INTO `creature_text` VALUES (42300, 4, 0, 'The Warchief\'s zeppelin is in flames! It\'s going down!', 12, 0, 100, 25, 0, 21505, 0, 0, 'Kor\'kron Air Marine');
INSERT INTO `creature_text` VALUES (42638, 0, 0, 'I will help, but know that you\'re stepping into a viper\'s nest. Let me draw you a map - wha - behind you!', 12, 0, 100, 5, 0, 3325, 0, 0, 'Herezegor Flametusk to Player');
INSERT INTO `creature_text` VALUES (42638, 1, 0, '%s motions for you to speak with him again.', 16, 0, 100, 0, 0, 0, 0, 0, 'Herezegor Flametusk to Player');
INSERT INTO `creature_text` VALUES (42638, 2, 0, 'You fight without honor and die like vermin!', 12, 0, 100, 0, 0, 3325, 0, 0, 'Herezegor Flametusk to Twilight Nightblade');
INSERT INTO `creature_text` VALUES (42638, 3, 0, '%s scrawls out a depiction of his home port on a torn parchment.', 16, 0, 100, 397, 0, 0, 0, 0, 'Herezegor Flametusk to Player');
INSERT INTO `creature_text` VALUES (42638, 4, 0, 'There you have it, $n. All the safe approaches to the Twilight Highlands. Unleash havoc on our foes!', 12, 0, 100, 1, 0, 0, 0, 0, 'Herezegor Flametusk to Player');
INSERT INTO `creature_text` VALUES (42641, 0, 0, 'My warriors! You have already proven your valor. This is why the Warchief has selected you.', 12, 0, 100, 1, 0, 0, 0, 0, 'Warlord Krogg');
INSERT INTO `creature_text` VALUES (42641, 1, 0, 'Our destination is the Twilight Highlands. We strike at the beating heart of our enemy.', 12, 0, 100, 274, 0, 0, 0, 0, 'Warlord Krogg');
INSERT INTO `creature_text` VALUES (42644, 0, 0, 'All hail Samophlange!', 12, 0, 100, 5, 0, 0, 0, 0, 'Smoot');
INSERT INTO `creature_text` VALUES (42644, 1, 0, '$n stiff-arms Smoot and throws him to the ground.', 16, 0, 100, 0, 0, 0, 0, 0, 'Smoot to Player');
INSERT INTO `creature_text` VALUES (42646, 0, 0, '$gRight away, sir:Right away ma\'am;!', 12, 0, 100, 2, 0, 0, 0, 0, 'AWOL Grunt to Player');
INSERT INTO `creature_text` VALUES (42646, 1, 0, 'Please don\'t tell the Warlord...', 12, 0, 100, 2, 0, 0, 0, 0, 'AWOL Grunt to Player');
INSERT INTO `creature_text` VALUES (42646, 2, 0, '$gSir, yes sir:Ma\'am, yes ma\'am;!', 12, 0, 100, 2, 0, 0, 0, 0, 'AWOL Grunt to Player');
INSERT INTO `creature_text` VALUES (42646, 3, 0, 'It won\'t happen again, $gsir:ma\'am;.', 12, 0, 100, 16, 0, 0, 0, 0, 'AWOL Grunt to Player');
INSERT INTO `creature_text` VALUES (42650, 0, 0, 'We\'ll get right on this, $gsir:ma\'am;!', 12, 0, 100, 432, 0, 0, 0, 0, 'Goblin Siegeworker to Player');
INSERT INTO `creature_text` VALUES (42650, 1, 0, 'We\'re on it!', 12, 0, 100, 432, 0, 0, 0, 0, 'Goblin Siegeworker to Player');
INSERT INTO `creature_text` VALUES (42650, 2, 0, 'Yes $gsir:ma\'am;! Right away, $gsir:ma\'am;! ', 12, 0, 100, 432, 0, 0, 0, 0, 'Goblin Siegeworker to Player');
INSERT INTO `creature_text` VALUES (42671, 0, 0, 'This baby is all mine! I call her, \"The Throttler.\"', 12, 0, 100, 66, 0, 0, 0, 0, 'Chief Engineer to Player');
INSERT INTO `creature_text` VALUES (42671, 1, 0, 'Ready for inspection! ...Be gentle.', 12, 0, 100, 66, 0, 0, 0, 0, 'Chief Engineer to Player');
INSERT INTO `creature_text` VALUES (42671, 2, 0, 'Uh oh. Um. Oops.', 12, 0, 100, 0, 0, 0, 0, 0, 'Chief Engineer to Player');
INSERT INTO `creature_text` VALUES (42671, 3, 0, 'Look, look, it\'s okay! It\'s just a tiny little glitch. See?', 12, 0, 100, 0, 0, 0, 0, 0, 'Chief Engineer to Player');
INSERT INTO `creature_text` VALUES (42671, 4, 0, 'A little paint and it\'ll be fine!', 12, 0, 100, 0, 0, 0, 0, 0, 'Chief Engineer to Player');
INSERT INTO `creature_text` VALUES (42707, 0, 0, 'Cover the door - we\'ve got him!', 12, 0, 100, 0, 0, 0, 0, 0, 'Twilight Nightblade to Player');
INSERT INTO `creature_text` VALUES (42709, 0, 0, 'Oh no not de bar! Take it outside, mon!', 12, 0, 100, 0, 0, 0, 0, 0, 'Gravy to Player');
INSERT INTO `creature_text` VALUES (42775, 0, 0, 'What can I do for you, chief?', 12, 0, 100, 432, 0, 0, 0, 0, 'Bilgewater Dockworker to Player');
INSERT INTO `creature_text` VALUES (42777, 0, 0, 'Nobody tells me how to run my business here.', 12, 0, 100, 25, 0, 0, 0, 0, 'Bilgewater Foreman to Player');
INSERT INTO `creature_text` VALUES (42777, 1, 0, 'It was just a couple of rivets. That ship had plenty!', 12, 0, 100, 5, 0, 0, 0, 0, 'Bilgewater Foreman');
INSERT INTO `creature_text` VALUES (42777, 2, 0, 'I\'m sure it would\'ve been seaworthy in calm waters.', 12, 0, 100, 15, 0, 0, 0, 0, 'Bilgewater Foreman');
INSERT INTO `creature_text` VALUES (42777, 3, 0, 'Less rivets mean a cleaner environment!', 12, 0, 100, 25, 0, 0, 0, 0, 'Bilgewater Foreman');
INSERT INTO `creature_text` VALUES (42777, 4, 0, '...can we settle this with cash?', 12, 0, 100, 0, 0, 0, 0, 0, 'Bilgewater Foreman');
INSERT INTO `creature_text` VALUES (42777, 5, 0, 'Okay okay, stop! Stop! You make a compelling argument.', 12, 0, 100, 5, 0, 0, 0, 0, 'Bilgewater Foreman');
INSERT INTO `creature_text` VALUES (42777, 6, 0, 'I\'ll double the worker shifts and put in my own money to get those ships seaworthy and back on schedule. Count on it!', 12, 0, 100, 1, 0, 0, 0, 0, 'Bilgewater Foreman');
INSERT INTO `creature_text` VALUES (42978, 0, 0, 'What do we do now? Surely the Warchief would want us to storm the port in force!', 12, 0, 100, 5, 0, 0, 0, 0, 'Admiral Stonefist to Player');
INSERT INTO `creature_text` VALUES (43187, 0, 0, 'Ornak orders a boat to push off for the fleet.', 16, 0, 100, 0, 0, 0, 0, 0, 'Ornak to Player');
INSERT INTO `creature_text` VALUES (43188, 0, 0, 'See me if you need a boat to the shore, $n!', 12, 0, 100, 0, 0, 0, 0, 0, 'Horzog to Player');
INSERT INTO `creature_text` VALUES (43188, 1, 0, 'Horzog orders a landing boat to be lowered into the water. ', 16, 0, 100, 0, 0, 0, 0, 0, 'Horzog to Player');
INSERT INTO `creature_text` VALUES (43193, 0, 0, 'Perhaps. And perhaps we would win, but at what cost? Maybe $n has a better plan...', 12, 0, 100, 274, 0, 0, 0, 0, 'Warlord Krogg to Player');
INSERT INTO `creature_text` VALUES (43238, 0, 0, 'I am ready, $n. Soon the Dragonmaw will join the Horde!', 12, 0, 100, 0, 0, 0, 0, 0, 'Horde Negotiator to Player');
INSERT INTO `creature_text` VALUES (43238, 1, 0, 'I hear a great Dragonmaw medicine man lives here, in the large hut to our right.', 12, 0, 100, 25, 0, 0, 0, 0, 'Horde Negotiator to Player');
INSERT INTO `creature_text` VALUES (43238, 2, 0, 'After these negotiations, I am looking forward to a long and prosperous life.', 12, 0, 100, 1, 0, 0, 0, 0, 'Horde Negotiator to Player');
INSERT INTO `creature_text` VALUES (43238, 3, 0, 'The Dragonmaw Warchief is in the largest building up on the hilltop here. I\'m sure he\'ll be reasonable.', 12, 0, 100, 1, 0, 0, 0, 0, 'Horde Negotiator to Player');
INSERT INTO `creature_text` VALUES (43238, 4, 0, 'This is it! Stay close and let me do the talking. I\'m an expert.', 12, 0, 100, 1, 0, 0, 0, 0, 'Horde Negotiator to Player');
INSERT INTO `creature_text` VALUES (43238, 5, 0, 'Most esteemed Warchief -', 12, 0, 100, 2, 0, 0, 0, 0, 'Horde Negotiator to Player');
INSERT INTO `creature_text` VALUES (43238, 6, 0, 'Warchief, you misunderstand. Garrosh Hellscream will have a beach head on these shores, with or without your help. What we ask -', 12, 0, 100, 1, 0, 0, 0, 0, 'Horde Negotiator to Player');
INSERT INTO `creature_text` VALUES (43238, 7, 0, 'But the Horde has assembled an enormous host! A vast fleet of war machines and hardened veterans. Hellscream would as soon crush you as-', 12, 0, 100, 274, 0, 0, 0, 0, 'Horde Negotiator to Player');
INSERT INTO `creature_text` VALUES (43238, 8, 0, 'This is madness!', 12, 0, 100, 15, 0, 0, 0, 0, 'Horde Negotiator to Player');
INSERT INTO `creature_text` VALUES (43581, 0, 0, 'Abandon the port? Has Mor\'ghor beaten all the sense out of you? This attack is our best opportunity to take our destiny into our own - wait! Here comes $n from the Horde!', 12, 0, 100, 1, 0, 0, 0, 0, 'Zaela to Player');
INSERT INTO `creature_text` VALUES (43627, 0, 0, 'For the Horde!', 12, 0, 100, 4, 0, 0, 0, 0, 'Dragonmaw Warrior to Player');
INSERT INTO `creature_text` VALUES (43628, 0, 0, 'Victory for the Dragonmaw!', 12, 0, 100, 0, 0, 0, 0, 0, 'Dragonmaw Shaman to Player');
INSERT INTO `creature_text` VALUES (43869, 0, 0, 'She\'s going to blow! Get a parachute and jump!', 41, 0, 100, 0, 0, 0, 0, 0, 'Zeppelin Explosion Bunny (Large)');
INSERT INTO `creature_text` VALUES (43943, 0, 0, 'Ahh, there she is. The battle-maiden of the Dragonmaw. Let me see your face.', 12, 0, 100, 1, 0, 20514, 0, 0, 'Garrosh Hellscream to Player');
INSERT INTO `creature_text` VALUES (43943, 1, 0, 'Blood and honor, hero. The Horde welcomes you. Your people will be put to the test as we obliterate Twilight\'s Hammer and lay claim to this distant shore. But first... some unfinished business.', 12, 0, 100, 273, 0, 20515, 0, 0, 'Garrosh Hellscream to Player');
INSERT INTO `creature_text` VALUES (43962, 0, 0, 'Wherever the Horde takes us, we will keep our heads high and our blood pure. Good riddance, Mor\'ghor. A new Dragonmaw dawns this day.', 12, 0, 100, 0, 0, 21600, 0, 0, 'Zaela to Player');
INSERT INTO `creature_text` VALUES (43962, 1, 0, 'Hellscream, I am Zaela of the Dragonmaw. Your people have freed mine from tyranny. We are your instrument. The strength of the Dragonmaw is yours to command. For the Horde!', 12, 0, 100, 0, 0, 21601, 0, 0, 'Zaela to Player');
INSERT INTO `creature_text` VALUES (44024, 0, 0, 'Step on through, $n!', 12, 0, 100, 0, 0, 0, 0, 0, 'Artesh to Player');
INSERT INTO `creature_text` VALUES (45982, 0, 0, 'Hold the line!', 14, 0, 100, 22, 0, 0, 0, 0, 'Gralok to Player');
INSERT INTO `creature_text` VALUES (47206, 0, 0, 'Zeppelin arrival in two minutes.', 12, 0, 100, 0, 0, 22491, 0, 0, 'Loudspeaker');
INSERT INTO `creature_text` VALUES (47206, 1, 0, 'Zeppelin arrival in one minute. One minute.', 12, 0, 100, 0, 0, 22491, 0, 0, 'Loudspeaker');
INSERT INTO `creature_text` VALUES (47208, 0, 0, 'You\'ll also find a complementary bag of peanuts.', 12, 0, 100, 1, 0, 0, 0, 0, 'Assistant Greely to Loudspeaker');
INSERT INTO `creature_text` VALUES (47208, 1, 0, 'Nothing keeps a fleet of combustible dirigibles safer than randomly flinging molten metal in every conceivable direction!', 12, 0, 100, 4, 0, 0, 0, 0, 'Assistant Greely to Loudspeaker');
INSERT INTO `creature_text` VALUES (47208, 2, 0, 'With Pleasure!', 12, 0, 100, 273, 0, 0, 0, 0, 'Assistant Greely to Loudspeaker');
INSERT INTO `creature_text` VALUES (47208, 3, 0, 'I can hit my house from here!', 12, 0, 100, 5, 0, 0, 0, 0, 'Assistant Greely to Loudspeaker');
INSERT INTO `creature_text` VALUES (47208, 4, 0, 'No.', 12, 0, 100, 6, 0, 0, 0, 0, 'Assistant Greely to Loudspeaker');
INSERT INTO `creature_text` VALUES (47208, 5, 0, 'Maybe.', 12, 0, 100, 0, 0, 0, 0, 0, 'Assistant Greely to Loudspeaker');
INSERT INTO `creature_text` VALUES (47208, 6, 0, 'A little.', 12, 0, 100, 0, 0, 0, 0, 0, 'Assistant Greely to Loudspeaker');
INSERT INTO `creature_text` VALUES (47208, 7, 0, 'We call them \"Emergency Rampless Debarkation Devices.\"', 12, 0, 100, 1, 0, 0, 0, 0, 'Assistant Greely to Loudspeaker');
INSERT INTO `creature_text` VALUES (47208, 8, 0, 'Oh hells no! Those things are death-traps!', 12, 0, 100, 5, 0, 0, 0, 0, 'Assistant Greely to Loudspeaker');
INSERT INTO `creature_text` VALUES (47208, 9, 0, 'From all of us at Bilgewater Harbor...', 12, 0, 100, 1, 0, 0, 0, 0, 'Assistant Greely to Loudspeaker');
INSERT INTO `creature_text` VALUES (47208, 10, 0, 'Have fun storming the Highlands!', 12, 0, 100, 3, 0, 0, 0, 0, 'Assistant Greely to Loudspeaker');
INSERT INTO `creature_text` VALUES (47209, 0, 0, 'Should your zeppelin be attacked en-route, panic is inadvisable.', 12, 0, 100, 274, 0, 0, 0, 0, 'Hobart Grapplehammer to Loudspeaker');
INSERT INTO `creature_text` VALUES (47209, 1, 0, 'Instead, look for a number of deck-mounted flak cannons positioned for just such an emergency.', 12, 0, 100, 1, 0, 0, 0, 0, 'Hobart Grapplehammer to Loudspeaker');
INSERT INTO `creature_text` VALUES (47209, 2, 0, 'Greely, show them how the guns work.', 12, 0, 100, 25, 0, 0, 0, 0, 'Hobart Grapplehammer to Loudspeaker');
INSERT INTO `creature_text` VALUES (47209, 3, 0, 'Simply aim and pull the trigger. Or don\'t aim. I wash my hands of the whole thing.', 12, 0, 100, 1, 0, 0, 0, 0, 'Hobart Grapplehammer to Loudspeaker');
INSERT INTO `creature_text` VALUES (47209, 4, 0, 'Greely, you\'re not randomly firing that thing into Bilgewater Port, are you?', 12, 0, 100, 6, 0, 0, 0, 0, 'Hobart Grapplehammer to Loudspeaker');
INSERT INTO `creature_text` VALUES (47209, 5, 0, 'In the unlikely event that a catastrophic failure does not instantly kill everyone aboard...', 12, 0, 100, 1, 0, 0, 0, 0, 'Hobart Grapplehammer to Loudspeaker');
INSERT INTO `creature_text` VALUES (47209, 6, 0, '...a number of parachutes will appear along the railings.', 12, 0, 100, 273, 0, 0, 0, 0, 'Hobart Grapplehammer to Loudspeaker');
INSERT INTO `creature_text` VALUES (47209, 7, 0, 'Simply grab a nearby parachute to be vaulted clear of the burning zeppelin. Hopefully onto dry land.', 12, 0, 100, 1, 0, 0, 0, 0, 'Hobart Grapplehammer to Loudspeaker');
INSERT INTO `creature_text` VALUES (47209, 8, 0, 'Greely, would you like to demonstrate how our parachutes work?', 12, 0, 100, 6, 0, 0, 0, 0, 'Hobart Grapplehammer to Loudspeaker');
INSERT INTO `creature_text` VALUES (47209, 9, 0, 'Very well then. I see that the Spear of Durotar has almost arrived.', 12, 0, 100, 1, 0, 0, 0, 0, 'Hobart Grapplehammer to Loudspeaker');
INSERT INTO `creature_text` VALUES (47209, 10, 0, 'Your safety and well-being are no longer any concern of mine.', 12, 0, 100, 274, 0, 0, 0, 0, 'Hobart Grapplehammer to Loudspeaker');
INSERT INTO `creature_text` VALUES (45167, 0, 0, 'Aye, friend. How fare the Wildhammer clans? We\'ve much to discuss!', 12, 0, 100, 6, 0, 0, 0, 0, 'Kurdran Wildhammer to Player');
INSERT INTO `creature_text` VALUES (45168, 0, 0, 'Thas\'it! Pulverize the beach! Leave nuthin\' but ... sand.', 12, 0, 100, 5, 0, 0, 0, 0, 'Fargo Flintlocke');
INSERT INTO `creature_text` VALUES (45168, 1, 0, 'So long as we\'re sendin\' troops up the beach, we should double-up the artillery-fire. You know, to help.', 12, 0, 100, 6, 0, 0, 0, 0, 'Fargo Flintlocke');
INSERT INTO `creature_text` VALUES (45171, 0, 0, 'Hold fast, $c. This is for yer own protection.', 12, 0, 100, 432, 0, 0, 0, 0, 'Ephram Hardstone to Player');
INSERT INTO `creature_text` VALUES (45171, 1, 0, 'Here she comes. Oh, foul temptress of the sea...', 12, 0, 100, 5, 0, 0, 0, 0, 'Ephram Hardstone to Player');
INSERT INTO `creature_text` VALUES (45171, 2, 0, 'Avert yer eyes! Don\'t look at her!', 12, 0, 100, 274, 0, 0, 0, 0, 'Ephram Hardstone to Player');
INSERT INTO `creature_text` VALUES (45171, 3, 0, 'Don\'t listen to her voice!', 12, 0, 100, 25, 0, 0, 0, 0, 'Ephram Hardstone to Player');
INSERT INTO `creature_text` VALUES (45171, 4, 0, 'Don\'t look at her... ruby red... soft... sensual lips! Oh...', 12, 0, 100, 20, 0, 0, 0, 0, 'Ephram Hardstone to Player');
INSERT INTO `creature_text` VALUES (45171, 5, 0, 'There! She reveals herself. Attack!', 12, 0, 100, 5, 0, 0, 0, 0, 'Ephram Hardstone to Player');
INSERT INTO `creature_text` VALUES (45171, 6, 0, 'Eh - what? Who? What\'s going on?', 12, 0, 100, 0, 0, 0, 0, 0, 'Ephram Hardstone to Player');
INSERT INTO `creature_text` VALUES (45173, 0, 0, 'By wind and wave, by earth and fire... To Stormwind we go, $n!', 12, 0, 100, 0, 0, 0, 0, 0, 'Talaa to Player');
INSERT INTO `creature_text` VALUES (45210, 0, 0, 'She\'s beautiful...', 12, 0, 100, 0, 0, 0, 0, 0, 'Stranded Crewman to Player');
INSERT INTO `creature_text` VALUES (45210, 1, 0, 'Like an angel...', 12, 0, 100, 0, 0, 0, 0, 0, 'Stranded Crewman to Player');
INSERT INTO `creature_text` VALUES (45334, 0, 0, 'I will prove myself to the master!', 12, 0, 100, 0, 0, 0, 0, 0, 'Twilight Bonebreaker to Player');
INSERT INTO `creature_text` VALUES (45334, 1, 0, 'Biting... clawing... clutching... tearing...', 12, 0, 100, 0, 0, 0, 0, 0, 'Twilight Bonebreaker to Player');
INSERT INTO `creature_text` VALUES (45334, 2, 0, 'Do you not feel the master\'s gaze?! It watches us all!', 12, 0, 100, 0, 0, 0, 0, 0, 'Twilight Bonebreaker to Player');
INSERT INTO `creature_text` VALUES (45524, 0, 0, 'Aye, protect the tank while the boys make repairs! I\'ll try to get her moving again...', 12, 0, 100, 0, 0, 0, 0, 0, 'Siege Tank Commander to Siege Tank Controller Bunny');
INSERT INTO `creature_text` VALUES (45524, 1, 0, 'Ahh, blast it. C\'mon, c\'mon already!', 12, 0, 100, 0, 0, 0, 0, 0, 'Siege Tank Commander to Siege Tank Controller Bunny');
INSERT INTO `creature_text` VALUES (45524, 2, 0, 'There! We got it! Let\'s get \'er outta here.', 12, 0, 100, 0, 0, 0, 0, 0, 'Siege Tank Commander to Siege Tank Controller Bunny');
INSERT INTO `creature_text` VALUES (45527, 0, 0, 'Defend the Siege Tank!', 42, 0, 100, 0, 0, 0, 0, 0, 'Siege Tank Controller Bunny to Player');
INSERT INTO `creature_text` VALUES (45527, 1, 0, 'Success! The Siege Tank is able to retreat.', 42, 0, 100, 0, 0, 0, 0, 0, 'Siege Tank Controller Bunny to Player');
INSERT INTO `creature_text` VALUES (45668, 0, 0, 'See, Cassius. I told you that we could depend upon $n.', 12, 0, 100, 1, 0, 0, 0, 0, 'Kurdran Wildhammer to Player');
INSERT INTO `creature_text` VALUES (45708, 0, 0, 'You\'ve crossed into the domain of red dragons, $r.', 12, 0, 100, 1, 0, 0, 0, 0, 'Lirastrasza to Player');
INSERT INTO `creature_text` VALUES (45708, 1, 0, 'You and your allies have struck against my kind at the gates to Grim Batol in the past. You\'ve shown no respect for the sacred oath we would die to keep. You\'ll find no trust with me.', 12, 0, 100, 1, 0, 0, 0, 0, 'Lirastrasza to Player');
INSERT INTO `creature_text` VALUES (45708, 2, 0, 'Fortunately for you, your fate is not mine to decide. Calen awaits you by the tree.', 12, 0, 100, 1, 0, 0, 0, 0, 'Lirastrasza to Player');
INSERT INTO `creature_text` VALUES (45708, 3, 0, 'My drakes shall be watching you. Choose your words and your actions carefully, $r.', 12, 0, 100, 1, 0, 0, 0, 0, 'Lirastrasza to Player');
INSERT INTO `creature_text` VALUES (45770, 0, 0, 'Hop aboard, $n. Let\'s move!', 12, 0, 100, 0, 0, 0, 0, 0, 'Thordun Hammerblow to Player');
INSERT INTO `creature_text` VALUES (45770, 1, 0, 'I hope you Alliance are here to give them Dragonmaw Orcs a good thumpin\'.', 12, 0, 100, 5, 0, 0, 0, 0, 'Thordun Hammerblow to Player');
INSERT INTO `creature_text` VALUES (45770, 2, 0, 'You\'ve got quite the setup here, friend. Come with me while I find out who\'s in charge.', 12, 0, 100, 1, 0, 0, 0, 0, 'Thordun Hammerblow to Lightning');
INSERT INTO `creature_text` VALUES (45770, 3, 0, 'Stuff me like a haggis! Kurdran Wildhammer - in the flesh. He lives!', 12, 0, 100, 1, 0, 0, 0, 0, 'Thordun Hammerblow to Lightning');
INSERT INTO `creature_text` VALUES (45773, 0, 0, 'Aaaagghhh!', 14, 0, 100, 0, 0, 1374, 0, 0, 'Wildhammer Hellion to Wildhammer Gryphon');
