INSERT INTO `creature_text` VALUES (39594, 3, 0, 'You will have a new home in Azshara and the Bilgewater Cartel will become a part of the Horde!', 14, 0, 100, 25, 0, 20149, 39588, 0, 'Thrall to Trade Prince Gallywix');
INSERT INTO `creature_text` VALUES (18248, 0, 0, 'Grunting, %s turns away from the wanted poster, returning to his warm spot by the fire.', 16, 0, 100, 0, 0, 0, 18782, 0, 'Nekthar');
INSERT INTO `creature_text` VALUES (18248, 1, 0, '%s begins to read the wanted poster.', 16, 0, 100, 0, 0, 0, 18781, 0, 'Nekthar');
INSERT INTO `creature_text` VALUES (18384, 0, 0, '%s dons his wolf-fur vestments.', 16, 0, 100, 0, 0, 0, 17039, 0, 'Malukaz');
INSERT INTO `creature_text` VALUES (18384, 1, 0, 'Wolf spirit of the forest, hear me!', 12, 0, 100, 0, 0, 0, 17044, 0, 'Malukaz');
INSERT INTO `creature_text` VALUES (18384, 2, 0, 'Long ago, you were a companion to my people. We gave ourselves to evil and you were forgotten.', 12, 0, 100, 0, 0, 0, 17046, 0, 'Malukaz');
INSERT INTO `creature_text` VALUES (18384, 3, 0, 'But we have returned, spirit! We have returned to honor you again. Favor us with your presence once more.', 12, 0, 100, 0, 0, 0, 17047, 0, 'Malukaz');
INSERT INTO `creature_text` VALUES (18384, 4, 0, 'May our bond with you never be broken again, spirit.', 12, 0, 100, 0, 0, 0, 17055, 0, 'Malukaz');
INSERT INTO `creature_text` VALUES (18384, 5, 0, 'We have done it! Thrall would be proud.', 12, 0, 100, 0, 0, 0, 17066, 0, 'Malukaz');
INSERT INTO `creature_text` VALUES (19616, 0, 0, '%s howls in response to Malukaz\'s call.', 16, 0, 100, 0, 0, 0, 17051, 0, 'Terokkar Wolf Spirit');
INSERT INTO `creature_text` VALUES (19616, 1, 0, '%s expresses its approval.', 16, 0, 100, 0, 0, 0, 17086, 0, 'Terokkar Wolf Spirit');
INSERT INTO `creature_text` VALUES (19569, 0, 0, 'I hope those blood elves across the way don\'t think that I\'m spying on them!', 12, 0, 100, 5, 0, 0, 16952, 0, 'Netherologist Coppernickels');
INSERT INTO `creature_text` VALUES (19569, 1, 0, 'If I were smart, I\'d be selling this research for a seat on the rocket ship.  I have a feeling we\'re going to need to get out of here soon!', 12, 0, 100, 5, 0, 0, 16955, 0, 'Netherologist Coppernickels');
INSERT INTO `creature_text` VALUES (19569, 2, 0, 'If this is right, we don\'t have long! Oh well.', 12, 0, 100, 5, 0, 0, 16953, 0, 'Netherologist Coppernickels');
INSERT INTO `creature_text` VALUES (19569, 3, 0, 'This is the last time that I buy second-hand equipment from an ethereal!', 12, 0, 100, 5, 0, 0, 16951, 0, 'Netherologist Coppernickels');
INSERT INTO `creature_text` VALUES (27010, 0, 0, 'All these folks are keeping me busy.  Take this horse over to your stables.', 12, 7, 100, 396, 0, 0, 26522, 0, 'Celidh Aletracker to Jimmy the Stable Boy');
INSERT INTO `creature_text` VALUES (27364, 0, 0, 'Hey Celidh!  Do you have any pets you want me to take over to the stable?', 12, 7, 100, 0, 0, 0, 26428, 0, 'Jimmy the Stable Boy');
INSERT INTO `creature_text` VALUES (27364, 1, 0, 'Let\'s go boy.', 12, 7, 100, 0, 0, 0, 26432, 0, 'Jimmy the Stable Boy');
INSERT INTO `creature_text` VALUES (27364, 2, 0, 'If I were king, I\'d have a horse like you.  I\'d make sure you didn\'t get lost, and I bet you\'d make sure I never got lost.', 12, 7, 100, 0, 0, 0, 26478, 0, 'Jimmy the Stable Boy');
INSERT INTO `creature_text` VALUES (27364, 3, 0, 'We\'d be best friends.  I bet me and Anduin would also be best friends, too.  Friends are important.  ', 12, 7, 100, 0, 0, 0, 26479, 0, 'Jimmy the Stable Boy');
INSERT INTO `creature_text` VALUES (27364, 4, 0, 'I\'ll take you to some other horses.  You probably miss being around other horses.  They can be your friend too.', 12, 7, 100, 0, 0, 0, 26486, 0, 'Jimmy the Stable Boy');
INSERT INTO `creature_text` VALUES (16580, 0, 0, 'I say we fire it. Cut the rope.', 12, 1, 100, 1, 0, 0, 16981, 0, 'Thrallmar Grunt');
INSERT INTO `creature_text` VALUES (16580, 1, 0, 'You cut the rope. You have the knife.', 12, 1, 100, 1, 0, 0, 16983, 0, 'Thrallmar Grunt');
INSERT INTO `creature_text` VALUES (16580, 2, 0, 'Harumph! Coward.', 12, 1, 100, 1, 0, 0, 16982, 0, 'Thrallmar Grunt');
INSERT INTO `creature_text` VALUES (16580, 3, 0, 'You know, I think we can hit Honor Hold from here.', 12, 1, 100, 1, 0, 0, 16986, 0, 'Thrallmar Grunt');
INSERT INTO `creature_text` VALUES (16580, 4, 0, 'If General Krakork found out, he\'d eat us alive. Don\'t.', 12, 1, 100, 1, 0, 0, 16988, 0, 'Thrallmar Grunt');
INSERT INTO `creature_text` VALUES (16580, 5, 0, 'Ah! We can tell him Hagash stumbled into it and fired it accidentally!', 12, 1, 100, 1, 0, 0, 16987, 0, 'Thrallmar Grunt');
INSERT INTO `creature_text` VALUES (19561, 0, 0, 'I\'m blind, you stupid sow-spawn, not deaf. Don\'t even think about it.', 12, 1, 100, 1, 0, 0, 16990, 0, 'Thrallmar Grunt');
INSERT INTO `creature_text` VALUES (16580, 7, 0, '%s sighs.', 16, 0, 100, 0, 0, 0, 16989, 0, 'Thrallmar Grunt');
INSERT INTO `creature_text` VALUES (25317, 1, 0, 'This is harder than it looks.', 12, 7, 100, 0, 0, 0, 26488, 0, 'Civilian Recruit');
INSERT INTO `creature_text` VALUES (25317, 1, 1, 'Did that seem okay?', 12, 7, 100, 0, 0, 0, 26487, 0, 'Civilian Recruit');
INSERT INTO `creature_text` VALUES (25317, 1, 2, 'My arm is getting sore.', 12, 7, 100, 0, 0, 0, 26491, 0, 'Civilian Recruit');
INSERT INTO `creature_text` VALUES (25317, 1, 3, 'Hmm....', 12, 7, 100, 0, 0, 0, 26492, 0, 'Civilian Recruit');
INSERT INTO `creature_text` VALUES (25317, 1, 4, 'I think I\'m starting to get the hang of this.', 12, 7, 100, 0, 0, 0, 26490, 0, 'Civilian Recruit');
INSERT INTO `creature_text` VALUES (25317, 1, 5, 'This isn\'t easy.', 12, 7, 100, 0, 0, 0, 26489, 0, 'Civilian Recruit');
INSERT INTO `creature_text` VALUES (25317, 2, 0, 'You aren\'t going to let me sleep are you?', 12, 7, 100, 0, 0, 0, 26497, 0, 'Civilian Recruit');
INSERT INTO `creature_text` VALUES (25317, 2, 1, 'Are you going to be doing this for much longer?', 12, 7, 100, 0, 0, 0, 26502, 0, 'Civilian Recruit');
INSERT INTO `creature_text` VALUES (25317, 2, 2, 'Could you try to be a little quieter?', 12, 7, 100, 0, 0, 0, 26500, 0, 'Civilian Recruit');
INSERT INTO `creature_text` VALUES (25317, 3, 0, 'I just don\'t want to let anyone down.', 12, 7, 100, 0, 0, 0, 26494, 0, 'Civilian Recruit');
INSERT INTO `creature_text` VALUES (25317, 3, 1, 'I need to do something to keep myself busy.', 12, 7, 100, 0, 0, 0, 26495, 0, 'Civilian Recruit');
INSERT INTO `creature_text` VALUES (25317, 3, 2, 'I\'m just nervous.  Sorry.', 12, 7, 100, 0, 0, 0, 26493, 0, 'Civilian Recruit');
INSERT INTO `creature_text` VALUES (29369, 1, 0, 'You\'re not worth the scrap you\'re made from!', 12, 0, 100, 25, 0, 0, 30237, 0, 'Stormforged Taskmaster');
INSERT INTO `creature_text` VALUES (29369, 1, 1, 'Your tinker must\'ve made you from tin, you lazy piles of slag!', 12, 0, 100, 25, 0, 0, 30235, 0, 'Stormforged Taskmaster');
INSERT INTO `creature_text` VALUES (29369, 1, 2, 'A pack of mechanical squirrels could mine faster than you!', 12, 0, 100, 25, 0, 0, 30233, 0, 'Stormforged Taskmaster');
INSERT INTO `creature_text` VALUES (30275, 0, 0, '%s swipes at you with his claws!', 42, 0, 100, 0, 0, 0, 32763, 0, 'Wild Wyrm - SAY_SWIPE');
INSERT INTO `creature_text` VALUES (30275, 1, 0, 'DODGED!', 42, 0, 100, 0, 0, 0, 32794, 0, 'Wild Wyrm - SAY_DODGED');
INSERT INTO `creature_text` VALUES (30275, 2, 0, '%s shrieks in pain and twists around, grabbing you with his mouth!', 42, 0, 100, 0, 0, 0, 32813, 0, 'Wild Wyrm - SAY_PHASE_2');
INSERT INTO `creature_text` VALUES (30275, 3, 0, 'Your Grip is Failing!', 42, 0, 100, 0, 0, 0, 32784, 0, 'Wild Wyrm - SAY_GRIP_WARN');
INSERT INTO `creature_text` VALUES (30275, 4, 0, 'FATAL STRIKE MISSED! KEEP PRYING!', 42, 0, 100, 0, 0, 0, 32795, 0, 'Wild Wyrm - SAY_STRIKE_MISS');
INSERT INTO `creature_text` VALUES (36296, 0, 0, 'Did they bother to tell you who I am and why I am doing this?', 12, 0, 100, 6, 0, 0, 38586, 3, 'SAY_INTRO_0');
INSERT INTO `creature_text` VALUES (36296, 1, 0, '...or are they just using you like they do everybody else?', 12, 0, 100, 396, 0, 0, 38600, 3, 'SAY_INTRO_1');
INSERT INTO `creature_text` VALUES (36296, 2, 0, 'But what does it matter.  It is time for this to end.', 12, 0, 100, 397, 0, 0, 38591, 3, 'SAY_INTRO_2');
INSERT INTO `creature_text` VALUES (36296, 3, 0, 'Baxter! Get in here and help! NOW!', 12, 0, 100, 0, 0, 0, 38592, 3, 'SAY_CALL_BAXTER');
INSERT INTO `creature_text` VALUES (36296, 4, 0, 'It is time, Frye!  Attack!', 12, 0, 100, 0, 0, 0, 38593, 3, 'SAY_CALL_FRYE');
INSERT INTO `creature_text` VALUES (36296, 5, 0, '...please don\'t think less of me.', 12, 0, 100, 0, 0, 0, 38597, 3, 'SAY_HUMMEL_DEATH');
INSERT INTO `creature_text` VALUES (36272, 0, 0, 'Great. We\'re not gutless, we\'re incompetent.', 12, 0, 100, 0, 0, 0, 38596, 3, 'SAY_FRYE_DEATH');
INSERT INTO `creature_text` VALUES (36565, 0, 0, 'It has been the greatest honor of my life to serve with you, Hummel.', 12, 0, 100, 0, 0, 0, 38595, 3, 'SAY_BAXTER_DEATH');
INSERT INTO `creature_text` VALUES (3390, 0, 0, '%s places the spores within his alchemical instruments...', 16, 0, 100, 0, 0, 0, 1076, 0, 'Apothecary Helbrim');
INSERT INTO `creature_text` VALUES (3390, 1, 0, 'Ah, good.  These spores have rendered nicely.  Thank you, $n.', 12, 0, 100, 0, 0, 0, 1077, 0, 'Apothecary Helbrim');
INSERT INTO `creature_text` VALUES (17228, 0, 0, 'How can I be expected to get any work done with that thing spreading its heresy?', 12, 0, 100, 0, 0, 0, 14383, 0, 'Draenei Artificer');
INSERT INTO `creature_text` VALUES (17228, 1, 0, '%s sighs disgustedly as he turns back to his work.', 16, 0, 100, 0, 0, 0, 14384, 0, 'Draenei Artificer');
INSERT INTO `creature_text` VALUES (17228, 2, 0, 'Turning to face Tuluun, the %s sneers contemptuously.', 16, 0, 100, 0, 0, 0, 14381, 0, 'Draenei Artificer');
INSERT INTO `creature_text` VALUES (17228, 3, 0, 'We do not need your kind here.', 12, 0, 100, 0, 0, 0, 14382, 0, 'Draenei Artificer');
INSERT INTO `creature_text` VALUES (17528, 0, 0, 'Fools! Weaklings! Tzerak has no need of your assistance!', 14, 0, 100, 0, 0, 0, 14070, 1, 'Tzerak');
INSERT INTO `creature_text` VALUES (17663, 0, 0, 'If only I could get back to the mushroom thicket. I have not seen such a mature cluster in my lifetime!', 12, 7, 100, 0, 0, 0, 14269, 0, 'Jessera of Mac\'Aree');
INSERT INTO `creature_text` VALUES (17703, 0, 0, 'Don\'t you people ever check the mail?', 12, 7, 100, 0, 0, 0, 14328, 0, 'Messenger Hermesius');
INSERT INTO `creature_text` VALUES (17703, 0, 1, 'How am I expected to deliver all of these messages?', 12, 7, 100, 0, 0, 0, 14329, 0, 'Messenger Hermesius');
INSERT INTO `creature_text` VALUES (17703, 0, 2, 'I need to be re-assigned. This job is pushing me over the edge...', 12, 7, 100, 0, 0, 0, 14330, 0, 'Messenger Hermesius');
INSERT INTO `creature_text` VALUES (17703, 0, 3, 'He thinks he\'s so damned important. Always pushing me around... I\'ll show him. I\'ll show all of them!', 12, 7, 100, 0, 0, 0, 14332, 0, 'Messenger Hermesius');
INSERT INTO `creature_text` VALUES (17703, 0, 4, 'One of these days I\'m going to bust out the crystalline doomhammer and go positively orc on this town. Then they\'ll be sorry...', 12, 7, 100, 0, 0, 0, 14331, 0, 'Messenger Hermesius');
INSERT INTO `creature_text` VALUES (19847, 0, 0, '%s reads from the book of the dead.', 16, 0, 100, 0, 0, 0, 17446, 0, 'Levixus');
INSERT INTO `creature_text` VALUES (19847, 1, 0, 'Shane Dabiri, Lee, Alex T., Rob F., J. A. B.,  A. Mayberry!', 12, 8, 100, 15, 0, 0, 17454, 0, 'Levixus');
INSERT INTO `creature_text` VALUES (19847, 1, 1, 'Jeffrey Kaplan! Tom Chilton! Rob Pardo!', 12, 8, 100, 15, 0, 0, 17447, 0, 'Levixus');
INSERT INTO `creature_text` VALUES (19847, 1, 2, 'Justin Thavirat! Roman Kenney! Our entire awesome art and animation team!', 12, 8, 100, 15, 0, 0, 17448, 0, 'Levixus');
INSERT INTO `creature_text` VALUES (19847, 1, 3, 'Kevin Jordan and Jonathan LeCraft!', 12, 8, 100, 15, 0, 0, 17453, 0, 'Levixus');
INSERT INTO `creature_text` VALUES (19847, 1, 4, 'CHRIS METZEN!', 12, 8, 100, 15, 0, 0, 17449, 0, 'Levixus');
INSERT INTO `creature_text` VALUES (19847, 1, 5, 'Pat Nagle! World design team! Cory Stockton, Paul C., Jim Chadwick, Staats, Ed Hanes, Morris, Gotcher, Jesse!, Sarah B., Victor C., Dave A.! All of our amazing exterior artists!', 12, 8, 100, 15, 0, 0, 17450, 0, 'Levixus');
INSERT INTO `creature_text` VALUES (19847, 1, 6, 'Scott Mercer! Geoff, Wyatt, Joe, Travis, Kris, Steven! Brazie!', 12, 8, 100, 15, 0, 0, 17451, 0, 'Levixus');
INSERT INTO `creature_text` VALUES (19847, 1, 7, 'Luis, Brianna and Eric! Blizzard QA team! Of course our fans!', 12, 8, 100, 15, 0, 0, 17452, 0, 'Levixus');
INSERT INTO `creature_text` VALUES (21134, 0, 0, 'A deep, bone chilling voice echoes from the %s...', 16, 0, 100, 0, 0, 0, 18774, 0, 'Eye of the Citadel');
INSERT INTO `creature_text` VALUES (21134, 1, 0, 'How dare you trifle with my playthings, $n!', 12, 0, 100, 0, 0, 0, 18775, 0, 'Eye of the Citadel');
INSERT INTO `creature_text` VALUES (21134, 2, 0, 'The orcs in my citadel will drink your blood and dovour you!', 12, 0, 100, 0, 0, 0, 18776, 0, 'Eye of the Citadel');
INSERT INTO `creature_text` VALUES (22011, 0, 0, 'And... if we do not?', 12, 0, 100, 0, 0, 0, 19695, 0, 'Corok the Mighty');
INSERT INTO `creature_text` VALUES (22012, 0, 0, 'Lend us the strength of the giants of Shadowmoon, Corok. The master is a just ruler. He will be sure to take care of those that assisted in subduing the dissidents of this world.', 12, 0, 100, 0, 0, 0, 19693, 0, 'Chancellor Bloodleaf');
INSERT INTO `creature_text` VALUES (22012, 1, 0, 'Then you become enemies of my lord, Illidan. Enemies of Illidan are destroyed. Are your giants able to withstand an attack from the forces of Eclipse Point?', 12, 0, 100, 0, 0, 0, 19696, 0, 'Chancellor Bloodleaf');
INSERT INTO `creature_text` VALUES (22012, 2, 0, 'No... I did not think so...', 12, 0, 100, 0, 0, 0, 19697, 0, 'Chancellor Bloodleaf');
INSERT INTO `creature_text` VALUES (25274, 0, 0, 'Keep \'em hot you big dummy!', 12, 1, 100, 0, 0, 0, 25133, 0, 'Armorer Orkuruk');
INSERT INTO `creature_text` VALUES (25274, 0, 1, 'Those weapons ain\'t gonna build themselves! Faster!', 12, 1, 100, 0, 0, 0, 25134, 0, 'Armorer Orkuruk');
INSERT INTO `creature_text` VALUES (25274, 0, 2, 'You call that a hammer strike? What kind of amateurs am I workin\' with here?', 12, 1, 100, 0, 0, 0, 25135, 0, 'Armorer Orkuruk');
INSERT INTO `creature_text` VALUES (25274, 0, 3, 'You keep that garbage up and I\'ll ship you back to your momma in a box with a note strapped to your chest that reads: FAILURE.', 12, 1, 100, 0, 0, 0, 25136, 0, 'Armorer Orkuruk');
INSERT INTO `creature_text` VALUES (25274, 0, 4, 'And WHAT are our soldiers supposed to do with that shoddy piece of junk?', 12, 1, 100, 0, 0, 0, 25137, 0, 'Armorer Orkuruk');
INSERT INTO `creature_text` VALUES (25274, 0, 5, 'You\'re an embarassment to this entire operation! Pack up your stuff and get out!', 12, 1, 100, 0, 0, 0, 25138, 0, 'Armorer Orkuruk');
INSERT INTO `creature_text` VALUES (25289, 0, 0, 'Aye, solja! Lift off time!', 12, 1, 100, 1, 0, 0, 24521, 0, 'Wind Master To\'bor');
INSERT INTO `creature_text` VALUES (25286, 0, 0, 'Yes sir!', 12, 1, 100, 0, 0, 0, 24522, 0, 'Warsong Wind Rider');
INSERT INTO `creature_text` VALUES (27299, 0, 0, 'Tell me if this hurts.', 12, 0, 100, 0, 0, 0, 26296, 0, 'Initiate Greer');
INSERT INTO `creature_text` VALUES (27299, 0, 1, 'Let\'s try this one out...', 12, 0, 100, 0, 0, 0, 26298, 0, 'Initiate Greer');
INSERT INTO `creature_text` VALUES (27299, 0, 2, 'I think I\'ve got it this time..', 12, 0, 100, 0, 0, 0, 26297, 0, 'Initiate Greer');
INSERT INTO `creature_text` VALUES (27299, 1, 0, 'How\'d that feel?', 12, 0, 100, 0, 0, 0, 26299, 0, 'Initiate Greer');
INSERT INTO `creature_text` VALUES (27299, 1, 1, 'Was that potent enough?', 12, 0, 100, 0, 0, 0, 26302, 0, 'Initiate Greer');
INSERT INTO `creature_text` VALUES (27299, 1, 2, 'Do you think that will get some answers?', 12, 0, 100, 0, 0, 0, 26300, 0, 'Initiate Greer');
INSERT INTO `creature_text` VALUES (27299, 2, 0, 'Hmm...', 12, 0, 100, 0, 0, 0, 26304, 0, 'Initiate Greer');
INSERT INTO `creature_text` VALUES (27299, 2, 1, 'True, our enemies would show us no mercy.', 12, 0, 100, 0, 0, 0, 26320, 0, 'Initiate Greer');
INSERT INTO `creature_text` VALUES (27299, 2, 2, 'True, but how far should we push?  At what point are we no better than our enemies?', 12, 0, 100, 0, 0, 0, 26319, 0, 'Initiate Greer');
INSERT INTO `creature_text` VALUES (27300, 0, 0, 'You need more bite.  Do not show our enemies any mercy.', 12, 0, 100, 0, 0, 0, 26305, 0, 'Initiate Vernon');
INSERT INTO `creature_text` VALUES (27300, 0, 1, 'The more pain we can inflict, the quicker we can get our answers.', 12, 0, 100, 0, 0, 0, 26306, 0, 'Initiate Vernon');
INSERT INTO `creature_text` VALUES (25379, 1, 0, 'Look at \'em, Ug. You know what those are?', 12, 1, 100, 25, 0, 0, 24631, 0, 'Warden Nork Bloodfrenzy');
INSERT INTO `creature_text` VALUES (25379, 2, 0, 'Those...', 12, 1, 100, 25, 0, 0, 24633, 0, 'Warden Nork Bloodfrenzy');
INSERT INTO `creature_text` VALUES (25379, 3, 0, 'Those are cowards.', 12, 1, 100, 1, 0, 0, 24634, 0, 'Warden Nork Bloodfrenzy');
INSERT INTO `creature_text` VALUES (25379, 4, 0, '%s shakes his head.', 16, 1, 100, 274, 0, 0, 24636, 0, 'Warden Nork Bloodfrenzy');
INSERT INTO `creature_text` VALUES (25379, 5, 0, 'No, blood of my blood, they are soldiers of the Alliance. Our sworn enemies.', 12, 1, 100, 1, 0, 0, 24637, 0, 'Warden Nork Bloodfrenzy');
INSERT INTO `creature_text` VALUES (25379, 6, 0, 'They are here because they do not wish to defend their homes... their families...', 12, 1, 100, 1, 0, 0, 24639, 0, 'Warden Nork Bloodfrenzy');
INSERT INTO `creature_text` VALUES (25379, 7, 0, 'They have given up and surrendered - willingly - to us.', 12, 1, 100, 1, 0, 0, 24640, 0, 'Warden Nork Bloodfrenzy');
INSERT INTO `creature_text` VALUES (25379, 8, 0, 'Ug\'thor, they are afraid to die.', 12, 1, 100, 1, 0, 0, 24641, 0, 'Warden Nork Bloodfrenzy');
INSERT INTO `creature_text` VALUES (25379, 9, 0, '%s laughs.', 16, 1, 100, 11, 0, 0, 24646, 0, 'Warden Nork Bloodfrenzy');
INSERT INTO `creature_text` VALUES (25379, 10, 0, 'That\'s right, Ug. Victory or death!', 12, 1, 100, 273, 0, 0, 24647, 0, 'Warden Nork Bloodfrenzy');
INSERT INTO `creature_text` VALUES (25379, 11, 0, '[In Broken Common] Hear boy, cowards? Boy die! Boy die for freedom!', 12, 0, 100, 273, 0, 0, 24648, 0, 'Warden Nork Bloodfrenzy');
INSERT INTO `creature_text` VALUES (25426, 0, 0, 'No, popo. They look kind of like the pigs. Are they pigs?', 12, 1, 100, 1, 0, 0, 24632, 0, 'Ug\'thor Bloodfrenzy');
INSERT INTO `creature_text` VALUES (25426, 1, 0, 'Are they cowardly pigs, popo?', 12, 1, 100, 1, 0, 0, 24635, 0, 'Ug\'thor Bloodfrenzy');
INSERT INTO `creature_text` VALUES (25426, 2, 0, 'But popo, what are they afraid of? Why are they here?', 12, 1, 100, 1, 0, 0, 24638, 0, 'Ug\'thor Bloodfrenzy');
INSERT INTO `creature_text` VALUES (25426, 3, 0, '%s scratches his head.', 16, 1, 100, 0, 0, 0, 24642, 0, 'Ug\'thor Bloodfrenzy');
INSERT INTO `creature_text` VALUES (25426, 4, 0, 'I... I don\'t understand, popo.', 12, 1, 100, 1, 0, 0, 24643, 0, 'Ug\'thor Bloodfrenzy');
INSERT INTO `creature_text` VALUES (25426, 5, 0, '%s puffs out his chest and stands on his tip-toes.', 16, 1, 100, 0, 0, 0, 24644, 0, 'Ug\'thor Bloodfrenzy');
INSERT INTO `creature_text` VALUES (25426, 6, 0, 'Lok\'tar ogar, popo!', 12, 1, 100, 1, 0, 0, 24645, 0, 'Ug\'thor Bloodfrenzy');
INSERT INTO `creature_text` VALUES (28357, 0, 0, 'This one...', 12, 0, 100, 25, 0, 0, 27876, 0, 'Instructor Razuvious');
INSERT INTO `creature_text` VALUES (28357, 1, 0, 'The Lich King will be pleased with this initiate.', 12, 0, 100, 5, 0, 0, 27869, 0, 'Instructor Razuvious');
INSERT INTO `creature_text` VALUES (28357, 1, 1, 'Amidst the wretch, a champion has been found.', 12, 0, 100, 5, 0, 0, 27870, 0, 'Instructor Razuvious');
INSERT INTO `creature_text` VALUES (28357, 1, 2, 'CHAOS! DOOM! DESTRUCTION! This one will claim them all!', 12, 0, 100, 5, 0, 0, 27871, 0, 'Instructor Razuvious');
INSERT INTO `creature_text` VALUES (28357, 1, 3, 'Marvel at its tenacity and vigor! A champion has been found!', 12, 0, 100, 5, 0, 0, 27868, 0, 'Instructor Razuvious');
INSERT INTO `creature_text` VALUES (28357, 1, 4, 'A harbinger of death is reborn...', 12, 0, 100, 5, 0, 0, 27872, 0, 'Instructor Razuvious');
INSERT INTO `creature_text` VALUES (28357, 2, 0, 'Place upon it the trappings befitting a herald of Arthas.', 12, 0, 100, 1, 0, 0, 27873, 0, 'Instructor Razuvious');
INSERT INTO `creature_text` VALUES (28357, 3, 0, 'Stand and be measured! Rise, for your master awaits your arrival. Go now!', 12, 0, 100, 5, 0, 0, 27883, 0, 'Instructor Razuvious');
INSERT INTO `creature_text` VALUES (28357, 3, 1, 'Listen, death knight... Listen for the voice of your master. He calls to you now.', 12, 0, 100, 5, 0, 0, 27884, 0, 'Instructor Razuvious');
INSERT INTO `creature_text` VALUES (28357, 4, 0, 'Pathetic...', 12, 0, 100, 1, 0, 0, 27865, 0, 'Instructor Razuvious');
INSERT INTO `creature_text` VALUES (28357, 4, 1, 'Useless...', 12, 0, 100, 1, 0, 0, 27862, 0, 'Instructor Razuvious');
INSERT INTO `creature_text` VALUES (28357, 4, 2, 'Another failure...', 12, 0, 100, 1, 0, 0, 27861, 0, 'Instructor Razuvious');
INSERT INTO `creature_text` VALUES (28357, 4, 3, 'This one has awoken too soon. It retains emotion and memory...', 12, 0, 100, 1, 0, 0, 27864, 0, 'Instructor Razuvious');
INSERT INTO `creature_text` VALUES (28357, 5, 0, 'You have been measured and found wanting...', 12, 0, 100, 25, 0, 0, 27866, 0, 'Instructor Razuvious');
INSERT INTO `creature_text` VALUES (28357, 6, 0, 'Dispose of it...', 12, 0, 100, 1, 0, 0, 27867, 0, 'Instructor Razuvious');
