INSERT INTO `creature_text` VALUES (1377, 4, 0, 'Bah! That\'s no fun!', 12, 7, 100, 274, 0, 0, 241, 0, 'Pilot Stonegear');
INSERT INTO `creature_text` VALUES (1377, 5, 0, 'Aye sure, just as long as we get to hit the arena right after!', 12, 7, 100, 1, 0, 0, 244, 0, 'Pilot Stonegear');
INSERT INTO `creature_text` VALUES (1377, 6, 0, 'Har! You scared I\'ll hurt your little dainty racer?', 12, 7, 100, 6, 0, 0, 246, 0, 'Pilot Stonegear');
INSERT INTO `creature_text` VALUES (1377, 7, 0, 'Haha! Sturdy old Trollplow has just a few meager battle scars!', 12, 7, 100, 11, 0, 0, 248, 0, 'Pilot Stonegear');
