INSERT INTO `page_text` VALUES (2631, '<The pages are covered in ancient elven runes.>$B$BThe pages herein contain memories of events that transpired in the collection and creation of the reagents required to craft greater arcanum.$B$BMay our enemies never gain access to these libram. $B$BMay I live to see the pallid light of the moon shine upon Quel\'Thalas once again.$B$BMay I die but for the grace of Kael\'thas.$B$BMay I kill for the glory of Illidan.$B$B-Master Kariel Winthalus', 2632, 0, 0, 11723);
INSERT INTO `page_text` VALUES (2632, '<You cannot understand anything written on these pages.> ', 0, 0, 0, 11723);
INSERT INTO `page_text` VALUES (2633, '<The pages are covered in ancient elven runes.>$B$BThe pages herein contain memories of events that transpired in the collection and creation of the reagents required to craft greater arcanum.$B$BMay our enemies never gain access to these libram. $B$BMay I live to see the pallid light of the moon shine upon Quel\'Thalas once again.$B$BMay I die but for the grace of Kael\'thas.$B$BMay I kill for the glory of Illidan.$B$B-Master Kariel Winthalus', 2634, 0, 0, 11403);
INSERT INTO `page_text` VALUES (2634, '<The ink swirls and shifts around the page. You get the feeling that the book is mocking you.> ', 0, 0, 0, 11403);
INSERT INTO `page_text` VALUES (2635, '<The pages are covered in ancient elven runes.>$B$BThe pages herein contain memories of events that transpired in the collection and creation of the reagents required to craft greater arcanum.$B$BMay our enemies never gain access to these libram.$B$BMay I live to see the pallid light of the moon shine upon Quel\'Thalas once again.$B$BMay I die but for the grace of Kael\'thas.$B$BMay I kill for the glory of Illidan.$B$B-Master Kariel Winthalus', 2636, 0, 0, 11723);
INSERT INTO `page_text` VALUES (2653, '<You feel a searing pain when glancing at these pages.> ', 0, 0, 0, 0);
INSERT INTO `page_text` VALUES (2654, '<HTML>\n<BODY>\n<H1 align=\"center\">A TREATISE ON MILITARY RANKS</H1>\n<BR/>\n<BR/>\n<IMG src=\"Interface\\PvPRankBadges\\PvPRankAlliance\" align=\"left\"/>\n<BR/>\n<P align=\"right\">What follows are</P>\n<P align=\"right\">the military ranks</P>\n<P align=\"right\">of the Alliance,</P>\n<P align=\"right\">proceeded by such</P>\n<P align=\"right\">information as is</P>\n<P align=\"right\">known for the races</P>\n<P align=\"right\">of the Horde.  Each</P>\n<P align=\"right\">is presented in two</P>\n<P align=\"right\">sections, the officers and then the enlisted, with the ranks of each listed in descending order from highest to lowest.  Long live the Alliance!</P>\n</BODY>\n</HTML>', 2655, 0, 0, 25881);
