INSERT INTO `playerchoice_response` VALUES (247, 545, 3, 1390105, 0, 0, 0, 0, 0, 'Restoration', '', '', 'Select', 'Thousands of years ago, Aviana gifted the first druids a branch from the Mother Tree, G\'Hanir.\n\nIt is said that the staff aids its wielder in achieving perfect harmony with nature, allowing them to tap into deeply powerful restorative spells.\n\nThe branch is kept at a shrine beneath the roots of Shaladrassil, but contact has recently been lost with the druids there...', 'CONFIRM_ARTIFACT_CHOICE', NULL, 27062);
INSERT INTO `playerchoice_response` VALUES (240, 504, 2, 1390106, 0, 0, 0, 0, 0, 'Beast Mastery', '', '', 'Select', 'In ancient times, a vrykul warlord stole a rifle infused with the power of storms. He used the artifact\'s might to conquer his rivals on the Broken Isles.\n\nDelve into the warlord\'s tomb and recover this mighty weapon!', 'CONFIRM_ARTIFACT_CHOICE', NULL, 27062);
INSERT INTO `playerchoice_response` VALUES (87, 192, 0, 1041980, 0, 0, 0, 0, 0, '', '', '', 'Shattrath (group)', 'Every day, the Sargerai troops in Shattrath are becoming stronger and bolder. They pose a serious threat and can no longer be ignored.\r\n\r\nGather allies if you want to launch an attack on the Sargerais who took refuge in the city.', '', NULL, 26365);
INSERT INTO `playerchoice_response` VALUES (87, 191, 0, 1042067, 0, 0, 0, 0, 0, '', '', '', 'Plateau of the Secretary', 'Recent reports indicate increased activity of the Sargerai troops on the Socretar Plateau in the Shadowmoon Valley.\r\n\r\nSocrethar\'s minions present a danger to both the draenei and our garrison. You need to deal with them as soon as possible.', '', NULL, 26365);
INSERT INTO `playerchoice_response` VALUES (352, 997, 0, 1851141, 0, 0, 0, 0, 0, 'Gorgrond', '', '', 'Go to Gorgrond', 'The Blackrock Orcs are great masters in working with Blackrock ore, from which they smelter guns for the artillery and fleet of the Iron Horde.\r\n\r\nSet up a base in Gorgrond and stop the Iron Horde\'s mechanical battalion, terrifying Draenor.', '', NULL, 26365);
INSERT INTO `playerchoice_response` VALUES (308, 753, 0, 1854486, 0, 0, 0, 0, 0, 'Nightborne Elves', '', '', 'Select', 'After thousands of years of isolation, the night-born Suramar want to find their place in the world again. Lady Liadrin believes that the Horde will benefit from an alliance with these ancient elves.', 'CONFIRM_ALLY_CHOICE', NULL, 26654);
INSERT INTO `playerchoice_response` VALUES (308, 752, 0, 1854484, 0, 0, 0, 0, 0, 'Highmountain Tauren', '', '', 'Select', 'Highmountain Tauren have teamed up to push the Burning Legion from their lands. Bane Bloodhoof believes that they will serve the Horde well because of their strength and generosity.', 'CONFIRM_ALLY_CHOICE', NULL, 26654);
INSERT INTO `playerchoice_response` VALUES (322, 783, 0, 1854487, 0, 0, 0, 0, 0, 'Void Elves', '', '', 'Select', 'Alleria Windrunner learned of a group of blood elves exiled for studying Void magic. He believes that if you find these apostates, you can attract them to the Alliance.', 'CONFIRM_ALLY_CHOICE', NULL, 26755);
INSERT INTO `playerchoice_response` VALUES (322, 784, 0, 1854485, 0, 0, 0, 0, 0, 'Illuminated Draenei', '', '', 'Select', 'The Army of the Light played a key role in defeating the Burning Legion on Argus. High Exarch Turalyon believes that the Illuminators will proudly defend the Alliance from enemy attacks.', 'CONFIRM_ALLY_CHOICE', NULL, 26822);
INSERT INTO `playerchoice_response` VALUES (317, 776, 0, 1707689, 0, 0, 0, 0, 0, 'Shroud of Echoes', '', '', 'Select', 'Selecting this allows use of the Shroud of Arcane Echoes\' invisibility power while on Argus.', '', NULL, 27602);
INSERT INTO `playerchoice_response` VALUES (317, 775, 0, 1707686, 0, 0, 0, 0, 0, 'Desecrated Heart', '', '', 'Select', 'By choosing the defiled heart of Argus, you will be able to heal outside the battle, as well as return to life after taking mortal damage.', '', NULL, 27602);
INSERT INTO `playerchoice_response` VALUES (317, 771, 0, 1707688, 0, 0, 0, 0, 0, 'Justice of the Light', '', '', 'Select', 'By choosing this ability, you can specify the targets for the shelling from the \"Justice of the Light\", the main weapon of the \"Vindicar\". Valid only on Argus.', '', NULL, 27602);
INSERT INTO `playerchoice_response` VALUES (317, 782, 0, 1707688, 0, 0, 0, 0, 0, '', '', '', 'Activate', 'Activate the core of the matrix and select \"Justice of the Light\" - the main tool of \"Vindicar\" - as the ability to use on Argus.', '', NULL, 27602);
INSERT INTO `playerchoice_response` VALUES (317, 774, 0, 1707687, 0, 0, 0, 0, 0, 'Warframe', '', '', 'Select', 'Selecting this allows use of the Lightforged Warframe mechanized armor suit while on Argus.', '', NULL, 27602);
INSERT INTO `playerchoice_response` VALUES (70, 158, 0, 1042005, 0, 0, 0, 0, 0, '', '', '', 'Pit (group)', 'The forces of the Blackrock clan, based in the Pit area of ​​Gorgrond, form the backbone of the Iron Horde army. If we smash them in their native land, the remnants of the Iron Horde will no longer be able to withstand us.\r\n\r\nIf you decide on this battle, take reliable comrades with you. It’s definitely not worth counting on an easy victory over the Black Mountain clan.', '', NULL, 25961);
