INSERT INTO `quest_offer_reward` VALUES (11875, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you for your help, $n. No contribution to the war effort is too small. Our men are going to need every advantage they can get in this battle.', 0);
INSERT INTO `quest_offer_reward` VALUES (11877, 1, 1, 0, 0, 0, 0, 0, 0, '<Lord Torvos accepts the plans and looks over them.>$B$BYou\'ve done the offensive a great service, $N, but we should not rest. I wouldn\'t put it beyond our foes to plan more mischief in the future.', 15595);
INSERT INTO `quest_offer_reward` VALUES (11880, 0, 0, 0, 0, 0, 0, 0, 0, 'Thanks for your help. You don\'t know what it\'s like to have to deal with \"Doctor\" Wafflefry over there. In between his \"checkups\" on my progress, he\'s busy hawking some gadget or another derived from his military inventions.', 0);
INSERT INTO `quest_offer_reward` VALUES (11882, 2, 0, 0, 0, 0, 0, 0, 0, 'Well met. You\'ve come to learn some new ways to handle fire?', 15595);
INSERT INTO `quest_offer_reward` VALUES (11885, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent! Take this. I\'ve prepared this bundle with items symbolizing the foes you\'ve defeated. They will allow you to summon Terokk.$b$bBeware, $n. He is the most powerful arakkoa to ever exist. Do not underestimate him!', 0);
INSERT INTO `quest_offer_reward` VALUES (11886, 0, 0, 0, 0, 0, 0, 0, 0, 'This is worrisome, friend. It hints at a much larger plot beyond simple agitation of the local fire and water elements, and something here bears investigation...', 15595);
INSERT INTO `quest_offer_reward` VALUES (11891, 0, 0, 0, 0, 0, 0, 0, 0, 'They seek to summon a frost lord into our world? No! They cannot tip the balance!', 15595);
INSERT INTO `quest_offer_reward` VALUES (11915, 2, 0, 0, 0, 0, 0, 0, 0, 'Greetings. You\'ve come to learn some new ways to handle fire?', 15595);
INSERT INTO `quest_offer_reward` VALUES (11917, 0, 0, 0, 0, 0, 0, 0, 0, 'You have done well. It may seem a small act, but even this can aid the fight as a whole. Take this with my blessing, and return later, as I may have more for you to do.', 0);
INSERT INTO `quest_offer_reward` VALUES (11921, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done!  You\'ll be a fire juggler faster than the fuse on a ground flower!  Now gird yourself!  The next test is when things really heat up...', 0);
INSERT INTO `quest_offer_reward` VALUES (11922, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done!  You\'ll be a fire juggler faster than the fuse on a ground flower!  Now gird yourself!  The next test is when things really heat up...', 15595);
INSERT INTO `quest_offer_reward` VALUES (11923, 6, 11, 0, 0, 0, 0, 0, 0, 'Did you get burned?  If you did, then maybe you\'ll think twice before playing with fire...$B$BJust kidding!  Well done!  It\'s official.  You\'re a bona fide fire juggler.  Now get out there and have some fun!', 15595);
INSERT INTO `quest_offer_reward` VALUES (11924, 0, 0, 0, 0, 0, 0, 0, 0, 'Torch catching is a lot of fun, don\'t you think? But have care! You don\'t want to get singed!', 0);
INSERT INTO `quest_offer_reward` VALUES (11925, 0, 0, 0, 0, 0, 0, 0, 0, 'Torch catching is a lot of fun, don\'t you think? But have care! You don\'t want to get singed!', 0);
INSERT INTO `quest_offer_reward` VALUES (11926, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done!  You\'ll be a fire juggler faster than the fuse on a ground flower!  Now gird yourself!  The next test is when things really heat up...', 0);
INSERT INTO `quest_offer_reward` VALUES (11933, 5, 1, 0, 0, 0, 0, 0, 0, 'This fire seems new... Yet, you say it came from the Exodar? Fascinating! To make your way into that ship...  Of course, I\'ll pay you for this. If you find anything else like this, be sure to speak with me again.', 15595);
INSERT INTO `quest_offer_reward` VALUES (11935, 5, 1, 0, 0, 0, 0, 0, 0, 'What a glorious fire! You received this from Silvermoon, you say? Unbelievable! With the flames of all four cities, who knows what we could accomplish?', 15595);
INSERT INTO `quest_offer_reward` VALUES (11947, 0, 0, 0, 0, 0, 0, 0, 0, 'You have done well. It may seem a small act, but even this can aid the fight as a whole. Take this with my blessing, and return later, as I may have more for you to do.', 0);
