INSERT INTO `quest_offer_reward` VALUES (12372, 1, 6, 0, 0, 0, 0, 0, 0, 'Incredible! Let me just say that I continue to be amazed by what you younger races seem capable of.$B$BWe are indebted to you. Will you come back and help us again tomorrow?', 15595);
