INSERT INTO `quest_offer_reward` VALUES (13021, 0, 0, 0, 0, 0, 0, 0, 0, 'It is good to know the people of this land still pay homage to the olden races.  I bid you well, $N, and offer you this token...', 0);
INSERT INTO `quest_offer_reward` VALUES (13024, 0, 0, 0, 0, 0, 0, 0, 0, 'Your spirit burns with life, young $c.  I accept the homage you pay, and offer in return this token...', 0);
INSERT INTO `quest_offer_reward` VALUES (13025, 0, 0, 0, 0, 0, 0, 0, 0, 'Your spirit burns with life, young $c.  I accept the homage you pay, and offer in return this token...', 0);
INSERT INTO `quest_offer_reward` VALUES (13026, 0, 0, 0, 0, 0, 0, 0, 0, 'Your spirit burns with life, young $c.  I accept the homage you pay, and offer in return this token...', 0);
INSERT INTO `quest_offer_reward` VALUES (13027, 0, 0, 0, 0, 0, 0, 0, 0, 'Your spirit burns with life, young $c.  I accept the homage you pay, and offer in return this token...', 0);
