INSERT INTO `quest_offer_reward` VALUES (13816, 0, 0, 0, 0, 0, 0, 0, 0, 'It\'s locked?  Figures!$b$bI\'ll let Brann know... maybe he can uncover something in the Archivum.', 0);
INSERT INTO `quest_offer_reward` VALUES (13817, 0, 0, 0, 0, 0, 0, 0, 0, 'The platinum disc fits perfectly inside the console. It begins to spin as its contents are scanned by white beams of light.', 0);
INSERT INTO `quest_offer_reward` VALUES (13818, 0, 0, 0, 0, 0, 0, 0, 0, 'Stand by for verification.$b$bAccess to the Celestial Planetarium granted. Please retrieve your key.', 0);
INSERT INTO `quest_offer_reward` VALUES (13819, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'re here.  Let us hope this works.  For all our sakes.', 0);
INSERT INTO `quest_offer_reward` VALUES (13821, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent job, $n. We\'re one step closer to saving Azeroth!', 0);
INSERT INTO `quest_offer_reward` VALUES (13822, 0, 0, 0, 0, 0, 0, 0, 0, 'Ye did it! I don\'t know how, but ye did!', 0);
INSERT INTO `quest_offer_reward` VALUES (13823, 0, 0, 0, 0, 0, 0, 0, 0, 'Ye never cease to impress, $n.$b$bYe and me are having a pint together after all this is done, ye hear me?', 0);
INSERT INTO `quest_offer_reward` VALUES (13824, 0, 0, 0, 0, 0, 0, 0, 0, 'I\'ve got to hand it to you, $n. Our odds might not be the best, but I feel a lot better with you lending a hand.', 0);
INSERT INTO `quest_offer_reward` VALUES (13826, 0, 0, 0, 0, 0, 0, 0, 0, 'Well hello there, young $r.  Either my memory is failing me, or I forgot to give you this the last time we spoke....', 0);
INSERT INTO `quest_offer_reward` VALUES (13828, 0, 0, 0, 0, 0, 0, 0, 0, 'Keep the words of the master in mind as you practice with the training dummies.', 0);
INSERT INTO `quest_offer_reward` VALUES (13829, 1, 1, 0, 0, 0, 0, 0, 0, 'Keep the words of the master in mind as you practice with the training dummies.', 15595);
INSERT INTO `quest_offer_reward` VALUES (13830, 1, 0, 0, 0, 0, 0, 0, 0, 'So eating it makes you invisible... what a strange defense this clever fish has developed.$b$bI\'d like to digest this for a bit.  Pun not intended, $N.  Really.  Why don\'t you come see me tomorrow?', 0);
INSERT INTO `quest_offer_reward` VALUES (13832, 0, 0, 0, 0, 0, 0, 0, 0, 'Well now, that looks like it could be valuable.  Difficult to know until it\'s cleaned up and appraised, but I\'m still willing to make the trade if you are.', 0);
INSERT INTO `quest_offer_reward` VALUES (13833, 1, 0, 0, 0, 0, 0, 0, 0, 'You are a brave one, $N.  I hope you didn\'t lose any extremities making your fishing spot.  If you\'ve still got the fishing bug, come back tomorrow and we\'ll talk some more.', 15595);
INSERT INTO `quest_offer_reward` VALUES (13834, 1, 1, 5, 0, 0, 0, 0, 0, 'My, my, my!  You\'ve caught yourself some Terrorfish.  Why, that reminds me of the time...$b$b<Marcia regales you with stories of adventure and fishing for as long as you\'re willing to listen.>', 15595);
INSERT INTO `quest_offer_reward` VALUES (13835, 0, 0, 0, 0, 0, 0, 0, 0, 'Commit Valis\'s lesson to memory, $N. It will mean the difference between winning and losing your mounted combat matches.', 0);
INSERT INTO `quest_offer_reward` VALUES (13836, 0, 0, 0, 0, 0, 0, 0, 0, 'Oh, hello there $N.  Marcia said you\'d be coming by with that poor man\'s missing arm.$b$bShe also gave me this bag to give you as a reward.  It smells horrible, and I\'m anxious to make the trade if you don\'t mind.', 0);
INSERT INTO `quest_offer_reward` VALUES (13837, 0, 0, 0, 0, 0, 0, 0, 0, 'With a teacher like Rugan, you\'ll be an expert at mounted combat in no time.', 0);
INSERT INTO `quest_offer_reward` VALUES (13838, 1, 1, 0, 0, 0, 0, 0, 0, 'Commit Valis\'s lesson to memory, $N. It will mean the difference between winning and losing your mounted combat matches.', 15595);
INSERT INTO `quest_offer_reward` VALUES (13839, 1, 1, 0, 0, 0, 0, 0, 0, 'With experience and practice, you will become deadly with the charge.', 15595);
INSERT INTO `quest_offer_reward` VALUES (13845, 0, 0, 0, 0, 0, 0, 0, 0, 'You are brave to have carried this.  If even one drop were to touch your skin, you\'d have died an agonizing death.$b$bAllow me to pay you for it... I can always use venom of exceptional potency.', 15595);
INSERT INTO `quest_offer_reward` VALUES (13846, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you. I just know the crusade will put it to good use!', 0);
INSERT INTO `quest_offer_reward` VALUES (13847, 0, 0, 0, 0, 0, 0, 0, 0, 'Your bravery on the field has shown the crusaders what they already knew -- that our warriors are more than just parade ground decorations.', 0);
INSERT INTO `quest_offer_reward` VALUES (13850, 0, 0, 0, 0, 0, 0, 0, 0, 'You did it? You actually did it?!$b$bHo ho! Stubborn as my wife, you are.', 0);
INSERT INTO `quest_offer_reward` VALUES (13851, 0, 0, 0, 0, 0, 0, 0, 0, 'Your bravery on the field has shown the crusaders that we are worthy allies.', 0);
INSERT INTO `quest_offer_reward` VALUES (13852, 0, 0, 0, 0, 0, 0, 0, 0, 'Your bravery on the field has shown the that we are a match for the Argent Crusade\'s own.', 0);
