INSERT INTO `quest_offer_reward` VALUES (29848, 0, 0, 0, 0, 0, 0, 0, 0, '<Kaldir stares into the crystal in silence for a moment.>$B$BNo being has ever felt such sincere sorrow as the Maiden of Grief has.$B$BLet us remember her spirit as we continue our efforts against Loken.', 27101);
INSERT INTO `quest_offer_reward` VALUES (41510, 1, 2, 0, 0, 0, 0, 0, 0, 'Wise decision, prophet. Behind me is the astral portal to Dalaran, allowing us to move quickly between the base of operations and the Broken Isles. Use it to return to Dalaran. When you need to strengthen the artifact or command your followers, you can return here at any time through this portal.$b$b愿元素指引你，先知。', 29896);
INSERT INTO `quest_offer_reward` VALUES (39771, 1, 66, 0, 0, 0, 0, 0, 0, 'You are indeed the chosen one,$n。我将荣幸地为你效命终生。$b$No matter where you take the Ring of Earth, I will follow you!', 29896);
INSERT INTO `quest_offer_reward` VALUES (43338, 1, 6, 0, 0, 0, 0, 0, 0, 'Bai Hu is serious, right?$b$After you came here, many people came to Baihu Temple to present gifts. It seems that we are going to have competitors.', 29896);
INSERT INTO `quest_offer_reward` VALUES (43334, 1, 0, 0, 0, 0, 0, 0, 0, 'Welcome, shaman! Do you want the weapon of Raiden, god of the storm?$b$Many people came to Pandaria admiringly, but none of them qualified.', 29896);
INSERT INTO `quest_offer_reward` VALUES (26272, 0, 0, 0, 0, 0, 0, 0, 0, 'Anotha newblood, eh?$B$BYa actually have a sly look about ya though. Fierce eyes. You might have the makings of a real $c.$B$BLets not waste anymore time \'den.', 25383);
INSERT INTO `quest_offer_reward` VALUES (24641, 0, 0, 0, 0, 0, 0, 0, 0, 'Dese are some fine pelts, $G boy:girl;. We could make some hefty cloaks outta dese.', 25383);
