INSERT INTO `quest_offer_reward` VALUES (25644, 0, 0, 0, 0, 0, 0, 0, 0, 'You were right to come to me with this, $n.  This represents a great threat to us if we do not act swiftly and boldly.', 27326);
INSERT INTO `quest_offer_reward` VALUES (25548, 66, 0, 0, 0, 0, 0, 0, 0, 'You did well, $n.  I\'ve a new target for you.', 27326);
INSERT INTO `quest_offer_reward` VALUES (25549, 1, 6, 66, 0, 0, 0, 0, 0, 'You continue to prove your prowess, $n.  At this rate Hyjal might survive this ordeal after all.', 27326);
