INSERT INTO `quest_offer_reward` VALUES (25552, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'ve done well, $n.  We will use this to draw out the Twilight matriarch.', 27326);
INSERT INTO `quest_offer_reward` VALUES (25551, 0, 0, 0, 0, 0, 0, 0, 0, 'Hyjal has been saved, $n.  And it\'s all thanks to you.', 27326);
