INSERT INTO `quest_offer_reward` VALUES (28651, 0, 0, 0, 0, 0, 0, 0, 0, 'May the Dark Lady watch over you, $n.', 24015);
INSERT INTO `quest_offer_reward` VALUES (26566, 0, 0, 0, 0, 0, 0, 0, 0, 'So Mekkatorque himself has heard of my little project? Splendid! I\'ve almost finished my latest prototype, but maybe you can help me chase down those last few parts.', 23420);
INSERT INTO `quest_offer_reward` VALUES (26264, 0, 0, 0, 0, 0, 0, 0, 0, 'I can\'t wait to give this new technology a try! We\'ve lost more gnomes to radiation than we can count. It\'s time to turn that around!', 23420);
INSERT INTO `quest_offer_reward` VALUES (26265, 0, 0, 0, 0, 0, 0, 0, 0, 'What a relief! Thank you for your help. I hope that gadget Engineer Grindspark was working on helps get the airfield cleaned up permanently.', 23420);
INSERT INTO `quest_offer_reward` VALUES (26316, 0, 0, 0, 0, 0, 0, 0, 0, 'It won\'t be tough to collapse th\' tunnel, but I\'m goin\' to need my team an\' my equipment \'afore I can get to th\' task.', 23420);
INSERT INTO `quest_offer_reward` VALUES (26285, 0, 0, 0, 0, 0, 0, 0, 0, 'Well, now, that\'s more like it. We may get around to blowin\' up somethin\' after all.', 23420);
INSERT INTO `quest_offer_reward` VALUES (26284, 0, 0, 0, 0, 0, 0, 0, 0, 'You have me own thanks and th\' thanks of th\' men you rescued. I\'ve already had th\' boys start setting up the gear down inside the cave. It should be ready to go soon.', 23420);
INSERT INTO `quest_offer_reward` VALUES (26318, 0, 0, 0, 0, 0, 0, 0, 0, 'I could feel th\' explosion way up here! My boys did a fine job riggin\' up that blast, but it wouldn\'t have happened without your help. It\'s only fair that I split my contract payment with you for helpin\' me finish th\' job.', 23420);
INSERT INTO `quest_offer_reward` VALUES (26329, 0, 0, 0, 0, 0, 0, 0, 0, '<The high tinker reads Jessup\'s report.>$B$BSplendid news! With the troggs taken care of, we should be able to turn our attention to Crushcog\'s troublemaking.', 23420);
INSERT INTO `quest_offer_reward` VALUES (26331, 0, 0, 0, 0, 0, 0, 0, 0, 'That should set back Crushcog\'s plans a bit, but we can\'t rest until he is defeated and Chill Breeze Valley is secure.', 23420);
INSERT INTO `quest_offer_reward` VALUES (26333, 0, 0, 0, 0, 0, 0, 0, 0, 'Mekkatorque will be pleased to hear that you\'ve ruined Crushcog\'s plans to use our old weapons for his followers. Without the mechano-tanks under his command, he\'ll be weak and vulnerable.', 23420);
INSERT INTO `quest_offer_reward` VALUES (26339, 0, 0, 0, 0, 0, 0, 0, 0, 'Ah, yes, Kelsey told me to expect you. There\'s still much to do before we can take on Crushcog.', 23420);
INSERT INTO `quest_offer_reward` VALUES (26342, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'ve given us just the opening we\'ll need to get the drop on Crushcog. Excellent work, $n.', 23420);
INSERT INTO `quest_offer_reward` VALUES (26364, 0, 0, 0, 0, 0, 0, 0, 0, 'We\'re finally free of Razlo Crushcog and his interference! With the defeat of Crushcog and his forces, Thermaplugg can\'t afford to send any more of his followers to the surface. I can\'t wait for the day when we defeat him for good!', 23420);
INSERT INTO `quest_offer_reward` VALUES (30987, 273, 0, 0, 0, 0, 0, 0, 0, 'Pandaren... you\'re here.$b$bGood.', 19831);
INSERT INTO `quest_offer_reward` VALUES (30988, 0, 0, 0, 0, 0, 0, 0, 0, 'Before you go, $n - I have one more thing to discuss with you.', 19831);
INSERT INTO `quest_offer_reward` VALUES (30989, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'ve more than proven your worth, in battle and in heart. Few would be willing to leave their homeland to join the Alliance. Fewer still would be willing to face the King of Stormwind in combat.$b$bYou, and the rest of your people, are welcome among our ranks. Welcome to the Alliance, $n.', 19831);
INSERT INTO `quest_offer_reward` VALUES (31450, 1, 0, 0, 0, 0, 0, 0, 0, 'And so it is done... here we are, in our new home.', 19831);
