INSERT INTO `quest_request_items` VALUES (6032, 1, 0, 0, 0, 'Have you brought the mooncloth, $N?', 0);
INSERT INTO `quest_request_items` VALUES (6132, 1, 0, 0, 0, '', 0);
INSERT INTO `quest_request_items` VALUES (6134, 6, 0, 0, 0, 'Where\'s my ghost-o-plasm, $N?', 0);
INSERT INTO `quest_request_items` VALUES (6142, 1, 0, 0, 0, 'I can\'t wait to go fishing with some fresh soft-shelled clam meat; did you bring me the 10 pieces of it I need?', 0);
INSERT INTO `quest_request_items` VALUES (6143, 1, 0, 0, 0, '', 0);
INSERT INTO `quest_request_items` VALUES (6321, 1, 0, 0, 0, 'You appear to be on official business...', 0);
INSERT INTO `quest_request_items` VALUES (6322, 1, 0, 0, 0, 'If you must travel a long distance, then a bat is your best option.', 0);
INSERT INTO `quest_request_items` VALUES (6323, 1, 0, 0, 0, 'I keep my weapons in top condition.  They are cleaned and ready for use.', 0);
INSERT INTO `quest_request_items` VALUES (6324, 1, 0, 0, 0, '$N, you return.  Do you have our supplies from the Undercity?', 0);
INSERT INTO `quest_request_items` VALUES (6341, 1, 0, 0, 0, 'How may I help you?', 0);
INSERT INTO `quest_request_items` VALUES (6342, 1, 0, 0, 0, 'Are you ready to return to Dolanaar?', 0);
INSERT INTO `quest_request_items` VALUES (6343, 1, 0, 0, 0, '$N, you\'re back from Darnassus?', 0);
INSERT INTO `quest_request_items` VALUES (6344, 1, 0, 0, 0, 'How can I help you?', 0);
INSERT INTO `quest_request_items` VALUES (6361, 1, 0, 0, 0, 'Are you here for a wind rider?  You have something to transport?', 0);
INSERT INTO `quest_request_items` VALUES (6362, 1, 0, 0, 0, 'There is dust on your shoulders that must be from Mulgore.  You have spoken with my friend Varg?', 0);
