INSERT INTO `quest_request_items` VALUES (11237, 5, 0, 0, 0, 'What?! Plans for an attack upon the keep?$B$BA FROST WYRM?!!!', 0);
INSERT INTO `quest_request_items` VALUES (11236, 0, 0, 0, 0, 'Are you sure that you killed Mezhen and his necrolords, $c?', 0);
INSERT INTO `quest_request_items` VALUES (8193, 0, 0, 0, 0, 'Today between 2pm and 4pm we have a contest to determine who the greatest angler of them all is! Just be the FIRST PERSON to bring me 40 Tastyfish fished from the Tastyfish schools found along the coast of Stranglethorn before anyone else does and you will be declared the Master Angler!$b$bIf you are not the first person to bring me 40 fish, my apprentice here will still reward you with money for each 5 Tastyfish you bring her.$b$bOh, and don\'t delay, Tastyfish go bad quickly!', 0);
INSERT INTO `quest_request_items` VALUES (11007, 0, 0, 0, 0, 'You heard my call? Yes...', 0);
INSERT INTO `quest_request_items` VALUES (8194, 1, 0, 0, 0, 'Even though we already have a contest winner, I will still buy your Tastyfish from you at a reasonable price. Whadda ya say!', 0);
INSERT INTO `quest_request_items` VALUES (11233, 0, 0, 0, 0, 'Are the Alliance leaders dead, $N? Do not return until they are.', 0);
INSERT INTO `quest_request_items` VALUES (8221, 0, 0, 0, 0, '*Whir* *Click*$b$bI seek rare fish for Fishmaster Bluegill to catalogue. If you bring me a Keefer\'s Angelfish, I will provide you with this fine reward.$b$b*Zzzap* *Whir*', 0);
