INSERT INTO `quest_request_items` VALUES (11232, 0, 0, 0, 0, 'The cannons, $n. Is someone going to take care of them?', 0);
INSERT INTO `quest_request_items` VALUES (11231, 0, 0, 0, 0, 'With the prisoners at Gjalerbron freed, the vrykul will be unable to continue their unholy rites.$B$BYou\'re our only hope of stopping them.', 0);
INSERT INTO `quest_request_items` VALUES (8249, 1, 0, 0, 0, 'My rogues need to hone their lockpicking skills, and I need locks of adequate difficulty -- the stuff Zan makes us is unfit for all but the lowliest neophytes.$b$bTell ya what, if you bring me 5 Heavy Junkboxes, I\'ll provide you with some of the finest throwing knives known to roguekind.$b$bYou can unlock the boxes and rifle through them, but you\'ll have to leave at least a few coins in them... even a rogue in training needs motivation.', 0);
INSERT INTO `quest_request_items` VALUES (8260, 1, 0, 0, 0, NULL, 0);
