INSERT INTO `quest_request_items` VALUES (8356, 0, 0, 0, 0, 'No candy until you flex for me, $N...', 0);
INSERT INTO `quest_request_items` VALUES (8357, 0, 0, 0, 0, 'You know how to dance, don\'t you? Try by putting your right foot in... then your right foot out... your right foot in... shake it all about...', 0);
