INSERT INTO `quest_request_items` VALUES (11218, 1, 0, 0, 0, 'Construction on this place seems to be a never-ending task. I just hope that you\'re able to get us what we need to continue.', 0);
INSERT INTO `quest_request_items` VALUES (11202, 0, 0, 0, 0, 'It\'s hard to tell from all the way over here exactly what\'s happening down there. Did you manage to put those plague tanks to the torch?', 0);
INSERT INTO `quest_request_items` VALUES (11190, 0, 0, 0, 0, 'We\'re runnin\' dangerously low on rounds \'round here, $c. Our enemies need poundin\', especially Skorn!$B$BYa got more balls fer us?', 0);
INSERT INTO `quest_request_items` VALUES (11188, 0, 0, 0, 0, 'It is unfortunate that the captain had to have you go rile up the proto-drakes. Though they seem to lack the intelligence of some of the dragons we know, they still seem to be noble creatures. Not to mention the situation it places us in.$B$BDare I ask if you have dealt with the situation?', 0);
