INSERT INTO `quest_request_items` VALUES (8784, 1, 0, 0, 0, 'You\'re glowing! I know what that means...', 0);
INSERT INTO `quest_request_items` VALUES (8788, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (8789, 1, 0, 0, 0, 'Seek out what I requested. You will become greater than the sum of your parts, champion.', 0);
INSERT INTO `quest_request_items` VALUES (8790, 1, 0, 0, 0, 'You will strike them down and free us of these bonds.', 0);
INSERT INTO `quest_request_items` VALUES (8791, 1, 0, 0, 0, 'Ah, $N, you have returned! And in one piece, I might add. What news do you bring from Ahn\'Qiraj?', 0);
INSERT INTO `quest_request_items` VALUES (8798, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (8799, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (8801, 68, 0, 0, 0, 'Lady $N, you have freed us of its grasp.', 0);
INSERT INTO `quest_request_items` VALUES (8802, 1, 0, 0, 0, 'Lord $N! It is over...', 0);
