INSERT INTO `quest_request_items` VALUES (9436, 6, 0, 0, 0, 'Do not fail me, $N. If we can bend this Bloodscalp magic to our will, my people may yet eliminate the Amani threat.', 0);
INSERT INTO `quest_request_items` VALUES (9437, 1, 1, 0, 0, 'Have you freed Ithania and reclaimed my cargo from those glorified privateers?', 0);
