INSERT INTO `quest_request_items` VALUES (9447, 0, 0, 0, 0, 'You\'ve returned, $N.  Is your task complete?', 0);
INSERT INTO `quest_request_items` VALUES (9452, 1, 0, 0, 0, 'Watch out for murlocs: Strange and extremely annoying little creatures.', 0);
INSERT INTO `quest_request_items` VALUES (9453, 1, 0, 0, 0, 'What do you think of my armor? Crafted it myself! It\'s a little different than what we\'d traditionally wear but extremely comfortable.', 0);
INSERT INTO `quest_request_items` VALUES (9454, 1, 0, 0, 0, 'The stag hides are frail, which tends to make for tender, succulent flesh.', 0);
INSERT INTO `quest_request_items` VALUES (9455, 1, 0, 0, 0, 'You found what? Where?', 0);
INSERT INTO `quest_request_items` VALUES (9456, 1, 0, 0, 0, 'We can\'t risk this contamination spreading. The reports I\'ve heard come from Blood Watch are bad enough. This has to be stopped now!', 0);
INSERT INTO `quest_request_items` VALUES (9457, 1, 0, 0, 0, 'What news of Naias, $N?', 0);
INSERT INTO `quest_request_items` VALUES (9463, 1, 0, 0, 0, 'I fear that her health is in steady decline.', 0);
INSERT INTO `quest_request_items` VALUES (9466, 1, 0, 0, 0, 'You have news regarding Blacktalon?', 0);
INSERT INTO `quest_request_items` VALUES (9472, 1, 0, 0, 0, 'Well, $N. Is it done?', 0);
INSERT INTO `quest_request_items` VALUES (9473, 1, 0, 0, 0, 'Do not waste time, $g boy:girl;.', 0);
INSERT INTO `quest_request_items` VALUES (9483, 0, 0, 0, 0, 'Just because we\'re on this pilgrimage doesn\'t mean we have to give up all of life\'s pleasures.  It\'s been ages since I had a good wine.$B$BI\'d literally kill for a bottle of cenarion spirits!$B$BI know, I know, how unfashionable of me to enjoy a drink brewed by night elves.', 0);
INSERT INTO `quest_request_items` VALUES (9487, 1, 0, 0, 0, 'Didn\'t get too close to the brutes, I hope?', 0);
INSERT INTO `quest_request_items` VALUES (9488, 1, 0, 0, 0, 'The robe cannot be completed without the necessary supplies.  But you knew that, didn\'t you?', 0);
INSERT INTO `quest_request_items` VALUES (9490, 0, 0, 0, 0, 'Did you bring back proof of Blacktalon\'s death, $N?', 0);
