INSERT INTO `quest_request_items` VALUES (9512, 1, 0, 0, 0, 'Arrr... Yarr... Shiver me timbers... Er, sorry $g lad:lass;, I gotta practice me pirate lest I forget it...', 0);
INSERT INTO `quest_request_items` VALUES (9513, 1, 0, 0, 0, 'You do a good thing for my people, $N.', 0);
