INSERT INTO `quest_request_items` VALUES (9523, 1, 0, 0, 0, 'Don\'t come back without some ancient relics.', 0);
INSERT INTO `quest_request_items` VALUES (9527, 1, 0, 0, 0, 'My wife was named Thalrisa and my daughter, Magwin.', 0);
INSERT INTO `quest_request_items` VALUES (9528, 1, 0, 0, 0, 'Hail, $N.', 0);
INSERT INTO `quest_request_items` VALUES (9529, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9530, 1, 0, 0, 0, 'I love it when a plan comes together...', 0);
INSERT INTO `quest_request_items` VALUES (9531, 1, 0, 0, 0, 'That traitor is going to wish he never messed with Alliance Naval Command!', 0);
INSERT INTO `quest_request_items` VALUES (9537, 1, 0, 0, 0, 'Did you find the lying dog?', 0);
