INSERT INTO `quest_request_items` VALUES (9544, 1, 0, 0, 0, '[Furbolg] So the prophecy of Akida is true... A hero has been delivered to us.', 0);
INSERT INTO `quest_request_items` VALUES (9545, 0, 0, 0, 0, 'Have you seen the vision yet, $N?', 0);
INSERT INTO `quest_request_items` VALUES (9548, 1, 0, 0, 0, 'Did you manage to find my equipment?', 0);
INSERT INTO `quest_request_items` VALUES (9549, 1, 0, 0, 0, 'Do you have those samples for me to send back to Ironforge?', 0);
INSERT INTO `quest_request_items` VALUES (9550, 1, 0, 0, 0, 'Perhaps this old journal is what the map is referencing.', 0);
INSERT INTO `quest_request_items` VALUES (9557, 1, 0, 0, 0, 'The Light shine upon you in all your endeavors, $c. What is it you have there?', 0);
INSERT INTO `quest_request_items` VALUES (9559, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9560, 1, 0, 0, 0, 'They are ugly little creatures. Even the Blind Watcher could see them... and he\'s blind.', 0);
INSERT INTO `quest_request_items` VALUES (9561, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9562, 1, 0, 0, 0, 'A murloc kicked me in the behind and spit on me... in broad daylight!', 0);
INSERT INTO `quest_request_items` VALUES (9563, 0, 0, 0, 0, 'Having trouble finding the bitter?  Good old Bernie at Nethergarde Keep should stock barrels of it.$B$BUnless he\'s run out... that would be disastrous!', 0);
INSERT INTO `quest_request_items` VALUES (9564, 1, 0, 0, 0, 'Is that what I think it is?', 0);
INSERT INTO `quest_request_items` VALUES (9565, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9566, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9567, 1, 0, 0, 0, 'Were you successful in obtaining knowledge of the Nazzivus satyr?', 0);
INSERT INTO `quest_request_items` VALUES (9569, 1, 0, 0, 0, '<Vindicator Aalesia seems to look through you rather than at you, her mind fixed on dealing with the satyr.>$B$BHave you moved against the satyr leader as I have asked?', 0);
INSERT INTO `quest_request_items` VALUES (9570, 1, 0, 0, 0, '<Kurz grunts at you.>', 0);
