INSERT INTO `quest_request_items` VALUES (9574, 1, 0, 0, 0, 'Were you able to get the samples I need?', 0);
INSERT INTO `quest_request_items` VALUES (9576, 1, 0, 0, 0, 'What is it you have there?', 0);
INSERT INTO `quest_request_items` VALUES (9578, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9579, 1, 0, 0, 0, '<Morae looks at you hopefully.>$B$BDid your search turn up any trace of Galaen?', 0);
INSERT INTO `quest_request_items` VALUES (9580, 1, 0, 0, 0, 'How goes the hunt, $N?', 0);
INSERT INTO `quest_request_items` VALUES (9581, 1, 0, 0, 0, 'Were you able to gather the first crystal sample?', 0);
INSERT INTO `quest_request_items` VALUES (9582, 1, 0, 0, 0, 'Have you put down the beast? ', 0);
INSERT INTO `quest_request_items` VALUES (9584, 1, 0, 0, 0, 'Do you have the second crystal sample?', 0);
INSERT INTO `quest_request_items` VALUES (9585, 1, 0, 0, 0, 'Have you returned with the last sample for my set?', 0);
INSERT INTO `quest_request_items` VALUES (9594, 1, 0, 0, 0, 'Yes, $c? What is it you have there?', 0);
INSERT INTO `quest_request_items` VALUES (9595, 1, 0, 0, 0, 'Have you faced your nightmares?', 0);
INSERT INTO `quest_request_items` VALUES (9602, 1, 0, 0, 0, 'You have returned!', 0);
INSERT INTO `quest_request_items` VALUES (9603, 1, 0, 0, 0, 'Good day, $N. Are you interested in renting a hippogryph?', 0);
INSERT INTO `quest_request_items` VALUES (9604, 1, 0, 0, 0, 'Greetings, friend. You look like you\'ve come a long way. How can I help you?', 0);
INSERT INTO `quest_request_items` VALUES (9605, 1, 0, 0, 0, 'Where are you headed, $c?', 0);
INSERT INTO `quest_request_items` VALUES (9606, 1, 0, 0, 0, 'Welcome back, $N. Was Nurguni able to gather everything on the list?', 0);
INSERT INTO `quest_request_items` VALUES (9612, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9616, 1, 0, 0, 0, 'Yes? I\'m very busy, $N.', 0);
INSERT INTO `quest_request_items` VALUES (9620, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9621, 1, 0, 0, 0, 'Ah, a visitor from Quel\'Thalas!  What news do you bring?', 0);
INSERT INTO `quest_request_items` VALUES (9622, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9623, 1, 0, 0, 0, '<Torallius adjusts his eyepatch to look at you.>$B$BThis is the best we can offer the Hand of Argus?', 0);
INSERT INTO `quest_request_items` VALUES (9624, 1, 0, 0, 0, 'The sand pears are getting harder to find and, of course, that means less pie and fewer treats for the elekk here. Were you able to gather all the fruit I\'ll need?', 0);
INSERT INTO `quest_request_items` VALUES (9625, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9626, 1, 0, 0, 0, 'You\'ve come to see me, $r?  Speak and be quick.  I\'ve no time for the formalities of your race. ', 0);
INSERT INTO `quest_request_items` VALUES (9627, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9628, 1, 0, 0, 0, 'Were you able to find out what was keeping the survey team from returning?', 0);
INSERT INTO `quest_request_items` VALUES (9629, 1, 0, 0, 0, 'Did the device work? Have you marked the murlocs for my research?', 0);
INSERT INTO `quest_request_items` VALUES (9631, 1, 0, 0, 0, 'Alturus sent you. The stars spoke of your arrival.', 0);
INSERT INTO `quest_request_items` VALUES (9632, 1, 0, 0, 0, 'Greetings, $c. What have you there?', 0);
INSERT INTO `quest_request_items` VALUES (9634, 1, 0, 0, 0, 'We must act before the ravager displaces Bloodmyst Isle\'s native predators. Have you begun culling the ravager population?', 0);
INSERT INTO `quest_request_items` VALUES (9635, 1, 0, 0, 0, 'You got those parts yet?  I\'ve got some diving to do.', 0);
INSERT INTO `quest_request_items` VALUES (9636, 1, 0, 0, 0, 'You got those parts yet?  I\'ve got a compression coil to fix before Anchorite Ahuurn starts breathing down my neck!', 0);
INSERT INTO `quest_request_items` VALUES (9637, 1, 0, 0, 0, 'Have you obtained the tomes I seek, $N?', 0);
INSERT INTO `quest_request_items` VALUES (9638, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9639, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9640, 1, 0, 0, 0, 'Weren\'t you looking for a journal or something?', 0);
INSERT INTO `quest_request_items` VALUES (9641, 1, 0, 0, 0, 'Have you gathered crystal shards from the creatures of Bloodmyst?', 0);
INSERT INTO `quest_request_items` VALUES (9642, 1, 0, 0, 0, 'If you have more of the irradiated crystal shards, I\'d be happy to exchange them for another of my crystals.', 0);
INSERT INTO `quest_request_items` VALUES (9643, 1, 0, 0, 0, 'Did you manage to get the constrictor vines?', 0);
INSERT INTO `quest_request_items` VALUES (9644, 1, 0, 0, 0, 'What news do you bring, $N?', 0);
INSERT INTO `quest_request_items` VALUES (9645, 1, 0, 0, 0, 'You\'ve returned! What news do you bring?', 0);
INSERT INTO `quest_request_items` VALUES (9646, 1, 0, 0, 0, 'How can I help you, $c?', 0);
INSERT INTO `quest_request_items` VALUES (9647, 1, 0, 0, 0, 'Have you culled the moths?', 0);
INSERT INTO `quest_request_items` VALUES (9648, 1, 0, 0, 0, 'Remember, the mushrooms I requested are each unique to the different environments of Bloodmyst.', 0);
INSERT INTO `quest_request_items` VALUES (9649, 1, 0, 0, 0, 'Back so soon?', 0);
INSERT INTO `quest_request_items` VALUES (9663, 1, 0, 0, 0, 'This is no time to relax!', 0);
INSERT INTO `quest_request_items` VALUES (9666, 1, 0, 0, 0, 'Is it done?', 0);
INSERT INTO `quest_request_items` VALUES (9667, 1, 0, 0, 0, 'I don\'t have much longer, $N. They will kill me soon...', 0);
INSERT INTO `quest_request_items` VALUES (9668, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9669, 1, 0, 0, 0, 'It was a terrible mess... ', 0);
INSERT INTO `quest_request_items` VALUES (9670, 1, 0, 0, 0, 'Any luck?', 0);
INSERT INTO `quest_request_items` VALUES (9671, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9672, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9674, 1, 0, 0, 0, 'They\'re nasty creatures...', 0);
INSERT INTO `quest_request_items` VALUES (9680, 1, 0, 0, 0, 'Have you retrieved the bone fragment I require, $N?', 0);
INSERT INTO `quest_request_items` VALUES (9682, 1, 0, 0, 0, 'Do not mourn their demise, $N.', 0);
INSERT INTO `quest_request_items` VALUES (9683, 1, 0, 0, 0, 'Atoph must be lured out!', 0);
INSERT INTO `quest_request_items` VALUES (9687, 1, 0, 0, 0, 'The dragon wars have long since passed. All that remains are bones and the agitated remnants of Ysera\'s brood.', 0);
INSERT INTO `quest_request_items` VALUES (9688, 1, 0, 0, 0, 'It pains me to watch. Please, let us not speak of this any further.', 0);
INSERT INTO `quest_request_items` VALUES (9689, 1, 0, 0, 0, 'I recommend that you bring allies, $N.', 0);
INSERT INTO `quest_request_items` VALUES (9693, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9694, 1, 0, 0, 0, 'There are so many of them that I cannot help but think that they are somehow multiplying.', 0);
INSERT INTO `quest_request_items` VALUES (9696, 1, 0, 0, 0, 'I\'m a bit busy here, agent $n. This better be important.', 0);
INSERT INTO `quest_request_items` VALUES (9697, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9698, 1, 0, 0, 0, '<Velen smiles.>$B$BGreetings, $N. I have been expecting you.', 0);
INSERT INTO `quest_request_items` VALUES (9699, 1, 0, 0, 0, 'Well?', 0);
INSERT INTO `quest_request_items` VALUES (9700, 1, 0, 0, 0, 'I\'ve been hearing reports of strange creatures appearing in the area. Whether they are mutated indigenous animals or true void anomalies is something that you need to find out.', 0);
INSERT INTO `quest_request_items` VALUES (9701, 1, 0, 0, 0, 'Back so soon? Your observation of the Sporeggar sporelings is critical to my research. I hope you aren\'t the type to rush things. Show patience and make certain that you have explored the entirety of the Spawning Glen before giving me your final report.', 0);
INSERT INTO `quest_request_items` VALUES (9702, 5, 0, 0, 0, 'Be sure that you collect all of the evidence carefully, $c!  We\'re fitting the pieces of a giant puzzle together here.  A mistake on your part could lead us to the wrong conclusions.$B$BThen where would we be in our understanding of Zangarmarsh\'s ecology?', 0);
INSERT INTO `quest_request_items` VALUES (9703, 6, 0, 0, 0, 'Was the information accurate?', 0);
INSERT INTO `quest_request_items` VALUES (9704, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9706, 6, 0, 0, 0, 'What have you got there, $N? Is that a journal?', 0);
INSERT INTO `quest_request_items` VALUES (9708, 6, 0, 0, 0, 'Do you have those samples yet?', 0);
INSERT INTO `quest_request_items` VALUES (9709, 1, 0, 0, 0, 'You have all of the mushrooms that we need to create a new garden for the bog lords?', 0);
INSERT INTO `quest_request_items` VALUES (9711, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9716, 1, 0, 0, 0, 'Did you discover anything, $N?', 0);
INSERT INTO `quest_request_items` VALUES (9718, 0, 0, 0, 0, 'Hail, $N.', 0);
INSERT INTO `quest_request_items` VALUES (9720, 0, 0, 0, 0, 'Is it done, $c?', 0);
INSERT INTO `quest_request_items` VALUES (9724, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9726, 1, 0, 0, 0, 'Have you paid the naga a visit yet?', 0);
INSERT INTO `quest_request_items` VALUES (9727, 1, 0, 0, 0, 'Have you paid the naga a visit yet?', 0);
INSERT INTO `quest_request_items` VALUES (9728, 0, 0, 0, 0, 'It\'s good to see you again, $N.  How\'s your hunt for the naga going?', 0);
INSERT INTO `quest_request_items` VALUES (9729, 1, 0, 0, 0, 'Is Fwhoor alright? Have you recovered the ark?', 0);
INSERT INTO `quest_request_items` VALUES (9730, 1, 0, 0, 0, 'You want to talk about the bounty for Haghazed?  Well, is he dead?  There isn\'t much else to talk about!', 0);
INSERT INTO `quest_request_items` VALUES (9731, 0, 0, 0, 0, 'What news do you bring, $N?', 0);
INSERT INTO `quest_request_items` VALUES (9732, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9739, 0, 0, 0, 0, 'The spore sacs!  Did you get them?', 0);
INSERT INTO `quest_request_items` VALUES (9740, 1, 0, 0, 0, 'You must take out their portal controllers before you can destroy the gate itself!', 0);
INSERT INTO `quest_request_items` VALUES (9741, 1, 0, 0, 0, 'Void critters grow into void anomalies. Truth be told, we really don\'t know what these creatures are... the name is more of a classification.', 0);
INSERT INTO `quest_request_items` VALUES (9742, 1, 0, 0, 0, 'Have you rescued any more spore sacs, $r?', 0);
INSERT INTO `quest_request_items` VALUES (9743, 0, 0, 0, 0, 'How\'s the bog lord slaying going?  Teach those brutes not to come into sporeling territory!', 0);
INSERT INTO `quest_request_items` VALUES (9744, 0, 0, 0, 0, 'You killed many giants, but there are still many more left.  And they\'re still attacking our spore sacs!', 0);
INSERT INTO `quest_request_items` VALUES (9746, 1, 0, 0, 0, 'Their leader can\'t hide from us forever. We will systematically eliminate every possible hiding place until Sironas is found.', 0);
INSERT INTO `quest_request_items` VALUES (9747, 1, 0, 0, 0, 'You\'ve returned; are Kataru and the Umbrafen slavers dead?', 0);
INSERT INTO `quest_request_items` VALUES (9748, 5, 0, 0, 0, 'Don\'t drink the water, $N!', 0);
INSERT INTO `quest_request_items` VALUES (9752, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9753, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9756, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9758, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9759, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9760, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9761, 1, 0, 0, 0, 'Do you see any unusually brave and powerful draenei around here?', 0);
INSERT INTO `quest_request_items` VALUES (9762, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9769, 1, 0, 0, 0, 'What\'s that, there? Been bitten by the wing bug, have ya?$B$B<Magasha chuckles at her own joke.>', 0);
INSERT INTO `quest_request_items` VALUES (9770, 1, 1, 0, 0, 'I still see marshfangs, $N. Surely you can\'t be done so soon.', 0);
INSERT INTO `quest_request_items` VALUES (9771, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9772, 1, 0, 0, 0, 'You\'re back, but Jyoba\'s not with you. What became of him?', 0);
INSERT INTO `quest_request_items` VALUES (9773, 20, 0, 0, 0, 'Tell me you haven\'t come back empty-handed. $N.', 0);
INSERT INTO `quest_request_items` VALUES (9774, 1, 0, 0, 0, 'I see you smirking. Yes, my armor\'s going to look a bit odd until I\'ve replaced all the old metal, but it\'s better this than unprotected.', 0);
INSERT INTO `quest_request_items` VALUES (9775, 0, 0, 0, 0, 'You don\'t look like one of my men. Speak your piece, $r.', 0);
INSERT INTO `quest_request_items` VALUES (9776, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9777, 1, 0, 0, 0, 'From the looks of things, we\'ll need to replenish our spores soon.', 0);
INSERT INTO `quest_request_items` VALUES (9778, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9779, 1, 0, 0, 0, 'Back so soon? Have you found the missive?', 0);
INSERT INTO `quest_request_items` VALUES (9780, 1, 0, 0, 0, 'Have you any fish to contribute to our stores?', 0);
INSERT INTO `quest_request_items` VALUES (9781, 1, 0, 0, 0, 'How goes the hunt, friend?', 0);
INSERT INTO `quest_request_items` VALUES (9782, 1, 0, 0, 0, 'Were you able to get a sample of the soil in the Dead Mire?', 0);
INSERT INTO `quest_request_items` VALUES (9783, 1, 0, 0, 0, 'It pains me to kill the creatures of the Dead Mire, but they are beyond saving. The best we can do is work to restore the area and nurture a new generation.', 0);
INSERT INTO `quest_request_items` VALUES (9784, 0, 0, 0, 0, 'I could still use some more plant parts.  With the lakes losing their water, I\'ve a feeling the number of invasive species is on the rise.  I need more numbers to back up my theory, however.', 0);
INSERT INTO `quest_request_items` VALUES (9785, 1, 0, 0, 0, 'Have you spoken to the ancients yet, $N?', 0);
INSERT INTO `quest_request_items` VALUES (9786, 1, 0, 0, 0, 'Have you examined the Boha\'mu Ruins? Can you imagine what they were like in their full splendor?', 0);
INSERT INTO `quest_request_items` VALUES (9787, 1, 0, 0, 0, 'Do you have the idols?', 0);
INSERT INTO `quest_request_items` VALUES (9788, 0, 0, 0, 0, 'Did you find my belongings, $N?  The cave is not far from Umbrafen, near the border with Nagrand.', 0);
INSERT INTO `quest_request_items` VALUES (9789, 1, 1, 0, 0, 'The clefthoof of Nagrand are a good challenge.  I\'ve bagged myself quite a few.$B$BHow are you doing with that?', 0);
INSERT INTO `quest_request_items` VALUES (9790, 1, 0, 0, 0, 'Did you manage to gather any diaphanous wings?', 0);
INSERT INTO `quest_request_items` VALUES (9791, 1, 0, 0, 0, 'Have you been successful in your marshfang hunt?', 0);
INSERT INTO `quest_request_items` VALUES (9792, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9793, 1, 0, 0, 0, 'How may I help you?', 0);
INSERT INTO `quest_request_items` VALUES (9794, 1, 0, 0, 0, '<Kialon eyes you suspiciously.>$B$BI don\'t know you, $R. Do you have business with me?', 0);
INSERT INTO `quest_request_items` VALUES (9795, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9796, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9797, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9798, 1, 0, 0, 0, 'What have you got there?', 0);
INSERT INTO `quest_request_items` VALUES (9799, 1, 0, 0, 0, 'Hello, $c.  Do you have those flowers yet?', 0);
INSERT INTO `quest_request_items` VALUES (9800, 1, 0, 0, 0, '<Elementalist Lo\'ap makes two fists and shakes his head in disapproval.>$B$BOgre sized...', 0);
INSERT INTO `quest_request_items` VALUES (9801, 1, 0, 0, 0, 'Have you gathered the reagents?', 0);
INSERT INTO `quest_request_items` VALUES (9802, 0, 0, 0, 0, 'Did you find any plant parts, $N?', 0);
INSERT INTO `quest_request_items` VALUES (9803, 1, 0, 0, 0, 'Did you speak with the Feralfen elder?', 0);
INSERT INTO `quest_request_items` VALUES (9804, 0, 0, 0, 0, 'Use the Nagrand cherries before delving too deeply into the lake.', 0);
INSERT INTO `quest_request_items` VALUES (9805, 1, 1, 0, 0, 'With great shame I tell you that it is my own kin who seek to drudge up the waters of Nagrand and create marshland.', 0);
INSERT INTO `quest_request_items` VALUES (9806, 0, 0, 0, 0, 'You\'re back, $N.  Did you obtain the fertile spores?', 0);
INSERT INTO `quest_request_items` VALUES (9807, 1, 0, 0, 0, 'We can always use more fertile spores, $r.  Tallstalks are incredibly useful mushrooms.  Being able to grow them lets us do many things with them.', 0);
INSERT INTO `quest_request_items` VALUES (9808, 0, 0, 0, 0, 'Did you find any glowcaps, $N?  They\'re literally everywhere in the marsh.$B$BYou can spot their bright glow from far away if you pay attention.', 0);
INSERT INTO `quest_request_items` VALUES (9809, 0, 0, 0, 0, 'I\'ll take more glowcaps if you have them.  Our village can always use more of them.', 0);
INSERT INTO `quest_request_items` VALUES (9810, 0, 1, 0, 0, 'The Earthen Ring will help in any way possible.\n', 0);
INSERT INTO `quest_request_items` VALUES (9811, 1, 0, 0, 0, 'You seek audience with me?  I do not recall hearing of an appointment. ', 0);
INSERT INTO `quest_request_items` VALUES (9812, 1, 0, 0, 0, 'I was told a visitor from Quel\'Thalas was coming. I was expecting a blood elf, to tell you the truth.$b$bWhat news do you bring?', 0);
INSERT INTO `quest_request_items` VALUES (9813, 1, 0, 0, 0, 'Yes, $C?', 0);
INSERT INTO `quest_request_items` VALUES (9814, 1, 0, 0, 0, 'Have you got the mushrooms, mon?', 0);
INSERT INTO `quest_request_items` VALUES (9815, 1, 0, 0, 0, 'Do not leave any tainted muck behind!', 0);
INSERT INTO `quest_request_items` VALUES (9816, 1, 11, 0, 0, '<Witch Doctor Tor\'gash grins.>$B$BDid the lil\' Lost Ones like their gift?', 0);
INSERT INTO `quest_request_items` VALUES (9817, 1, 0, 0, 0, 'You\'re here to claim the bounty for Fyashe? That\'s the best news I\'ve heard all day.', 0);
INSERT INTO `quest_request_items` VALUES (9819, 0, 0, 0, 0, 'Do they sleep?', 0);
INSERT INTO `quest_request_items` VALUES (9820, 1, 0, 0, 0, 'What do you have there, $N?', 0);
INSERT INTO `quest_request_items` VALUES (9821, 0, 0, 0, 0, 'Gordawg eats this rock. Gordawg finds usurper. Bring rock!', 0);
INSERT INTO `quest_request_items` VALUES (9822, 1, 0, 0, 0, 'Have you found proof of the ogres\' intentions?', 0);
INSERT INTO `quest_request_items` VALUES (9823, 1, 1, 0, 0, 'The threat of the Ango\'rosh looms over us. Have you eliminated their leader?', 0);
INSERT INTO `quest_request_items` VALUES (9824, 0, 0, 0, 0, 'The readings, $N.  Did you complete them?', 0);
INSERT INTO `quest_request_items` VALUES (9825, 0, 0, 0, 0, 'Have you obtained the ghostly essences I require, $c?', 0);
INSERT INTO `quest_request_items` VALUES (9826, 0, 0, 0, 0, 'You know of the Violet Eye?  You do not look familiar.', 0);
INSERT INTO `quest_request_items` VALUES (9827, 6, 0, 0, 0, 'What is it you have there, $N?', 0);
INSERT INTO `quest_request_items` VALUES (9828, 1, 0, 0, 0, 'What is it you have there, $N?', 0);
INSERT INTO `quest_request_items` VALUES (9830, 1, 0, 0, 0, 'I hope to demonstrate the uses of bleeder venom to Anchorite Ahuurn and the others at Telredor. That\'s going to require me to double my supply.', 0);
INSERT INTO `quest_request_items` VALUES (9831, 0, 0, 0, 0, 'Have you obtained the fragment, $c?  You would do well not to waste any time in this matter.', 0);
INSERT INTO `quest_request_items` VALUES (9832, 0, 0, 0, 0, 'How goes your search for the key fragments, $N?', 0);
INSERT INTO `quest_request_items` VALUES (9833, 1, 0, 0, 0, 'Have you made progress in securing the roads?', 0);
INSERT INTO `quest_request_items` VALUES (9834, 1, 0, 0, 0, 'Does $N have hides for Maktu?', 0);
INSERT INTO `quest_request_items` VALUES (9835, 1, 0, 0, 0, 'How goes the fight against the Ango\'rosh?', 0);
INSERT INTO `quest_request_items` VALUES (9836, 0, 0, 0, 0, 'Who are you and what do you want?', 0);
INSERT INTO `quest_request_items` VALUES (9837, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9839, 1, 0, 0, 0, 'Is it done? Have you dealt with Overlord Gorefist?', 0);
INSERT INTO `quest_request_items` VALUES (9841, 1, 1, 0, 0, 'The sound seems to have receded into the distance. Is that wishful thinking or have you really done something about the problem?', 0);
INSERT INTO `quest_request_items` VALUES (9842, 1, 0, 0, 0, 'Have you managed to scare up some of those marshfang blades?', 0);
INSERT INTO `quest_request_items` VALUES (9843, 0, 0, 0, 0, 'Keanna\'s log... did you find it?', 0);
INSERT INTO `quest_request_items` VALUES (9844, 0, 0, 0, 0, 'I dread to ask, $N. What has become of the situation inside Karazhan?', 0);
INSERT INTO `quest_request_items` VALUES (9845, 1, 0, 0, 0, 'Is it safe for me to fish again?', 0);
INSERT INTO `quest_request_items` VALUES (9846, 1, 0, 0, 0, 'Have you gathered those totems?', 0);
INSERT INTO `quest_request_items` VALUES (9847, 1, 1, 0, 0, 'Were you able to summon the Feralfen spirit?', 0);
INSERT INTO `quest_request_items` VALUES (9848, 1, 0, 0, 0, 'Did your search of Daggerfen Village yield the information I need in order to get to the bottom of this poison puzzle?', 0);
INSERT INTO `quest_request_items` VALUES (9849, 0, 0, 0, 0, 'Gordawg wants the boulder returned.', 0);
INSERT INTO `quest_request_items` VALUES (9850, 1, 0, 0, 0, 'You think the clefthoof bulls are just going to fall dead at your feet here in front of the campfire?', 0);
INSERT INTO `quest_request_items` VALUES (9851, 1, 0, 0, 0, 'A warm fire and a $g woman : man; at your side for idle chit-chat is something you can get at home.  Here we hunt big game or die trying.', 0);
INSERT INTO `quest_request_items` VALUES (9852, 0, 0, 0, 0, 'There\'s no shame in admitting that you haven\'t the spine to hunt the mightiest of animals this land has to offer, $N.', 0);
INSERT INTO `quest_request_items` VALUES (9853, 0, 0, 0, 0, 'Gordawg eat Gurok power.', 0);
INSERT INTO `quest_request_items` VALUES (9854, 1, 6, 0, 0, 'Have you come to know the peace that comes with knowing your place in nature?  Are you the hunter or the hunted?', 0);
INSERT INTO `quest_request_items` VALUES (9855, 1, 6, 0, 0, 'Have you learned yet to calm the beating of your heart?  To be as still as a calm lake and patient, waiting for the right moment to strike down your quarry?', 0);
INSERT INTO `quest_request_items` VALUES (9856, 0, 0, 0, 0, 'Have you faced Gutripper yet?', 0);
INSERT INTO `quest_request_items` VALUES (9857, 1, 0, 0, 0, 'I\'m just going to lay here a little while longer.  I should be fine soon.', 0);
INSERT INTO `quest_request_items` VALUES (9858, 1, 0, 0, 0, 'I don\'t suppose...<cough>... you\'ve taken care of those...<cough>... thorngrazers yet?', 0);
INSERT INTO `quest_request_items` VALUES (9859, 0, 0, 0, 0, '<Harold looks up at you weakly, but manages a smile.>$B$BYou have...<cough>... the hoof?', 0);
INSERT INTO `quest_request_items` VALUES (9860, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9861, 5, 0, 0, 0, 'Give it here! Quickly!', 0);
INSERT INTO `quest_request_items` VALUES (9862, 0, 0, 0, 0, 'The air spirit claims that the Murkblood are searching for something at Sunspring...', 0);
INSERT INTO `quest_request_items` VALUES (9863, 1, 0, 0, 0, 'Do this for us because we cannot do it for ourselves. ', 0);
INSERT INTO `quest_request_items` VALUES (9864, 1, 0, 0, 0, '', 0);
INSERT INTO `quest_request_items` VALUES (9865, 1, 5, 0, 0, 'For each of my kin that they slew, ten of them must die!', 0);
INSERT INTO `quest_request_items` VALUES (9866, 1, 0, 0, 0, '', 0);
INSERT INTO `quest_request_items` VALUES (9867, 1, 0, 0, 0, 'Bring me their leader\'s head, $N.', 0);
INSERT INTO `quest_request_items` VALUES (9868, 1, 0, 0, 0, '<Garrosh barely acknowledges your presence. He appears deeply depressed.>', 0);
INSERT INTO `quest_request_items` VALUES (9869, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9870, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9871, 1, 0, 0, 0, 'What is it?', 0);
INSERT INTO `quest_request_items` VALUES (9872, 6, 0, 0, 0, 'What is it?', 0);
INSERT INTO `quest_request_items` VALUES (9873, 1, 0, 0, 0, 'Remember, $N, Ortor is a shaman of great power.', 0);
INSERT INTO `quest_request_items` VALUES (9874, 1, 0, 0, 0, 'The orcs will not understand what we are doing but it must be done. For the good of both orc and Broken. ', 0);
INSERT INTO `quest_request_items` VALUES (9875, 0, 0, 0, 0, 'You have something to show me?', 0);
INSERT INTO `quest_request_items` VALUES (9877, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9878, 1, 0, 0, 0, 'Murkblood are a nefarious tribe of Broken, $N. They may look like Kurenai but they are most assuredly savage, murdering beasts.', 0);
INSERT INTO `quest_request_items` VALUES (9879, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9882, 0, 0, 0, 0, 'Have you retrieved the crystal fragments I asked of you, $N?', 0);
INSERT INTO `quest_request_items` VALUES (9883, 0, 0, 0, 0, 'You\'ve got more crystal fragments?  Make sure you\'re not taking any that are still attached to the mountain; we wouldn\'t want to stir up trouble with the naaru.', 0);
INSERT INTO `quest_request_items` VALUES (9884, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9885, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9886, 1, 0, 0, 0, '', 0);
INSERT INTO `quest_request_items` VALUES (9887, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9889, 1, 0, 0, 0, 'I submit! I submit!', 0);
INSERT INTO `quest_request_items` VALUES (9890, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9891, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9892, 1, 0, 0, 0, 'Have you obtained any more warbeads? You\'ll find plenty of ogres to the far north, near the border with Zangarmarsh.', 0);
INSERT INTO `quest_request_items` VALUES (9893, 1, 0, 0, 0, 'How are those ogres treating you, $N? Tough bunch, aren\'t they?', 0);
INSERT INTO `quest_request_items` VALUES (9894, 1, 1, 0, 0, 'Have you located and dealt with Lord Klaq?', 0);
INSERT INTO `quest_request_items` VALUES (9895, 1, 0, 0, 0, 'Have you encountered Boglash?', 0);
INSERT INTO `quest_request_items` VALUES (9896, 1, 0, 0, 0, 'Did you manage to get the stinger? Remember, it\'s useless to me if it\'s damaged.', 0);
INSERT INTO `quest_request_items` VALUES (9897, 1, 0, 0, 0, 'Kristen is a very nice...<cough>... person to share her skins, wouldn\'t you agree?', 0);
INSERT INTO `quest_request_items` VALUES (9898, 1, 0, 0, 0, 'My luck is about to change - I can feel it.', 0);
INSERT INTO `quest_request_items` VALUES (9899, 1, 1, 0, 0, 'There\'s no changing his mind, I swear. I think I\'m going to have to ask Shadow Hunter Denjai for a transfer back to Zabra\'jin.', 0);
INSERT INTO `quest_request_items` VALUES (9900, 1, 0, 0, 0, 'You\'ve returned, $r.  Is Gava\'xi dead?', 0);
INSERT INTO `quest_request_items` VALUES (9901, 1, 0, 0, 0, 'Did you manage to track down Sporewing?', 0);
INSERT INTO `quest_request_items` VALUES (9902, 1, 0, 0, 0, 'I am ashamed that I cannot do more to help my enslaved brethren. but at least we have prevented more needless deaths.', 0);
INSERT INTO `quest_request_items` VALUES (9903, 1, 1, 0, 0, 'Have you \"retired\" old Mragesh?', 0);
INSERT INTO `quest_request_items` VALUES (9904, 0, 1, 0, 0, 'I take it Terrorclaw lives no more?', 0);
INSERT INTO `quest_request_items` VALUES (9905, 1, 0, 0, 0, 'Long time ago, Maktu might have used Mragesh\'s hide to make strong armor. Mragesh far too old for that now. Hide too soft for good armor.', 0);
INSERT INTO `quest_request_items` VALUES (9906, 1, 0, 0, 0, 'I\'m sending out a small organized search party for the rest of the villagers.', 0);
INSERT INTO `quest_request_items` VALUES (9907, 1, 0, 0, 0, 'They must be reminded that we are a force to be reckoned with in this region.', 0);
INSERT INTO `quest_request_items` VALUES (9910, 1, 0, 0, 0, 'Respect from fear! It is as good as any other kind. ', 0);
INSERT INTO `quest_request_items` VALUES (9911, 1, 0, 0, 0, '<The watcher blinks several times in surprise.>$B$BWhere did you get that... thing?', 0);
INSERT INTO `quest_request_items` VALUES (9912, 1, 0, 0, 0, '', 0);
INSERT INTO `quest_request_items` VALUES (9913, 1, 0, 0, 0, '', 0);
INSERT INTO `quest_request_items` VALUES (9914, 0, 0, 0, 0, 'It\'s ivory that I\'m after, not excuses.  Of course, if you\'d rather, we can renegotiate your percentage of the profits.  I doubt that you\'d be pleased with the result.', 0);
INSERT INTO `quest_request_items` VALUES (9915, 0, 0, 0, 0, 'I still have a need for more pairs of ivory tusks from the wild elekk that roam this land.  Unfortunately, my budget will no longer handle your cut of the profits.  If you continue to provide me what I need, however, you will be ingratiating yourself with the Consortium.', 0);
INSERT INTO `quest_request_items` VALUES (9916, 0, 0, 0, 0, 'The children might be able to go a day without feeling hunger pangs.', 0);
INSERT INTO `quest_request_items` VALUES (9917, 1, 0, 0, 0, 'Have you found any new information?', 0);
INSERT INTO `quest_request_items` VALUES (9918, 1, 0, 0, 0, 'He\'ll probably be asleep on a ridge.', 0);
INSERT INTO `quest_request_items` VALUES (9919, 0, 0, 0, 0, 'You\'ve something to show me, $N?', 0);
INSERT INTO `quest_request_items` VALUES (9920, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9921, 1, 0, 0, 0, 'We have only recently taken back Telaar. We will not lose it again!', 0);
INSERT INTO `quest_request_items` VALUES (9922, 1, 0, 0, 0, 'Beware the elementals in the basin, $N.', 0);
INSERT INTO `quest_request_items` VALUES (9923, 1, 0, 0, 0, 'That boy...$B$B<Arechron shakes his head in disapproval.>$B$BHe\'ll be the death of me.', 0);
INSERT INTO `quest_request_items` VALUES (9924, 1, 0, 0, 0, 'Did you find him?', 0);
INSERT INTO `quest_request_items` VALUES (9925, 1, 0, 0, 0, 'Did you take care of the voidspawn, $N?', 0);
INSERT INTO `quest_request_items` VALUES (9927, 1, 0, 0, 0, '<Lantresor\'s eyes open in bewilderment.>Is it done? ', 0);
INSERT INTO `quest_request_items` VALUES (9928, 1, 0, 0, 0, 'We will ignite a hatred heretofore unseen between the two clans! ', 0);
INSERT INTO `quest_request_items` VALUES (9931, 1, 0, 0, 0, 'Can you predict what will happen next? ', 0);
INSERT INTO `quest_request_items` VALUES (9932, 1, 0, 0, 0, 'Do you now understand why you could never win a war against me? ', 0);
INSERT INTO `quest_request_items` VALUES (9933, 1, 0, 0, 0, 'Peace you say? ', 0);
INSERT INTO `quest_request_items` VALUES (9934, 1, 0, 0, 0, '<Garrosh seems unmoved by the offering.>', 0);
INSERT INTO `quest_request_items` VALUES (9935, 1, 6, 0, 0, 'Are you here to collect a reward?', 0);
INSERT INTO `quest_request_items` VALUES (9936, 1, 0, 0, 0, 'Are you here to collect a reward?', 0);
INSERT INTO `quest_request_items` VALUES (9937, 0, 0, 0, 0, 'For the Mag\'har!', 0);
INSERT INTO `quest_request_items` VALUES (9938, 0, 0, 0, 0, 'Waste no time! Durn must be stopped!', 0);
INSERT INTO `quest_request_items` VALUES (9939, 1, 0, 0, 0, 'Are you here to collect a reward?', 0);
INSERT INTO `quest_request_items` VALUES (9940, 1, 0, 0, 0, 'Are you here to collect a reward?', 0);
INSERT INTO `quest_request_items` VALUES (9944, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (9945, 1, 0, 0, 0, 'This is merely one step in a long process. Ogres, like dogs, must be shown the difference between right and wrong. Telling them does no good.', 0);
INSERT INTO `quest_request_items` VALUES (9946, 0, 0, 0, 0, 'He will undoubtedly be heavily guarded.', 0);
INSERT INTO `quest_request_items` VALUES (9948, 1, 0, 0, 0, '<Ungriz sighs.>$B$BI know it\'s a lot to ask for, $N; but bring them back alive...', 0);
INSERT INTO `quest_request_items` VALUES (9951, 1, 1, 0, 0, 'We should never have invited them to dinner.  That much is certain!$B$BOr, at least, we should have insisted upon a couple of courses before the dessert that they brought.', 0);
