INSERT INTO `quest_request_items` VALUES (9986, 1, 0, 0, 0, 'It\'s good to see you again, $N. Have you done as I asked?', 0);
INSERT INTO `quest_request_items` VALUES (9987, 1, 1, 0, 0, 'Welcome back, $N. Have you dealt with the arakkoa leaders?', 0);
INSERT INTO `quest_request_items` VALUES (9990, 1, 0, 0, 0, 'What news do you bring, $N?', 0);
INSERT INTO `quest_request_items` VALUES (9991, 1, 0, 0, 0, 'Never flown on the back of a nether drake, eh?', 0);
INSERT INTO `quest_request_items` VALUES (9992, 1, 0, 0, 0, 'I\'ve started a seed collection, but I\'m going to need some help before I have enough to send back to the park in Stormwind.', 0);
INSERT INTO `quest_request_items` VALUES (9993, 1, 0, 0, 0, 'Do you have any seeds for me?', 0);
INSERT INTO `quest_request_items` VALUES (9994, 1, 0, 0, 0, 'You look concerned.  What is it, $c?', 0);
INSERT INTO `quest_request_items` VALUES (9995, 1, 0, 0, 0, 'You look concerned.  What is it, $C?', 0);
INSERT INTO `quest_request_items` VALUES (9996, 1, 0, 0, 0, 'Report. What news do you bring from Firewing Point?', 0);
INSERT INTO `quest_request_items` VALUES (9997, 1, 0, 0, 0, 'Report. What news do you bring from Firewing Point?', 0);
INSERT INTO `quest_request_items` VALUES (9998, 1, 0, 0, 0, 'I take it Bertelm sent you to hamper the efforts of their peons?', 0);
INSERT INTO `quest_request_items` VALUES (9999, 1, 0, 0, 0, 'Back so soon?', 0);
INSERT INTO `quest_request_items` VALUES (10000, 1, 1, 0, 0, 'I was about to send a message back to Rokag recommending an attack, but I see that won\'t be necessary.', 0);
INSERT INTO `quest_request_items` VALUES (10001, 1, 0, 0, 0, 'Avoid the overseers at all costs. They must not be let on to your presence. The results could be disastrous! ', 0);
INSERT INTO `quest_request_items` VALUES (10002, 1, 0, 0, 0, 'The activity in the camp continues to escalate. Have you carried out your mission?', 0);
INSERT INTO `quest_request_items` VALUES (10003, 1, 0, 0, 0, 'The longer we permit them to make preparations, the more difficult it will be to root them out. Have you killed the liaison?', 0);
INSERT INTO `quest_request_items` VALUES (10005, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (10006, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (10007, 1, 0, 0, 0, 'Was your attack successful?', 0);
INSERT INTO `quest_request_items` VALUES (10008, 1, 1, 0, 0, 'Tell me I wasn\'t wrong to trust you with this mission...', 0);
INSERT INTO `quest_request_items` VALUES (10009, 0, 0, 0, 0, 'You no come back without Sal\'salabim golds!', 0);
INSERT INTO `quest_request_items` VALUES (10011, 0, 0, 0, 0, 'Is it done?', 0);
INSERT INTO `quest_request_items` VALUES (10012, 1, 0, 0, 0, 'What is it you have there, $N?', 0);
INSERT INTO `quest_request_items` VALUES (10013, 1, 0, 0, 0, 'What is it you have there, $N?', 0);
INSERT INTO `quest_request_items` VALUES (10016, 1, 0, 0, 0, 'I see you\'ve managed to survive the rigors of the forest. Have you brought any tails for my cloak?', 0);
INSERT INTO `quest_request_items` VALUES (10017, 1, 0, 0, 0, 'Have you obtained the venom sacs, $N? These refugees can\'t wait much longer.', 0);
INSERT INTO `quest_request_items` VALUES (10018, 1, 0, 0, 0, 'Were you able to find all the pelts that I need?', 0);
INSERT INTO `quest_request_items` VALUES (10019, 1, 0, 0, 0, 'Many refugees still need treatment for their poisonous wounds. Have you brought more venom sacs for me to use, $N?', 0);
INSERT INTO `quest_request_items` VALUES (10020, 1, 0, 0, 0, 'Have you obtained the blood, $N?  Without it, Zahlia may never see her son again.', 0);
INSERT INTO `quest_request_items` VALUES (10021, 1, 0, 0, 0, 'Have you completed your task, $N?', 0);
INSERT INTO `quest_request_items` VALUES (10022, 1, 0, 0, 0, 'Has old Ironjaw gotten the better of you, too, friend? Or have you managed to outsmart the old fellow?', 0);
INSERT INTO `quest_request_items` VALUES (10023, 1, 0, 0, 0, 'The spirit awaits, $N. Have you the pelt of the wolf patriarch?', 0);
INSERT INTO `quest_request_items` VALUES (10024, 1, 0, 0, 0, 'Have you obtained the basilisk eyes for Voren\'thal\'s elixir?', 0);
INSERT INTO `quest_request_items` VALUES (10025, 1, 0, 0, 0, 'It takes many basilisk eyes to make even a small dose of Voren\'thal\'s elixir. Bringing me more would certainly incur the Scryers\' favor.', 0);
INSERT INTO `quest_request_items` VALUES (10026, 1, 0, 0, 0, 'Have you cleared the warp stalkers from the vicinity of the stronghold?', 0);
INSERT INTO `quest_request_items` VALUES (10027, 1, 1, 0, 0, 'Keb\'ezil wants to know if it\'s safe to hurl a fireball without embarrassing himself again.$B$B<The imp makes a few clicking sounds and the warlock turns toward him.>$B$BNo, it wasn\'t my shadow bolt! Take it back, you overgrown pixy!', 0);
INSERT INTO `quest_request_items` VALUES (10028, 1, 0, 0, 0, 'Were you able to find any intact vessels?', 0);
INSERT INTO `quest_request_items` VALUES (10030, 1, 0, 0, 0, 'What\'s this?  Who are you?', 0);
INSERT INTO `quest_request_items` VALUES (10031, 1, 0, 0, 0, '$N? What brings you back to me? Did you deliver the bones of the restless to Ramdor as I requested?', 0);
INSERT INTO `quest_request_items` VALUES (10033, 1, 0, 0, 0, 'I suppose you\'re after the bonelasher bounty too?', 0);
INSERT INTO `quest_request_items` VALUES (10034, 1, 6, 0, 0, 'Is that bonelasher blood on your hands that I smell?', 0);
INSERT INTO `quest_request_items` VALUES (10035, 1, 0, 0, 0, 'You have the feather?  Am I to reward you for your foolhardy bravery?', 0);
INSERT INTO `quest_request_items` VALUES (10036, 1, 0, 0, 0, 'Did you kill him? Is Torgos dead?!$B$BYou have proof?', 0);
INSERT INTO `quest_request_items` VALUES (10037, 1, 0, 0, 0, '<Seth tugs on your cloak.>$B$BDid you find the eels? Didja?', 0);
INSERT INTO `quest_request_items` VALUES (10038, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (10039, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (10040, 1, 0, 0, 0, 'You speak with all of them yet? What did they have to say?', 0);
INSERT INTO `quest_request_items` VALUES (10041, 1, 0, 0, 0, 'You speak with all of them yet? What did they have to say?', 0);
INSERT INTO `quest_request_items` VALUES (10042, 1, 0, 0, 0, 'Well, what did ye find out, $glad:lass;?', 0);
INSERT INTO `quest_request_items` VALUES (10043, 1, 0, 0, 0, 'Well, what did you find out?', 0);
INSERT INTO `quest_request_items` VALUES (10044, 1, 0, 0, 0, 'Please, sit down, dear.', 0);
INSERT INTO `quest_request_items` VALUES (10045, 1, 0, 0, 0, 'Hello, dear.', 0);
INSERT INTO `quest_request_items` VALUES (10047, 1, 0, 0, 0, 'The cries seem somehow... fainter than they were before. The Light grant it\'s more than wishful thinking.', 0);
INSERT INTO `quest_request_items` VALUES (10050, 1, 0, 0, 0, 'What I asked of you is a terrible thing to request of anyone. But what you must understand is many of the men won\'t go near the ruins. The memories and the pain are too fresh, but we can\'t allow the spirits to continue to wander.$B$BThe longer they persist, the more they shed their remaining ties to their past life and give themselves over to malevolence.', 0);
INSERT INTO `quest_request_items` VALUES (10051, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (10052, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (10055, 1, 0, 0, 0, 'Were you able to find any usable materials out there underneath all the dust, ash, and char?', 0);
INSERT INTO `quest_request_items` VALUES (10057, 1, 0, 0, 0, 'Were you able to overcome the officers at the ruins?', 0);
INSERT INTO `quest_request_items` VALUES (10058, 25, 0, 0, 0, 'I thought I asked you not to bother me. What is it?', 0);
INSERT INTO `quest_request_items` VALUES (10063, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (10064, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (10065, 1, 0, 0, 0, 'Have you managed to clear some of the ravagers in the area?', 0);
INSERT INTO `quest_request_items` VALUES (10066, 1, 0, 0, 0, 'Have you managed to clear any of the mutated tanglers?', 0);
INSERT INTO `quest_request_items` VALUES (10067, 1, 0, 0, 0, 'Have you begun to clear the fouled water spirits?', 0);
INSERT INTO `quest_request_items` VALUES (8326, 0, 0, 0, 0, 'It gives me no pleasure to ask you to destroy these beasts.  While in times past we have lived in relative harmony with woodland creatures, these are different times.  The first order of business for all sin\'dorei is survival; make sure you remember that.', 20779);
INSERT INTO `quest_request_items` VALUES (10074, 1, 0, 0, 0, 'The crystalline powder from the mountain is infused within the earthen core of this region.$B$B', 0);
INSERT INTO `quest_request_items` VALUES (10075, 0, 0, 0, 0, 'This base was the primary draenei research facility for the study of naaru crystals.$B$BWhat we have discovered is a veritable goldmine of research notes about the crystals from the \"diamond\" mountain, Oshu\'gun. It would seem that everything in this region carries with it some residual powder of the Oshu\'gun crystals. If you find any powder while adventuring in this region, return it to me and I shall reward you with a token of my appreciation which you may use to make purchases from the quartermaster.', 0);
INSERT INTO `quest_request_items` VALUES (10076, 1, 0, 0, 0, 'The crystalline powder from the mountain is infused within the earthen core of this region.', 0);
INSERT INTO `quest_request_items` VALUES (10077, 1, 0, 0, 0, 'This base was the primary draenei research facility for the study of naaru crystals.$B$BWhat we have discovered is a veritable goldmine of research notes about the crystals from the \"diamond\" mountain, Oshu\'gun. It would seem that everything in this region carries with it some residual powder of the Oshu\'gun crystals. If you find any powder while adventuring in this region, return it to me and I shall reward you with a token of my appreciation which you may use to make purchases from the quartermaster.', 0);
INSERT INTO `quest_request_items` VALUES (10078, 1, 0, 0, 0, 'You seem to be in good spirits. Has burning Horde siege machinery put the spring back in your step?', 0);
INSERT INTO `quest_request_items` VALUES (10079, 1, 0, 0, 0, 'Tell me the shaking\'s over and my boys can get back to work... please?', 0);
INSERT INTO `quest_request_items` VALUES (10081, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (10082, 1, 0, 0, 0, 'You do not need a potion to see spirits, $N.', 0);
INSERT INTO `quest_request_items` VALUES (10085, 1, 0, 0, 0, 'Move as the wind, $N.', 0);
INSERT INTO `quest_request_items` VALUES (10086, 0, 0, 0, 0, 'So how did the salvage mission go?  Did you find anything useful?', 0);
INSERT INTO `quest_request_items` VALUES (10087, 0, 0, 0, 0, 'I\'ve been looking for the telltale signs of burning cannons -- smoke plumes, or the cries of burning fel orcs...$B$BAny luck on your mission?', 0);
INSERT INTO `quest_request_items` VALUES (10093, 1, 0, 0, 0, '', 0);
INSERT INTO `quest_request_items` VALUES (10096, 1, 6, 0, 0, 'What?  Back so soon?  How did it go?', 0);
INSERT INTO `quest_request_items` VALUES (10099, 1, 0, 0, 0, 'I\'m almost afraid to ask. Seeing you come out of the mine alive means one of two things: either you killed the bastard, or you lost your nerve. Well, which is it?', 0);
INSERT INTO `quest_request_items` VALUES (10101, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (10102, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (10103, 1, 0, 0, 0, '', 0);
INSERT INTO `quest_request_items` VALUES (10104, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (10105, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (10106, 0, 0, 0, 0, '$N, return to me when you have aided in the capture of each of the forts!', 0);
INSERT INTO `quest_request_items` VALUES (10108, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (10109, 6, 0, 0, 0, 'Got gas?', 0);
INSERT INTO `quest_request_items` VALUES (10110, 0, 0, 0, 0, 'Return to me after you have helped capture each fort!', 0);
INSERT INTO `quest_request_items` VALUES (10111, 1, 0, 0, 0, 'Did I mention my love for eggs? Completion', 0);
INSERT INTO `quest_request_items` VALUES (10112, 1, 0, 0, 0, 'Hmm, is my memory improving?', 0);
INSERT INTO `quest_request_items` VALUES (10113, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (10114, 1, 0, 0, 0, '', 0);
INSERT INTO `quest_request_items` VALUES (10115, 1, 0, 0, 0, 'Have you seen what the Daggerfen have done? Now you understand why we must isolate them from the other Lost Ones.', 0);
INSERT INTO `quest_request_items` VALUES (10116, 1, 0, 0, 0, 'So you wish to claim the bounty on Chieftain Mummaki?', 0);
INSERT INTO `quest_request_items` VALUES (10117, 1, 0, 0, 0, 'So you wish to claim the bounty on Chieftain Mummaki?', 0);
INSERT INTO `quest_request_items` VALUES (10118, 1, 1, 0, 0, 'The only thing the Daggerfen understand is swift and brutal action. They should know that we will not let them have their way.', 0);
INSERT INTO `quest_request_items` VALUES (10119, 1, 0, 0, 0, '', 0);
INSERT INTO `quest_request_items` VALUES (10120, 0, 0, 0, 0, 'I saw you speaking with Orion - it must be important business...', 0);
INSERT INTO `quest_request_items` VALUES (10123, 0, 0, 0, 0, 'Thrallmar is safe so long as the arcanist and his bloodmages remain at the front.', 0);
INSERT INTO `quest_request_items` VALUES (10129, 0, 0, 0, 0, 'If you missed during your first pass, take another...', 0);
INSERT INTO `quest_request_items` VALUES (10132, 1, 0, 0, 0, 'You have eliminated the colossal menace?', 0);
INSERT INTO `quest_request_items` VALUES (10134, 1, 0, 0, 0, 'That\'s a big crystal you have there.  Did you get that while you were up at the ridge killing colossi?$B$BLet me take a look at it.', 0);
INSERT INTO `quest_request_items` VALUES (10136, 1, 0, 0, 0, 'Destiny calls, $N. Arazzius the Cruel has eluded justice long enough. Let his death be a message to all those who would oppose the Horde. You are next.', 0);
INSERT INTO `quest_request_items` VALUES (10140, 5, 0, 0, 0, 'Thank the Light you made it here safely. Honor Hold needs all the seasoned warriors it can muster.', 0);
INSERT INTO `quest_request_items` VALUES (10141, 1, 0, 0, 0, '', 0);
INSERT INTO `quest_request_items` VALUES (10142, 1, 0, 0, 0, 'Honor Hold is safe so long as the archers guard our skies.', 0);
INSERT INTO `quest_request_items` VALUES (10143, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (10144, 1, 0, 0, 0, 'I wish I could be there to watch the fire display.  Those explosives pack a big punch!', 0);
INSERT INTO `quest_request_items` VALUES (10146, 0, 0, 0, 0, 'This is a vital mission, $N.  It\'s one I would not trust to just anyone.', 0);
INSERT INTO `quest_request_items` VALUES (10159, 1, 0, 0, 0, 'How fares your attempt to clear Thornfang Hill?', 0);
INSERT INTO `quest_request_items` VALUES (10160, 1, 0, 0, 0, '', 0);
INSERT INTO `quest_request_items` VALUES (10161, 1, 0, 0, 0, 'Did you find enough of the parts? If not, I might have to use that frying pan Legassi keeps waving around. Then, I\'d have to rename the zeppelin the \"Flying Skillet.\"', 0);
INSERT INTO `quest_request_items` VALUES (10162, 0, 0, 0, 0, 'Show them that not even the skies are safe!', 0);
INSERT INTO `quest_request_items` VALUES (10163, 5, 0, 0, 0, 'Show them that not even the skies are safe!', 0);
INSERT INTO `quest_request_items` VALUES (10166, 1, 0, 0, 0, 'You\'ve returned, $N.  Haven\'t you done enough?', 0);
INSERT INTO `quest_request_items` VALUES (10167, 1, 0, 0, 0, '<D\'ore\'s spirit hums.>$B$BGreetings, $N.', 0);
INSERT INTO `quest_request_items` VALUES (10168, 1, 0, 0, 0, '<Mother Kashur is visibly upset.>These naaru are wise beyond all manner of life on this world. ', 0);
INSERT INTO `quest_request_items` VALUES (10170, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (10171, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (10172, 1, 0, 0, 0, '<Greatmother Geyah is lying down.>', 0);
INSERT INTO `quest_request_items` VALUES (10173, 0, 0, 0, 0, 'I will not rest until Archmage Vargoth\'s staff is restored to him. Were you able to wrest it from the demon\'s grasp?', 0);
INSERT INTO `quest_request_items` VALUES (10176, 0, 0, 0, 0, 'At last, we meet.', 0);
INSERT INTO `quest_request_items` VALUES (10180, 1, 0, 0, 0, '', 0);
INSERT INTO `quest_request_items` VALUES (10182, 0, 0, 0, 0, 'What have you found, $N?', 0);
INSERT INTO `quest_request_items` VALUES (10183, 1, 0, 0, 0, 'Busy, busy.  No time to waste!  There\'s a rocket ship to be built!$B$BWhat do you want?', 0);
INSERT INTO `quest_request_items` VALUES (10184, 0, 0, 0, 0, 'It pains me to know there is so little to be done for our comrades.', 0);
INSERT INTO `quest_request_items` VALUES (10185, 1, 0, 0, 0, 'Have you managed to clear out some of the mana creatures from the village ruins?', 0);
INSERT INTO `quest_request_items` VALUES (10186, 1, 0, 0, 0, 'Does it look like the X-52 Nether-Rocket is ready for liftoff?  Am I cruising the Twisting Nether in the comfort of my command chair?  Exploiting strange new worlds?  Seeking out new technology and business opportunities?  Boldly going where no goblin has gone before?$B$BNo!$B$BSo, I hope you have those crystals that I sent you after.', 0);
INSERT INTO `quest_request_items` VALUES (10188, 0, 0, 0, 0, 'If I am to be free of the tower\'s curse, I must have the sigil.', 0);
INSERT INTO `quest_request_items` VALUES (10189, 1, 0, 0, 0, 'Did you obtain the items I asked of you, $N?', 0);
INSERT INTO `quest_request_items` VALUES (10190, 0, 0, 0, 0, 'Have you recharged the battery in that blaster yet?', 0);
INSERT INTO `quest_request_items` VALUES (10191, 1, 0, 0, 0, 'Back so soon?$B$BIs everything okay with the Maxx A. Million Mk. V?!', 0);
INSERT INTO `quest_request_items` VALUES (10192, 0, 0, 0, 0, 'Have you brought the book?', 0);
INSERT INTO `quest_request_items` VALUES (10193, 1, 0, 0, 0, 'How goes it, $N? Got any news? What have you done for me lately?', 0);
INSERT INTO `quest_request_items` VALUES (10194, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (10197, 1, 0, 0, 0, 'Have you obtained the outfit pieces I asked of you?', 0);
INSERT INTO `quest_request_items` VALUES (10198, 1, 0, 0, 0, 'How goes the eavesdropping, $N? You\'d best hurry. I\'m getting antsy here.', 0);
INSERT INTO `quest_request_items` VALUES (10199, 0, 0, 0, 0, 'If I can drain the venom from enough of those nether ray stingers, I think we might just have the most explosive fuel ever devised!', 0);
INSERT INTO `quest_request_items` VALUES (10200, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (10201, 1, 0, 0, 0, 'Well, do you have the test results?', 0);
INSERT INTO `quest_request_items` VALUES (10202, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (10203, 0, 0, 0, 0, 'Isn\'t this place great? And everyday yields something new and exotic!$B$BWhat a stroke of luck that we nearly destroyed the town digging down here.', 0);
INSERT INTO `quest_request_items` VALUES (10204, 1, 0, 0, 0, 'Did you investigate the bloodgems used by the Sunfury magisters, $N?', 0);
INSERT INTO `quest_request_items` VALUES (10205, 0, 0, 0, 0, 'It\'s over then? Warp-Raider Nesaad is dead?', 0);
INSERT INTO `quest_request_items` VALUES (10206, 1, 0, 0, 0, 'Back with their tech so soon?  You better not just be pulling my leg here, $C.$B$BThe success of the X-52 project out here depends upon you getting us that stuff a.s.a.p.!', 0);
INSERT INTO `quest_request_items` VALUES (10208, 0, 0, 0, 0, 'The Legion won\'t be prepared for the surprise you have for them, eh $N?', 0);
INSERT INTO `quest_request_items` VALUES (10209, 0, 0, 0, 0, 'Have you secured the Stone of Glacius?', 0);
INSERT INTO `quest_request_items` VALUES (10210, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (10211, 1, 0, 0, 0, 'The stories of men are ephemeral, $N. The stories of cities last well into the years.', 0);
INSERT INTO `quest_request_items` VALUES (10213, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (10220, 0, 0, 0, 0, 'Have you taught the Unyielding the consequences of defying the Forsaken?  ', 0);
INSERT INTO `quest_request_items` VALUES (10221, 1, 0, 0, 0, 'Have you saved us from the depredations of Dr. Boom? I promise this time there won\'t be any exploding wagons, though that was a lot of fun!$B$BWe really should do that more often.', 0);
INSERT INTO `quest_request_items` VALUES (10222, 1, 0, 0, 0, 'Is it done, $N? Have you avenged the fallen of Kirin\'Var Village?', 0);
INSERT INTO `quest_request_items` VALUES (10223, 1, 0, 0, 0, 'Have you finished the task, $N?', 0);
INSERT INTO `quest_request_items` VALUES (10224, 0, 0, 0, 0, 'You have all of those essences yet?', 0);
INSERT INTO `quest_request_items` VALUES (10225, 0, 0, 0, 0, 'Um, hi. I\'m a little busy here. What\'s that you have there?', 0);
INSERT INTO `quest_request_items` VALUES (10226, 0, 0, 0, 0, 'You have the power from those elementals yet? Oh, don\'t hurry or anything. It\'s just my life and career on the line.', 0);
INSERT INTO `quest_request_items` VALUES (10227, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (10228, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (10229, 0, 0, 0, 0, 'What is this?  A book carried by an Unyielding spirit?  How strange.  Let me study it.  Perhaps I can learn the history of the Unyielding.', 0);
INSERT INTO `quest_request_items` VALUES (10230, 0, 0, 0, 0, 'We must help the Unyielding soon.  If we can convince them to join us the Horde will have gained a powerful ally.  ', 0);
INSERT INTO `quest_request_items` VALUES (10231, 1, 0, 0, 0, 'Whaddya want?', 0);
INSERT INTO `quest_request_items` VALUES (10232, 0, 0, 0, 0, 'I sure hope that you\'re up to that tussle with the Burning Legion.$B$BThere aren\'t many things that I hate worse than those that renege on a deal, or <cough> gnomes, but demons are certainly one of them!', 0);
INSERT INTO `quest_request_items` VALUES (10233, 0, 0, 0, 0, 'Smoke rises from the Sunfury Hold fortifications. Is that your handiwork?', 0);
INSERT INTO `quest_request_items` VALUES (10234, 0, 0, 0, 0, 'You have those parts yet?  With the Burning Legion moving in on each of our claims around here one at a time, we unfortunately have little time.', 0);
INSERT INTO `quest_request_items` VALUES (10235, 0, 0, 0, 0, 'Dealt with Doomclaw yet?', 0);
INSERT INTO `quest_request_items` VALUES (10236, 1, 0, 0, 0, 'Did you find some spare parts for my shredder?  I really want to get it fixed so I can get back to mining.  I have to make a living somehow, if only so I can buy a one-way ticket back to Booty Bay.  ', 0);
INSERT INTO `quest_request_items` VALUES (10238, 1, 0, 0, 0, 'Have you saved my peons yet? Please hurry, I\'m afraid they are going to be eaten and they probably won\'t taste very good. If they taste really bad the fel orcs might get really mad at me.', 0);
INSERT INTO `quest_request_items` VALUES (10239, 0, 0, 0, 0, 'Were you able to gather any of their energy sources?', 0);
INSERT INTO `quest_request_items` VALUES (10240, 0, 0, 0, 0, 'Have you activated all the runes? I\'m eager to see if we can\'t improve upon the blood elf method here. Anything one of Kael\'thas\'s men can do. real Kirin Tor can do better.', 0);
INSERT INTO `quest_request_items` VALUES (10241, 0, 0, 0, 0, 'Have you completed your task, $N?', 0);
INSERT INTO `quest_request_items` VALUES (10242, 1, 0, 0, 0, 'Welcome to Spinebreaker Post.  How might my talents serve you?', 0);
INSERT INTO `quest_request_items` VALUES (10243, 0, 0, 0, 0, 'The symbols scrolling in front of you look alien and unintelligible.', 0);
INSERT INTO `quest_request_items` VALUES (10245, 0, 0, 0, 0, 'Let us see what you\'ve brought to us, $N.', 0);
INSERT INTO `quest_request_items` VALUES (10246, 0, 0, 0, 0, 'Have you completed your task, $N?', 0);
INSERT INTO `quest_request_items` VALUES (10250, 0, 0, 0, 0, 'Have you blown the horn by the siege tower yet?  We must do this soon.  Who knows when Thrallmar will have need of the Unyielding.  ', 0);
INSERT INTO `quest_request_items` VALUES (10251, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (10252, 1, 0, 0, 0, 'Levixus\'s army grows in size and power with each second that passes. You must hurry.', 0);
INSERT INTO `quest_request_items` VALUES (10253, 1, 0, 0, 0, 'Who are you and what do you want? Oh... It\'s YOU.', 0);
INSERT INTO `quest_request_items` VALUES (10254, 1, 0, 0, 0, '', 0);
INSERT INTO `quest_request_items` VALUES (10255, 0, 0, 0, 0, 'Do you have the results of the experiment?', 0);
INSERT INTO `quest_request_items` VALUES (10257, 0, 0, 0, 0, 'Did you obtain the keystone?', 0);
INSERT INTO `quest_request_items` VALUES (10258, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (10261, 0, 0, 0, 0, 'I hope that you\'re here concerning the wanted ad that I placed!', 0);
INSERT INTO `quest_request_items` VALUES (10262, 0, 0, 0, 0, 'Is it done? Have you wiped the Zaxxis scum from the face of Outland and brought me their insignia?', 0);
INSERT INTO `quest_request_items` VALUES (10265, 0, 0, 0, 0, 'I get the impression that the prince is looking for a particular crystal, but I\'m not certain what it is.$B$BHave you already recovered the one from the dreadlord?', 0);
INSERT INTO `quest_request_items` VALUES (10267, 0, 0, 0, 0, 'Do you have possession of the surveying equipment which the blood elves defaulted on?', 0);
INSERT INTO `quest_request_items` VALUES (10268, 0, 0, 0, 0, 'Approach.', 0);
INSERT INTO `quest_request_items` VALUES (10269, 0, 0, 0, 0, 'Hello, traveler. You come to Hazzin because you are in need of my goods?', 0);
INSERT INTO `quest_request_items` VALUES (10270, 0, 0, 0, 0, 'Are you prepared to discuss business now, $N?', 0);
INSERT INTO `quest_request_items` VALUES (10271, 1, 0, 0, 0, 'I\'m certain you have many questions, all of which will be answered in due time.', 0);
INSERT INTO `quest_request_items` VALUES (10272, 1, 0, 0, 0, 'Were you able to gather the eggs?', 0);
INSERT INTO `quest_request_items` VALUES (10273, 1, 0, 0, 0, 'This Marid is a fool, or shall I say, was a fool.', 0);
INSERT INTO `quest_request_items` VALUES (10274, 1, 0, 0, 0, 'Have you challenged and defeated Veraku?', 0);
INSERT INTO `quest_request_items` VALUES (10275, 0, 0, 0, 0, 'You wish to buy or sell?', 0);
INSERT INTO `quest_request_items` VALUES (10276, 0, 0, 0, 0, 'Hello again, my friend. How fare your efforts to find the crystal?', 0);
INSERT INTO `quest_request_items` VALUES (10277, 1, 1, 0, 0, 'Do not return until you have at least a rudimentary understanding of what is happening.', 0);
INSERT INTO `quest_request_items` VALUES (10278, 0, 0, 0, 0, 'Have you managed to use my experimental device yet?  I think my unstable warp rift generator will work out there so close to the abyss. ', 0);
INSERT INTO `quest_request_items` VALUES (10279, 1, 0, 0, 0, '', 0);
INSERT INTO `quest_request_items` VALUES (10280, 1, 0, 0, 0, '<The musical tinkling of the naaru\'s speech fills your mind.>$B$BI sense that you have something of great power with you, $R.  What is it that you bring to me?', 0);
INSERT INTO `quest_request_items` VALUES (10281, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (10282, 1, 0, 0, 0, '', 0);
INSERT INTO `quest_request_items` VALUES (10286, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (10287, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (10288, 5, 0, 0, 0, 'I saw you speaking with Duron over there.  You must be on an important mission!', 0);
INSERT INTO `quest_request_items` VALUES (10289, 0, 0, 0, 0, 'A report from Orion? Give it here, quickly!$B$B<Frowning, General Krakork quickly scans the tattered document.>$B$BDamn, it\'s just as we feared. Orion and his warriors could be overrun at any moment!', 0);
INSERT INTO `quest_request_items` VALUES (10290, 0, 0, 0, 0, 'I understand the dangers of gathering farahlite are great, but so are the opportunities. A merchant who isn\'t willing to take the risk may as well close his doors and save himself the trouble.', 0);
INSERT INTO `quest_request_items` VALUES (10293, 0, 0, 0, 0, 'You\'re still alive, which means you\'ve either got the core, or you\'ve changed your mind. Which is it?', 0);
INSERT INTO `quest_request_items` VALUES (10294, 0, 0, 0, 0, 'You have done it then?  You have the shards that we need?  ', 0);
INSERT INTO `quest_request_items` VALUES (10295, 0, 0, 0, 0, 'Have you destroyed Void Baron Galaxis yet?  ', 0);
INSERT INTO `quest_request_items` VALUES (10297, 1, 0, 0, 0, 'You may only use one chrono-beacon per pocket of time.', 0);
INSERT INTO `quest_request_items` VALUES (10299, 0, 0, 0, 0, 'Have you succeeded in your task, $N?', 0);
INSERT INTO `quest_request_items` VALUES (10300, 0, 0, 0, 0, 'Have you managed to find some suitable crystals for a new headpiece for the archmage\'s staff?', 0);
INSERT INTO `quest_request_items` VALUES (10301, 0, 0, 0, 0, 'Have you recovered the oculus?', 0);
INSERT INTO `quest_request_items` VALUES (10302, 1, 0, 0, 0, 'I am working on a solution I think may be effective with the strange creatures. I just need more time.', 0);
INSERT INTO `quest_request_items` VALUES (10303, 1, 0, 0, 0, 'There are too many of them... We must whittle them down or else the survivors will have no chance against them.', 0);
INSERT INTO `quest_request_items` VALUES (10304, 1, 0, 0, 0, '', 0);
INSERT INTO `quest_request_items` VALUES (10305, 0, 0, 0, 0, 'What have you found, $N?', 0);
INSERT INTO `quest_request_items` VALUES (10306, 0, 0, 0, 0, 'What have you found, $N?', 0);
INSERT INTO `quest_request_items` VALUES (10307, 0, 0, 0, 0, 'What have you found, $N?', 0);
INSERT INTO `quest_request_items` VALUES (10308, 1, 0, 0, 0, 'The Consortium is keenly interested in wiping out all of the Zaxxis rebels at the Heap to the south.$B$B$C, if you would like to ingratiate yourself further with us, go back down there and bring back more of their affiliation insignia.', 0);
INSERT INTO `quest_request_items` VALUES (10309, 0, 0, 0, 0, 'My eyesight must be going from all of the nether radiation. You have that fel reaver\'s heart yet?', 0);
INSERT INTO `quest_request_items` VALUES (10310, 0, 0, 0, 0, 'How may I help you?', 0);
INSERT INTO `quest_request_items` VALUES (10312, 0, 0, 0, 0, 'Were you able to find the registry?', 0);
INSERT INTO `quest_request_items` VALUES (10313, 0, 0, 0, 0, 'Have you performed the measurements I asked for, $N?', 0);
INSERT INTO `quest_request_items` VALUES (10314, 0, 0, 0, 0, 'What have you discovered, $N?', 0);
INSERT INTO `quest_request_items` VALUES (10315, 0, 0, 0, 0, 'How many nethermancers have you managed to fell?', 0);
INSERT INTO `quest_request_items` VALUES (10318, 0, 0, 0, 0, 'What news do you bring from Manaforge Ara? Did you deal with Foreman Sundown?', 0);
INSERT INTO `quest_request_items` VALUES (10319, 0, 0, 0, 0, 'You must recover Naberius\'s phylactery before we can attack him. Otherwise, he will be invulnerable.', 0);
INSERT INTO `quest_request_items` VALUES (10320, 0, 0, 0, 0, 'Have you destroyed the lich of that wretched Naberius?', 0);
INSERT INTO `quest_request_items` VALUES (10321, 0, 0, 0, 0, 'Have you completed your task yet, $N?', 0);
INSERT INTO `quest_request_items` VALUES (10322, 0, 0, 0, 0, 'Have you completed your task yet, $N?', 0);
INSERT INTO `quest_request_items` VALUES (10323, 0, 0, 0, 0, 'Have you completed the task I asked of you, $N?', 0);
INSERT INTO `quest_request_items` VALUES (10324, 1, 0, 0, 0, 'The buck hides are rugged and strong, making for very tough flesh - almost inedible, but we can use them for other purposes.', 0);
INSERT INTO `quest_request_items` VALUES (10325, 1, 0, 0, 0, 'Do you bear any of our enemies\' insignias, $N?', 0);
INSERT INTO `quest_request_items` VALUES (10326, 1, 0, 0, 0, 'Do you bear more of our enemies\' insignias, $C?  The Burning Legion continues to be a threat and we continue to pursue them anywhere that we find them.', 0);
INSERT INTO `quest_request_items` VALUES (10327, 0, 0, 0, 0, 'I\'ll take any leftover marks you might have.  Do not worry; your actions will be duly noted.', 0);
INSERT INTO `quest_request_items` VALUES (10328, 1, 0, 0, 0, 'Did you have any luck with the briefings from Manaforge Duro?', 0);
INSERT INTO `quest_request_items` VALUES (10329, 1, 0, 0, 0, 'Did the plan work? I was half-expecting you to get blown up, to tell you the truth.', 0);
INSERT INTO `quest_request_items` VALUES (10330, 1, 0, 0, 0, 'Have you completed your task yet, $N?', 0);
INSERT INTO `quest_request_items` VALUES (10331, 0, 0, 0, 0, 'Did you have any luck finding my hammer?', 0);
INSERT INTO `quest_request_items` VALUES (10332, 0, 0, 0, 0, '<There appear to be tears in the apprentice\'s eyes as he speaks.>$B$BHave you... dealt with Master Rhonsus?', 0);
INSERT INTO `quest_request_items` VALUES (10334, 0, 0, 0, 0, '<The cow looks at you distrustfully, but stands its ground.>', 0);
INSERT INTO `quest_request_items` VALUES (10335, 0, 0, 0, 0, 'That was fast. You must be a very efficient person to have placed all three of those markers already.', 0);
INSERT INTO `quest_request_items` VALUES (10336, 0, 0, 0, 0, 'Have you killed all of the demons at the Ruins of Farahlon yet?', 0);
INSERT INTO `quest_request_items` VALUES (10338, 1, 0, 0, 0, 'Did you complete the shut down? You should hurry before I change my mind about the whole deal.', 0);
INSERT INTO `quest_request_items` VALUES (10339, 0, 0, 0, 0, 'Well done, fleshling...', 0);
INSERT INTO `quest_request_items` VALUES (10340, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (10341, 1, 0, 0, 0, 'Have you completed your task, $N?', 0);
INSERT INTO `quest_request_items` VALUES (10342, 0, 0, 0, 0, 'You have enough of that shale yet? I\'m going to need a lot of it to extract the amount of oil that\'ll be needed for the X-52 Nether Rocket\'s fuel.', 0);
INSERT INTO `quest_request_items` VALUES (10343, 0, 0, 0, 0, 'Did you find the remains of the weapon Kael\'thas used against the village?', 0);
INSERT INTO `quest_request_items` VALUES (10344, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (10345, 1, 6, 0, 0, 'Any luck in finding Ya-six?', 0);
INSERT INTO `quest_request_items` VALUES (10346, 1, 0, 0, 0, 'There are still plenty of targets on the Abyssal Shelf, $n.  Grab a gryphon when you\'re ready for another run.', 0);
INSERT INTO `quest_request_items` VALUES (10347, 5, 5, 0, 0, 'The Legion must not think they are safe high atop the Abyssal Shelf.  We must hit them repeatably!', 0);
INSERT INTO `quest_request_items` VALUES (10348, 0, 0, 0, 0, 'Have you managed to gather the herbs?', 0);
INSERT INTO `quest_request_items` VALUES (10349, 6, 0, 0, 0, 'Is that a piece from the giant crystal shard up on Fallen Sky Ridge?', 0);
INSERT INTO `quest_request_items` VALUES (10350, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (10351, 6, 6, 0, 0, 'Did it work?  What did you learn?  Did you find out who or what is behind what happened at Fallen Sky Ridge?', 0);
INSERT INTO `quest_request_items` VALUES (10353, 1, 0, 0, 0, 'Status, fleshling? What is your status?', 0);
INSERT INTO `quest_request_items` VALUES (10355, 1, 0, 0, 0, 'Were you able to gather the samples?', 0);
INSERT INTO `quest_request_items` VALUES (10365, 0, 0, 0, 0, 'Have you completed the task I asked of you, $N?', 0);
INSERT INTO `quest_request_items` VALUES (10367, 0, 0, 0, 0, 'Did you obtain the key, $N?', 0);
INSERT INTO `quest_request_items` VALUES (10368, 0, 0, 0, 0, 'Tell me, $N.  Have the Dreghood elders been released?', 0);
INSERT INTO `quest_request_items` VALUES (10369, 0, 0, 0, 0, 'Is it done, $N?  Is Arzeth dead?', 0);
INSERT INTO `quest_request_items` VALUES (10372, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (10380, 1, 0, 0, 0, 'Have you succeeded in your task, $N?', 0);
INSERT INTO `quest_request_items` VALUES (10381, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (10382, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (10384, 0, 0, 0, 0, 'Have you recovered the data cell?', 0);
INSERT INTO `quest_request_items` VALUES (10385, 0, 0, 0, 0, 'Have you recovered the relay data?', 0);
INSERT INTO `quest_request_items` VALUES (10389, 1, 0, 0, 0, 'Well, $C - have the terrorfiends been defeated?', 0);
INSERT INTO `quest_request_items` VALUES (10390, 0, 0, 0, 0, 'Move quickly, $C, we\'ve got to shut down Forge Camp: Mageddon before the demons finish its construction! \n', 0);
INSERT INTO `quest_request_items` VALUES (10391, 0, 0, 0, 0, 'The Legion could start shelling us at any time, $N! You need to destroy those blasted cannons!', 0);
INSERT INTO `quest_request_items` VALUES (10392, 0, 0, 0, 0, 'Hurry, $N. Every second that warp-portal stands open, another foul demon is brought into this world! \n', 0);
INSERT INTO `quest_request_items` VALUES (10393, 1, 0, 0, 0, 'I have little time to waste, $C. What is this you pester me with? \n', 0);
INSERT INTO `quest_request_items` VALUES (10394, 1, 0, 0, 0, 'Don\'t tell me your getting cold feet, $C? If they finish construction on that Forge Camp, you\'ll have more to worry about than just those Fel Reavers! Get to it!', 0);
INSERT INTO `quest_request_items` VALUES (10395, 0, 0, 0, 0, 'What is it, $C? I sense you\'ve found something that speaks of a coming doom.', 0);
INSERT INTO `quest_request_items` VALUES (10396, 1, 0, 0, 0, 'The Legion could start shelling the Horde at any time, $N! You need to destroy those blasted cannons! \n', 0);
INSERT INTO `quest_request_items` VALUES (10397, 0, 0, 0, 0, 'You close down that war-portal yet, $N? While you\'re here shuffling your feet, there are more demons entering Outland! ', 0);
INSERT INTO `quest_request_items` VALUES (10399, 1, 0, 0, 0, 'I sense a growing darkness within your heart, $C. Have you faced your fear? Or does it still await you at the Pools of Aggonar?  \n', 0);
INSERT INTO `quest_request_items` VALUES (10400, 1, 0, 0, 0, 'Tell me, $N, has the deed been done? Has the Legionlord been vanquished? \n', 0);
INSERT INTO `quest_request_items` VALUES (10403, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (10405, 0, 0, 0, 0, 'Have you recovered the wrapping?', 0);
INSERT INTO `quest_request_items` VALUES (10406, 0, 0, 0, 0, 'He must succeed!', 0);
INSERT INTO `quest_request_items` VALUES (10407, 0, 0, 0, 0, 'Have you obtained Socrethar\'s teleportation stone, $N?', 0);
INSERT INTO `quest_request_items` VALUES (10408, 0, 0, 0, 0, 'Is... Is he really dead? Destroyed?', 0);
INSERT INTO `quest_request_items` VALUES (10409, 1, 0, 0, 0, 'Light be with you, $C.', 0);
INSERT INTO `quest_request_items` VALUES (10410, 1, 0, 0, 0, 'What troubles you, child?', 0);
INSERT INTO `quest_request_items` VALUES (10411, 0, 0, 0, 0, 'Both the sludge and waste are extremely toxic. They will form into a congealed void horror if left unchecked!', 0);
INSERT INTO `quest_request_items` VALUES (10412, 0, 0, 0, 0, 'Go on, $N.  Speak.', 0);
INSERT INTO `quest_request_items` VALUES (10413, 1, 0, 0, 0, 'Hrm, you look a bit dirty... What have you gotten yourself into?', 0);
INSERT INTO `quest_request_items` VALUES (10414, 0, 0, 0, 0, 'Even the smallest contribution to our cause is noted, $N.  Our enemies are many, but we shall prevail!', 0);
INSERT INTO `quest_request_items` VALUES (10415, 0, 0, 0, 0, 'What have you done for us lately, $N?  The war against Kael\'thas is not going to fight itself, you know?', 0);
INSERT INTO `quest_request_items` VALUES (10416, 1, 0, 0, 0, 'Have you obtained an arcane tome yet, $N?', 0);
INSERT INTO `quest_request_items` VALUES (10417, 0, 0, 0, 0, 'Did you get the diagnostic results?', 0);
INSERT INTO `quest_request_items` VALUES (10418, 0, 0, 0, 0, 'Have you put the creatures out of their misery? Don\'t get me wrong, I\'m really an animal lover, but not when the animals are biting the hand that feeds and shelters them.', 0);
INSERT INTO `quest_request_items` VALUES (10419, 1, 0, 0, 0, 'Have you obtained any more arcane tomes, $N?', 0);
INSERT INTO `quest_request_items` VALUES (10420, 1, 0, 0, 0, 'Have you obtained any fel armaments yet, $N?', 0);
INSERT INTO `quest_request_items` VALUES (10421, 1, 0, 0, 0, 'Have you run into any fel armaments lately?  Let the Light do its work, $N.  Do not allow these foul objects to remain in this world.', 0);
INSERT INTO `quest_request_items` VALUES (10422, 0, 0, 0, 0, 'Did you find Tyralius?', 0);
INSERT INTO `quest_request_items` VALUES (10424, 0, 0, 0, 0, 'Did you get those diagnostics?', 0);
INSERT INTO `quest_request_items` VALUES (10425, 1, 0, 0, 0, 'Yes?', 0);
INSERT INTO `quest_request_items` VALUES (10426, 0, 0, 0, 0, 'Did you try focusing the dome\'s energy?', 0);
INSERT INTO `quest_request_items` VALUES (10427, 0, 0, 0, 0, 'Were you able to tag enough of the talbuk?', 0);
INSERT INTO `quest_request_items` VALUES (10428, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (10429, 0, 0, 0, 0, 'Did you get the sample from the monstrous hydra?', 0);
INSERT INTO `quest_request_items` VALUES (10430, 0, 0, 0, 0, 'It looks like you\'ve got the prototype. Did Ghabar send you?', 0);
INSERT INTO `quest_request_items` VALUES (10431, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (10432, 1, 0, 0, 0, 'Well?  What did Theledorn have to say?', 0);
INSERT INTO `quest_request_items` VALUES (10433, 0, 0, 0, 0, 'Have any pelts to sell me?', 0);
INSERT INTO `quest_request_items` VALUES (10435, 0, 0, 0, 0, 'Did you collect everything?', 0);
INSERT INTO `quest_request_items` VALUES (10436, 0, 0, 0, 0, 'Have you cleared the test area?', 0);
INSERT INTO `quest_request_items` VALUES (10437, 0, 0, 0, 0, 'Have you collected enough fragments?', 0);
INSERT INTO `quest_request_items` VALUES (10438, 1, 0, 0, 0, 'Did you hit the mark?', 0);
INSERT INTO `quest_request_items` VALUES (10439, 1, 0, 0, 0, 'What of the void lord?', 0);
INSERT INTO `quest_request_items` VALUES (10442, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (10444, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (10446, 1, 0, 0, 0, 'Lieutenant Meridian has been keeping us apprised of the situation at Firewing Point. Is everything okay?', 0);
INSERT INTO `quest_request_items` VALUES (10447, 1, 0, 0, 0, 'Sergeant Chawni has been keeping us apprised of the situation at Firewing Point. Is everything okay?', 0);
INSERT INTO `quest_request_items` VALUES (10448, 1, 0, 0, 0, NULL, 0);
INSERT INTO `quest_request_items` VALUES (10449, 6, 0, 0, 0, 'It is my honor to serve the wishes of the Horde.  How might I aid you?', 0);
INSERT INTO `quest_request_items` VALUES (10450, 0, 0, 0, 0, 'Did you face the bonechewers, $N.  I hope their blood was spilled in great gouts!', 0);
INSERT INTO `quest_request_items` VALUES (10451, 1, 0, 0, 0, 'You found sister Wilda?', 0);
INSERT INTO `quest_request_items` VALUES (10455, 1, 0, 0, 0, 'Did I not speak the truth? The grovestalker lynxes have become a menace not only to those who would travel the path here, but also to Sylvanaar itself.$B$BTheir deaths are regrettable, but the whole point of our creation of the Living Grove is so that Sylvanaar can be surrounded by a defensive region of natural beauty and tranquility.', 0);
INSERT INTO `quest_request_items` VALUES (10456, 1, 0, 0, 0, 'I cannot stress strongly enough the need for those Thunderlord dire wolves to be dealt with. Their very existence endangers the stability of the Living Grove and all of our work here.', 0);
INSERT INTO `quest_request_items` VALUES (10457, 1, 0, 0, 0, 'How fares the bolstering of the Living Grove\'s defenses?$B$BWith the intentions of the arakkoa at Veil Lessh unknown, and the Horde just on the other side of the ravine, I fear that if we do not move quickly we will find ourselves in a poor situation to protect our new home and our work in the grove.', 0);
INSERT INTO `quest_request_items` VALUES (10458, 1, 0, 0, 0, 'I fear that this land may be without hope.', 0);
INSERT INTO `quest_request_items` VALUES (10474, 0, 0, 0, 0, 'May time always be on your side, $N.', 0);
INSERT INTO `quest_request_items` VALUES (10476, 0, 0, 0, 0, 'Well, $N?  Are you all talk or do you have something to show me?', 0);
INSERT INTO `quest_request_items` VALUES (10477, 0, 0, 0, 0, 'Do you bear any additional obsidian warbeads, $n?', 0);
INSERT INTO `quest_request_items` VALUES (10478, 0, 0, 0, 0, 'Have you obtained more warbeads, $N?', 0);
INSERT INTO `quest_request_items` VALUES (10479, 0, 0, 0, 0, 'You have something to show me, $N?', 0);
INSERT INTO `quest_request_items` VALUES (10480, 0, 0, 0, 0, 'Wherever you find naga you will almost certainly find tortured water spirits.', 0);
INSERT INTO `quest_request_items` VALUES (10481, 0, 0, 0, 0, 'Soon, we will speak with the spirits.', 0);
INSERT INTO `quest_request_items` VALUES (10482, 1, 0, 0, 0, 'The Bonechewers appear in large numbers, $N. Slay them or the fel orcs may overrun us!', 0);
INSERT INTO `quest_request_items` VALUES (10483, 0, 0, 0, 0, 'Reporting from Honor Hold, $n?  That\'s good -- our campaign against the demons and the fel orcs is running thin on able bodies!', 0);
INSERT INTO `quest_request_items` VALUES (10484, 1, 0, 0, 0, 'Do you have the talismans, $N?  It pains me to know that those cursed orcs possess our soldiers insignias...', 0);
INSERT INTO `quest_request_items` VALUES (10485, 1, 0, 0, 0, 'Do you have Morkh\'s shattered armor?  Have you braved Zeth\'Gor again?', 0);
INSERT INTO `quest_request_items` VALUES (10486, 1, 0, 0, 0, 'Our survival here depends upon our ability to concentrate our efforts against those that truly matter, not the mere bladewing bloodletters.$B$BYou are here again, so this must mean that you have dealt with the matter at hand?', 0);
INSERT INTO `quest_request_items` VALUES (10487, 1, 0, 0, 0, 'It would seem that the Alliance is determined to follow us wherever we go. Let us teach them that we are not intimidated by their presence.$B$BDo you have that which I have requested?', 0);
INSERT INTO `quest_request_items` VALUES (10488, 1, 0, 0, 0, 'How fares the bolstering of our Thunderlord dire wolve\'s defenses?', 0);
INSERT INTO `quest_request_items` VALUES (10489, 1, 0, 0, 0, 'What\'s that you have there? You\'re a strong one!', 0);
